INSERT INTO `tbl_tintuc` (`id_tintuc`, `manguoidung`, `tieude`, `maloaitintuc`, `mota`, `noidung`, `anh`, `ghichu`, `ngayviet`) VALUES
(24, 6, 'PLASTIC BANK', 1, 'The plastic bank is founded with the mission to stop Ocean Plastic by gathering a billion people together to earn money from the waste as well as improve their lives.', '&lt;h2&gt;&amp;nbsp;&lt;/h2&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;color:rgb(33, 37, 41); font-family:open sans,sans-serif; font-size:16px&quot;&gt;The people collect the plastic which will be paid by crypto as a pride and will be confirmed through the Plastic Bank application. After collecting, plastic will be categorized, recycled and sold for companies who use recycled products in production instead of new plastic. This will build a sustainable ecosystem when this project links to the philanthropy village. It means that when the parents collected the plastic and received the crypto. This crypto will be accumulated day by day and their students can have enough condition to go to school. Moreover, the disadvantage people can buy food to improve their lives. Our projects aim to build a nationwide sustainable ecosystem in Vietnam in order to share our vision. Not only do Plastic banks protect the environment and increase the recycling rate in Vietnam but they also help to decrease the poverty rate.&lt;/span&gt;&lt;/p&gt;\r\n', '4.jpg', NULL, '2020-07-13 11:59:18'),
(23, 6, 'M2030', 1, 'Nhằm nâng cao nhận thức và giảm tỷ lệ tử vong của người dân Việt Nam nói riêng và Châu Á nói chung về căn bệnh sốt rét, dự án M2030 được thực hiện với mục tiêu rằng năm 2030 sẽ là năm không còn bệnh s', '&lt;p&gt;Bệnh sốt r&amp;eacute;t đ&amp;atilde; cướp đi 500,000 sinh mạng mỗi năm tr&amp;ecirc;n to&amp;agrave;n thế giới v&amp;agrave; cứ 2 ph&amp;uacute;t sẽ c&amp;oacute; 1 đứa trẻ bị chết bởi căn bệnh. Theo thống k&amp;ecirc;, đ&amp;atilde; c&amp;oacute; 200 triệu người chết chỉ trong thế kỷ 20. K&amp;yacute; sinh tr&amp;ugrave;ng sốt r&amp;eacute;t kh&amp;ocirc;ng ngừng ph&amp;aacute;t triển dẫn đến hiện tượng kh&amp;aacute;ng thuốc v&amp;agrave; đang l&amp;agrave; mối nguy ngại của sức khỏe cộng đồng to&amp;agrave;n cầu nếu kh&amp;ocirc;ng c&amp;oacute; biện ph&amp;aacute;p khẩn cấp để ngăn chặn sự l&amp;acirc;y lan trong khu vực Đ&amp;ocirc;ng Nam &amp;Aacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Dự &amp;aacute;n M2030 được th&amp;agrave;nh lập với mục đ&amp;iacute;ch n&amp;acirc;ng cao nhận thức của người d&amp;acirc;n về m&amp;ocirc;i nguy hại mang t&amp;ecirc;n sốt r&amp;eacute;t để mọi người c&amp;ugrave;ng ngăn chặn v&amp;agrave; bảo vệ sức khỏe của m&amp;igrave;nh. Ngoải ra, dự &amp;aacute;n c&amp;ograve;n tiến h&amp;agrave;ng g&amp;acirc;y quỹ để chung tay b&amp;agrave;i trừ căn bệnh sốt r&amp;eacute;t ở Ch&amp;acirc;u &amp;Aacute;. Đồng thời sẽ ph&amp;aacute;t triển những sản phẩm hoặc dịch vụ mang thương hiệu M2030.&lt;/p&gt;\r\n\r\n&lt;p&gt;Khi kh&amp;aacute;ch h&amp;agrave;ng mua sản phẩm hoặc trải nghiệm dịch vụ của M2030, 100% khoản đ&amp;oacute;ng g&amp;oacute;p sẽ được trực tiếp chuyển thẳng đến cho quỹ sốt r&amp;eacute;t tại nơi được g&amp;acirc;y quỹ để gi&amp;uacute;p đỡ những người d&amp;acirc;n ngăn chặn, bảo vệ v&amp;agrave; chữa trị bệnh sốt r&amp;eacute;t.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ch&amp;uacute;ng t&amp;ocirc;i tin rằng d&amp;ugrave; chỉ một trường hợp mắc bệnh sốt r&amp;eacute;t hay tử vong đều l&amp;agrave; qu&amp;aacute; nhiều. Y học ng&amp;agrave;y c&amp;agrave;ng ph&amp;aacute;t triển v&amp;agrave; ch&amp;uacute;ng ta sẽ l&amp;agrave;m được. H&amp;atilde;y tưởng tượng rằng tất cả mạng sống đều được cứu v&amp;agrave; tương lai của họ sẽ kh&amp;ocirc;ng phải lo sợ sẽ mắc phải căn bệnh sốt r&amp;eacute;t n&amp;agrave;y nữa. C&amp;ugrave;ng nhau ch&amp;uacute;ng ta sẽ tạo n&amp;ecirc;n lịch sử v&amp;agrave; đ&amp;aacute;nh bại bệnh sốt r&amp;eacute;t.&lt;/p&gt;\r\n', '3.jpg', NULL, '2020-07-13 10:44:12'),
(27, 6, 'Ai ngờ 5 loại rau thơm rẻ bèo lại là ', 2, 'Rau thơm giúp cho món ăn trở nên hoàn hảo hơn, nó còn giúp cho bạn tăng cường sức đề kháng.', 'Tại Việt Nam c&oacute; rất nhiều loại rau thơm hay c&ograve;n gọi l&agrave; rau gia vị, để chỉ c&aacute;c loại rau ăn sống được v&agrave; cũng g&oacute;p phần l&agrave;m cho m&oacute;n ăn ngon hơn nhờ v&agrave;o hương thơm đặc trưng. Kh&ocirc;ng phải ai cũng ph&acirc;n biệt được c&aacute;c loại rau thơm, rau gia vị phổ biến ở Việt Nam hay biết ch&uacute;ng thường được th&ecirc;m v&agrave;o m&oacute;n ăn n&agrave;o.\r\n\r\nH&atilde;y c&ugrave;ng xem c&aacute;ch ph&acirc;n biệt c&aacute;c loại rau thơm dưới để bớt hoang mang khi đi chợ nh&eacute;.\r\n\r\nRau m&ugrave;i\r\n\r\nRau m&ugrave;i\r\n\r\n\r\n \r\nRau m&ugrave;i c&oacute; m&ugrave;i thơm, vị cay v&agrave; c&oacute; phần cuống d&agrave;i.\r\n\r\nRau m&ugrave;i được sử dụng rộng r&atilde;i trong c&aacute;c m&oacute;n ăn từ nước chấm, ăn sống đến cắt nhỏ cho v&agrave;o c&aacute;c m&oacute;n ăn nấu hoặc x&agrave;o&hellip;\r\n\r\nRau bạc h&agrave;\r\n\r\nRau bạc h&agrave;\r\n\r\nRau bạc h&agrave; c&oacute; l&aacute; mọc đối xứng, viền l&aacute; c&oacute; răng cưa, tr&ecirc;n l&aacute; c&oacute; l&ocirc;ng tơ mịn. Rau bạc h&agrave; c&oacute; vị v&agrave; m&ugrave;i hương the m&aacute;t.\r\n\r\n\r\n \r\nRau bạc h&agrave; thường được ăn sống, trang tr&iacute; b&aacute;nh hoặc nước uống&hellip;\r\n\r\nRau h&uacute;ng lủi/h&uacute;ng nhủi/h&uacute;ng đỏ\r\n\r\nRau h&uacute;ng lủi', '4.jpg', NULL, '2021-04-07 21:23:46'),
(28, 6, 'Video – Hoàn cảnh khó khăn của bà Nguyễn Thị Sinh', 1, 'B&agrave; ăn đầu c&aacute;, 6 ch&aacute;u nhỏ mỗi đứa ăn một khứa mỏng. Đ&oacute; l&agrave; ho&agrave;n cảnh đ&aacute;ng thương của b&agrave; Nguyễn Thị Sinh, 82 tuổi, một m&igrave;nh cưu mang đ&agrav', 'IMM Group: C&acirc;u chuyện v&agrave; gia cảnh của b&agrave; Sinh được IMM Group t&igrave;m hiểu ghi nhận trong chuyến khảo s&aacute;t từ thiện v&agrave;o th&aacute;ng 07 năm 2018. Mục ti&ecirc;u của chuyến đi nhằm x&aacute;c định c&aacute;c em học sinh ngh&egrave;o hiếu học v&agrave; hỗ trợ c&aacute;c em đến hết bậc đại học.\r\n\r\nPh&oacute;ng sự được VTV5 ghi h&igrave;nh v&agrave; ph&aacute;t s&oacute;ng trong chương tr&igrave;nh: Kết Nối Miền T&acirc;y.\r\n\r\nMọi sự gi&uacute;p đỡ qu&yacute; kh&aacute;n giả c&oacute; thể gởi về B&agrave; NGUYỄN THỊ SINH &ndash; Ấp Ninh Th&agrave;nh, x&atilde; Ninh Quới, Huyện Hồng D&acirc;n, Bạc Li&ecirc;u. ĐT: &lrm;0948249853.\r\n\r\nFacebook chương tr&igrave;nh Kết Nối Miền T&acirc;y (VTV5): https://www.facebook.com/ketnoimientay/\r\n\r\nB&agrave;i viết chuyến khảo s&aacute;t từ thiện Ninh Quới th&aacute;ng 7/2018 của IMM Group: https://immgroup.com/chua-phan-loai/khao-sat-tu-thien-xa-ninh-quoi-huyen-hong-dan-tinh-bac-lieu-thang-07-2018/', 'bg_3.jpg', NULL, '2021-04-08 18:40:54'),
(26, 6, 'Triển khai chương trình “Điều ước cho em” trong toàn ngành giáo dục', 2, '(Thanhuytphcm.vn) - Bộ Giáo dục-Đào tạo (GD-ĐT) cho biết sẽ triển khai chương trình “Điều ước cho em” trong trong ngành giáo dục.', '&lt;p style=&quot;text-align:justify&quot;&gt;&amp;ldquo;Điều ước cho em&amp;rdquo; l&amp;agrave; một chương tr&amp;igrave;nh nh&amp;acirc;n đạo nhằm kết nối, sẻ chia v&amp;agrave; lan tỏa c&amp;aacute;c gi&amp;aacute; trị nh&amp;acirc;n &amp;aacute;i trong cộng đồng v&amp;agrave; x&amp;atilde; hội, th&amp;uacute;c đẩy hỗ trợ ph&amp;aacute;t triển gi&amp;aacute;o dục tại c&amp;aacute;c địa b&amp;agrave;n v&amp;ugrave;ng s&amp;acirc;u, v&amp;ugrave;ng xa, v&amp;ugrave;ng đồng b&amp;agrave;o d&amp;acirc;n tộc thiểu số. Chương tr&amp;igrave;nh k&amp;ecirc;u gọi to&amp;agrave;n x&amp;atilde; hội chung tay hỗ trợ học sinh v&amp;agrave; gi&amp;aacute;o vi&amp;ecirc;n tại c&amp;aacute;c địa b&amp;agrave;n kh&amp;oacute; khăn, địa b&amp;agrave;n v&amp;ugrave;ng s&amp;acirc;u, v&amp;ugrave;ng xa, v&amp;ugrave;ng đồng b&amp;agrave;o d&amp;acirc;n tộc thiểu số để x&amp;acirc;y dựng cơ sở vật chất trường học (điểm trường, nh&amp;agrave; nội tr&amp;uacute;, nh&amp;agrave; vệ sinh, điện, hệ thống nước sạch); dinh dưỡng học đường (bữa ăn trưa cho học sinh); thiết bị, đồ d&amp;ugrave;ng học tập v&amp;agrave; sinh hoạt thiết yếu (s&amp;aacute;ch vở, dụng cụ học tập, quần &amp;aacute;o&amp;hellip;). Bộ GD-ĐT đ&amp;atilde; b&amp;aacute;o c&amp;aacute;o Ch&amp;iacute;nh phủ về thực hiện chương tr&amp;igrave;nh n&amp;agrave;y.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;Tuần qua, đo&amp;agrave;n c&amp;ocirc;ng t&amp;aacute;c Bộ GD-ĐT đ&amp;atilde; đến để thực hiện Chương tr&amp;igrave;nh &amp;ldquo;Điều ước cho em&amp;rdquo;, trao nhiều suất qu&amp;agrave; &amp;yacute; nghĩa đến 10 trường học tr&amp;ecirc;n địa b&amp;agrave;n huyện Tuy&amp;ecirc;n Ho&amp;aacute;. Đo&amp;agrave;n đ&amp;atilde; tặng 40 triệu đồng, tặng 100 suất học bổng, mỗi suất 1 triệu đồng cho c&amp;aacute;c em học sinh ngh&amp;egrave;o vượt kh&amp;oacute;; trao tặng 21 suất qu&amp;agrave; mỗi suất 2 triệu đồng cho c&amp;aacute;c gi&amp;aacute;o vi&amp;ecirc;n ti&amp;ecirc;u biểu vượt kh&amp;oacute; tại c&amp;aacute;c trường học tr&amp;ecirc;n địa b&amp;agrave;n Tuy&amp;ecirc;n H&amp;oacute;a. Tại Quảng Trị, đo&amp;agrave;n c&amp;ocirc;ng t&amp;aacute;c Bộ GD-ĐT đ&amp;atilde; c&amp;oacute; mặt tại huyện Hướng H&amp;oacute;a - kết nối nguồn lực x&amp;acirc;y dựng trường học an to&amp;agrave;n, th&amp;acirc;n thiện; trao 50 suất qu&amp;agrave;, mỗi suất trị gi&amp;aacute; 1 triệu đồng c&amp;ugrave;ng 50 chăn ấm v&amp;agrave; 50 bộ đồng phục hỗ trợ c&amp;aacute;c em học sinh c&amp;oacute; ho&amp;agrave;n cảnh đặc biệt kh&amp;oacute; khăn tr&amp;ecirc;n địa b&amp;agrave;n x&amp;atilde; Hướng Ph&amp;ugrave;ng; trao 12 suất qu&amp;agrave; cho c&amp;aacute;c c&amp;aacute;n bộ, gi&amp;aacute;o vi&amp;ecirc;n&amp;hellip;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;Hiện nay đ&amp;atilde; c&amp;oacute; 1.030 trường học của 33 tỉnh đăng k&amp;yacute; nhu cầu hỗ trợ tr&amp;ecirc;n Cổng nh&amp;acirc;n đạo quốc gia (http://inhandao.vn); gần 400 trường c&amp;oacute; dữ liệu cụ thể. L&amp;atilde;nh đạo Bộ GD-ĐT cũng đ&amp;atilde; tổ chức c&amp;aacute;c đo&amp;agrave;n xuống địa phương tặng qu&amp;agrave; cho gi&amp;aacute;o vi&amp;ecirc;n, học sinh (như tại Bắc Cạn, S&amp;oacute;c Trăng, Nghệ An...) v&amp;agrave; khởi c&amp;ocirc;ng x&amp;acirc;y dựng một số ph&amp;ograve;ng, lớp học. Bộ đ&amp;atilde; triển khai Kế hoạch 29/KH-BGDĐT ng&amp;agrave;y 11/1/2021 về kết nối nguồn lực x&amp;atilde; hội x&amp;acirc;y dựng trường học an to&amp;agrave;n, th&amp;acirc;n thiện cho trẻ em, học sinh giai đoạn 2021-2025; đ&amp;atilde; k&amp;yacute; kết với 37 đơn vị, doanh nghiệp đồng h&amp;agrave;nh hỗ trợ x&amp;acirc;y dựng trường học an to&amp;agrave;n, th&amp;acirc;n thiện giai đoạn 2021-2025.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;Lễ ph&amp;aacute;t động chương tr&amp;igrave;nh trong to&amp;agrave;n ng&amp;agrave;nh sẽ diễn ra v&amp;agrave;o th&amp;aacute;ng 4 tới đ&amp;acirc;y. C&amp;aacute;c nh&amp;agrave; trường trong cả nước c&amp;oacute; thể đăng k&amp;yacute; hỗ trợ qua Cổng nh&amp;acirc;n đạo quốc gia: inhandao.vn v&amp;agrave; l&amp;agrave;m theo hướng dẫn.&lt;/p&gt;\r\n', '3.jpg', NULL, '2020-07-13 14:41:16');

