--
-- Cấu trúc bảng cho bảng `tbl_truongdaihoc`
--

CREATE TABLE `tbl_truongdaihoc` (
  `id_truong` int(11) NOT NULL,
  `tentruong` varchar(100) NOT NULL,
  `mota` varchar(200) NOT NULL,
  `gioithieu` text NOT NULL,
  `chuyennganhdaotao` text NOT NULL,
  `diemchuan` float NOT NULL,
  `diachi` text NOT NULL,
  `anh` text NOT NULL,
  `manguoidung` int(11) NOT NULL,
  `ngayviet` datetime NOT NULL,
  `ngaycapnhat` datetime NOT NULL DEFAULT current_timestamp()
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `tbl_truongdaihoc`
--

INSERT INTO `tbl_truongdaihoc` (`id_truong`, `tentruong`, `mota`, `gioithieu`, `chuyennganhdaotao`, `diemchuan`, `diachi`, `anh`, `manguoidung`, `ngayviet`, `ngaycapnhat`) VALUES
(1, 'Trường Đại Học Kinh Tế Quốc Dân', 'Trường Đại Học Kinh Tế Quốc D&acirc;n', '&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh:&lt;/strong&gt; Viện c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin v&amp;agrave; kinh tế số.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Tổng quan giới thiệu trường + Cơ sở hạ tầng:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;L&amp;agrave; trường đại học về kinh tế đầu ti&amp;ecirc;n tại Việt Nam, nơi đ&amp;agrave;o tạo nguồn nh&amp;acirc;n lực chất lượng cao trong lĩnh vực kinh tế v&amp;agrave; quản trị kinh doanh, Đại học Kinh tế Quốc d&amp;acirc;n đ&amp;atilde; lu&amp;ocirc;n được Nh&amp;agrave; nước v&amp;agrave; to&amp;agrave;n thể x&amp;atilde; hội đ&amp;aacute;nh gi&amp;aacute; cao. Trường Đại học Kinh tế Quốc d&amp;acirc;n l&amp;agrave; một trong những trường đại học tự chủ đầu ti&amp;ecirc;n tại Việt Nam với mục ti&amp;ecirc;u nằm trong TOP 1000 trường đại học h&amp;agrave;ng đầu thế giới cả về cơ sở vật chất v&amp;agrave; chất lượng đ&amp;agrave;o tạo.&lt;/div&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://i.ytimg.com/vi/q6DiV3o00yo/maxresdefault.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://i.ytimg.com/vi/q6DiV3o00yo/maxresdefault.jpg&quot; style=&quot;height:320px; width:569px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://i.ytimg.com/vi/IB6-vp0TV2c/maxresdefault.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://i.ytimg.com/vi/IB6-vp0TV2c/maxresdefault.jpg&quot; style=&quot;height:320px; width:569px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Địa chỉ: &lt;/strong&gt;Ph&amp;ograve;ng&amp;nbsp;13.11 t&amp;ograve;a nh&amp;agrave; A1, Trường Đại học Kinh tế Quốc d&amp;acirc;n, 207 Giải Ph&amp;oacute;ng, Đồng T&amp;acirc;m, Hai B&amp;agrave; Trưng, H&amp;agrave; Nội.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Chất lượng giảng vi&amp;ecirc;n:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;Hội tụ đội ngũ Gi&amp;aacute;o sư, Ph&amp;oacute; Gi&amp;aacute;o sư, Nh&amp;agrave; gi&amp;aacute;o, Nh&amp;agrave; khoa học h&amp;ugrave;ng hậu, t&amp;acirc;m huyết với 10 Nh&amp;agrave; gi&amp;aacute;o nh&amp;acirc;n d&amp;acirc;n, 99 Nh&amp;agrave; gi&amp;aacute;o ưu t&amp;uacute;, 232 Gi&amp;aacute;o sư, 125 Ph&amp;oacute; gi&amp;aacute;o sư, 318 Tiến sĩ. B&amp;ecirc;n cạnh đ&amp;oacute;, nguồn nh&amp;acirc;n lực cũng ng&amp;agrave;y c&amp;agrave;ng được trẻ h&amp;oacute;a.&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Học ph&amp;iacute;: &lt;/strong&gt;16~19 triệu đồng/năm.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m&lt;/strong&gt;: Sinh vi&amp;ecirc;n tốt nghiệp ng&amp;agrave;nh CNTT c&amp;oacute; cơ hội l&amp;agrave;m việc:&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;+ Quản l&amp;yacute; dự &amp;aacute;n CNTT trong c&amp;aacute;c tổ chức, doanh nghiệp.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;+ Quản trị v&amp;agrave; vận h&amp;agrave;nh hệ thống CNTT trong c&amp;aacute;c tổ chức.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;+ Ph&amp;acirc;n t&amp;iacute;ch v&amp;agrave; xử l&amp;yacute; dữ liệu tại c&amp;aacute;c doanh nghiệp v&amp;agrave; tổ chức.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;+ X&amp;acirc;y dựng v&amp;agrave; triển khai dự &amp;aacute;n phần mềm.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;+ Nghi&amp;ecirc;n cứu v&amp;agrave; chuyển giao c&amp;ocirc;ng nghệ &amp;aacute;p dụng cho c&amp;aacute;c tổ chức, doanh nghiệp.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;+ Triển khai c&amp;aacute;c hoạt động trong lĩnh vực CNTT, truyền th&amp;ocirc;ng v&amp;agrave; nội dung số.&lt;/div&gt;\r\n', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><strong>Thời gian đ&agrave;o tạo:</strong> 4 năm</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ C&ocirc;ng nghệ th&ocirc;ng tin.</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Hệ thống th&ocirc;ng tin quản l&yacute;.</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Khoa học m&aacute;y t&iacute;nh.</div>\r\n\r\n<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><strong>Nội dung đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"text-align: justify;\">Sinh vi&ecirc;n được trang bị c&aacute;c kiến thức cơ bản về kinh tế, quản l&yacute; v&agrave; quản trị kinh doanh; chuy&ecirc;n s&acirc;u về C&ocirc;ng nghệ th&ocirc;ng tin. Đ&agrave;o tạo c&aacute;c kiến thức mới trong c&ocirc;ng nghệ th&ocirc;ng tin, ch&uacute; trọng đến ph&aacute;t triển kỹ năng trong quản trị v&agrave; vận h&agrave;nh hệ thống th&ocirc;ng tin quản l&yacute;; quản l&yacute; v&agrave; ph&acirc;n t&iacute;ch dữ liệu; ứng dụng v&agrave; chuyển giao c&ocirc;ng nghệ trong mọi lĩnh vực đồng thời ph&aacute;t triển năng lực tự nghi&ecirc;n cứu tăng cường kiến thức theo xu hướng thời đại.</div>\r\n', 24.1, '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3724.8199851392183!2d105.83999591437778!3d20.999851894141674!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ac71752d8f79%3A0xd2ec575c01017afa!2zVHLGsOG7nW5nIMSQ4bqhaSBI4buNYyBLaW5oIFThur8gUXXhu5FjIETDom4!5e0!3m2!1svi!2s!4v1594633397694!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>', 'taixuong.jpg', 4, '2020-06-26 13:45:21', '2020-07-12 16:59:00'),
(3, 'Đại học Công nghệ - Đại học Quốc gia Hà Nội', 'Đại học C&ocirc;ng nghệ - Đại học Quốc gia H&agrave; Nội', '&lt;ul&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;\r\n	&lt;div&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh: &lt;/strong&gt;C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;\r\n	&lt;div&gt;&lt;strong&gt;Tổng quan giới thiệu trường + Cơ sở hạ tầng:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;Khoa C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin (CNTT) được th&amp;agrave;nh lập v&amp;agrave;o ng&amp;agrave;y 11/02/1995 thu&amp;ocirc;̣c Trường Đại học T&amp;ocirc;̉ng hợp Hà N&amp;ocirc;̣i, hiện nay l&amp;agrave; Khoa CNTT thuộc Trường Đại học C&amp;ocirc;ng nghệ, Đại học Quốc Gia H&amp;agrave; Nội. Được th&amp;agrave;nh lập c&amp;aacute;ch đ&amp;acirc;y hơn 20 năm nhưng Khoa CNTT c&amp;oacute; truyền thống hơn 50 năm ph&amp;aacute;t triển từ năm 1965 với việc đ&amp;agrave;o tạo chuy&amp;ecirc;n ng&amp;agrave;nh M&amp;aacute;y t&amp;iacute;nh tại Khoa To&amp;aacute;n Cơ thuộc Trường Đại học Tổng hợp H&amp;agrave; Nội. Khoa đ&amp;atilde; x&amp;aacute;c định sứ mệnh đ&amp;agrave;o tạo v&amp;agrave; bồi dưỡng nguồn nh&amp;acirc;n lực ng&amp;agrave;nh CNTT v&amp;agrave; M&amp;aacute;y t&amp;iacute;nh chất lượng cao ở tr&amp;igrave;nh độ đại học v&amp;agrave; sau đại học; nghi&amp;ecirc;n cứu ph&amp;aacute;t triển v&amp;agrave; ứng dụng c&amp;aacute;c lĩnh vực khoa học &amp;ndash; c&amp;ocirc;ng nghệ ti&amp;ecirc;n tiến tr&amp;ecirc;n cơ sở ph&amp;aacute;t huy thế mạnh về Khoa học Cơ bản v&amp;agrave; C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng; ti&amp;ecirc;n phong tiếp cận chuẩn mực gi&amp;aacute;o dục đại học khu vực v&amp;agrave; thế giới, đ&amp;oacute;ng g&amp;oacute;p t&amp;iacute;ch cực v&amp;agrave;o sự ph&amp;aacute;t triển nền kinh tế v&amp;agrave; x&amp;atilde; hội tri thức của đất nước.&lt;/div&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://scontent.fhan2-1.fna.fbcdn.net/v/t1.0-9/82771864_3472554892774331_8636595653574656000_n.png?_nc_cat=103&amp;amp;_nc_sid=8024bb&amp;amp;_nc_ohc=NAKIeBlsqjEAX-hpmA6&amp;amp;_nc_ht=scontent.fhan2-1.fna&amp;amp;oh=eec7d903cd20e018e4bd4926be9142ba&amp;amp;oe=5F1A76DB&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://scontent.fhan2-1.fna.fbcdn.net/v/t1.0-9/82771864_3472554892774331_8636595653574656000_n.png?_nc_cat=103&amp;amp;_nc_sid=8024bb&amp;amp;_nc_ohc=NAKIeBlsqjEAX-hpmA6&amp;amp;_nc_ht=scontent.fhan2-1.fna&amp;amp;oh=eec7d903cd20e018e4bd4926be9142ba&amp;amp;oe=5F1A76DB&quot; style=&quot;height:405px; width:620px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Địa điểm:&lt;/strong&gt; Ph&amp;ograve;ng 301 &amp;ndash; Nh&amp;agrave; E3, 144 Xu&amp;acirc;n Thủy, Cầu Giấy, H&amp;agrave; Nội.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo:&lt;/strong&gt; 4~4,5 năm.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Mục ti&amp;ecirc;u chung:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;em&gt;Về kiến thức:&amp;nbsp;&lt;/em&gt;Trang bị c&amp;aacute;c kiến thức cơ bản v&amp;agrave; chuy&amp;ecirc;n s&amp;acirc;u về C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin, cũng như được định hướng một số vấn đề hiện đại tiệm cận với kiến thức chung về C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin của thế giới.&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;em&gt;Về kỹ năng:&amp;nbsp;&lt;/em&gt;Đ&amp;agrave;o tạo kỹ năng thực h&amp;agrave;nh cao trong hầu hết c&amp;aacute;c lĩnh vực của C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin, cho ph&amp;eacute;p sinh vi&amp;ecirc;n tốt nghiệp dễ d&amp;agrave;ng h&amp;ograve;a nhập v&amp;agrave; ph&amp;aacute;t triển trong m&amp;ocirc;i trường l&amp;agrave;m việc mới. Ngo&amp;agrave;i ra, trang bị chosinh vi&amp;ecirc;n kỹ năng tự học tập, nghi&amp;ecirc;n cứu, l&amp;agrave;m việc theo nh&amp;oacute;m v&amp;agrave; kỹ năng giao tiếp.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://www.vnu.edu.vn/upload/2012/09/13584/image/VNU-SANWA-by-buituan.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://www.vnu.edu.vn/upload/2012/09/13584/image/VNU-SANWA-by-buituan.jpg&quot; style=&quot;height:400px; width:600px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Chất lượng giảng vi&amp;ecirc;n:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;Trường Đại học C&amp;ocirc;ng nghệ hiện c&amp;oacute; 275 c&amp;aacute;n bộ trong đ&amp;oacute; c&amp;oacute;&amp;nbsp;205 giảng vi&amp;ecirc;n, nghi&amp;ecirc;n cứu vi&amp;ecirc;n v&amp;agrave; 66 c&amp;aacute;n bộ h&amp;agrave;nh ch&amp;iacute;nh, phục vụ. Nh&amp;agrave; trường lu&amp;ocirc;n tự h&amp;agrave;o c&amp;oacute; đội ngũ c&amp;aacute;n bộ, giảng vi&amp;ecirc;n, nghi&amp;ecirc;n cứu vi&amp;ecirc;n tr&amp;igrave;nh độ cao c&amp;oacute; học vị tiến sĩ trở l&amp;ecirc;n chiếm tỷ lệ 58%, trong đ&amp;oacute; tỉ lệ gi&amp;aacute;o sư v&amp;agrave; ph&amp;oacute; gi&amp;aacute;o sư đạt 17,7%. B&amp;ecirc;n cạnh đ&amp;oacute;, Nh&amp;agrave; trường c&amp;ograve;n c&amp;oacute; đội ngũ gi&amp;aacute;o sư ki&amp;ecirc;m nhiệm đến từ c&amp;aacute;c trường đại học v&amp;agrave; viện ti&amp;ecirc;n tiến tr&amp;ecirc;n thế giới. Dưới đ&amp;acirc;y l&amp;agrave; danh s&amp;aacute;ch c&amp;aacute;n bộ, giảng vi&amp;ecirc;n của trường c&amp;oacute; học vị tiến sĩ trở l&amp;ecirc;n.&amp;nbsp;&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Học ph&amp;iacute;:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;+ Đối với chương tr&amp;igrave;nh đ&amp;agrave;o tạo chuẩn: Khoảng 11 triệu đồng/năm.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;+ Đối với chương tr&amp;igrave;nh chất lượng cao: Khoảng 36 triệu đồng/năm.&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;+ Trường l&amp;agrave; đối t&amp;aacute;c l&amp;acirc;u đời của những tập đo&amp;agrave;n lớn như Samsung, Toshiba,&amp;hellip; do vậy, sinh vi&amp;ecirc;n sẽ c&amp;oacute; cơ hội nhận được nhiều học bổng khuyến kh&amp;iacute;ch học tập hoặc cơ hội thực tập, l&amp;agrave;m việc tại c&amp;aacute;c tập đo&amp;agrave;n lớn.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;+ Ngo&amp;agrave;i ra, khi tốt nghiệp, sinh vi&amp;ecirc;n c&amp;oacute; khả năng khởi nghiệp kh&amp;aacute; cao, đặc biệt trong ng&amp;agrave;nh Khoa học m&amp;aacute;y t&amp;iacute;nh (gần 10%), C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin,... Ng&amp;agrave;nh C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin c&amp;oacute; số lượng sinh vi&amp;ecirc;n khởi nghiệp lớn nhất trong c&amp;aacute;c nh&amp;oacute;m ng&amp;agrave;nh. Với xu hướng khởi nghiệp đang được ch&amp;uacute; trọng như hiện nay, trường Đại học C&amp;ocirc;ng nghệ đang quan t&amp;acirc;m khuyến kh&amp;iacute;ch v&amp;agrave; tạo điều kiện cho c&amp;aacute;c sinh vi&amp;ecirc;n c&amp;oacute; khả năng s&amp;aacute;ng tạo, &amp;yacute; tưởng kinh doanh v&amp;agrave; năng lực thực thi.&lt;/div&gt;\r\n', '<ul>\r\n	<li>\r\n	<div><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"text-align: justify; margin-left: 40px;\">+ C&ocirc;ng nghệ Phần mềm</div>\r\n\r\n<div style=\"text-align: justify; margin-left: 40px;\">+ Khoa học m&aacute;y t&iacute;nh</div>\r\n\r\n<div style=\"text-align: justify; margin-left: 40px;\">+ Khoa học v&agrave; kỹ thuật t&iacute;nh to&aacute;n</div>\r\n\r\n<div style=\"text-align: justify; margin-left: 40px;\">+ Mạng v&agrave; truyền th&ocirc;ng m&aacute;y t&iacute;nh</div>\r\n\r\n<ul>\r\n	<li>\r\n	<div><strong>Nội dung đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"text-align: justify;\">Trang bị c&aacute;c kiến thức cơ bản v&agrave; chuy&ecirc;n s&acirc;u về C&ocirc;ng nghệ th&ocirc;ng tin, cũng như được định hướng một số vấn đề hiện đại tiệm cận với kiến thức chung về C&ocirc;ng nghệ th&ocirc;ng tin của thế giới. Đ&agrave;o tạo kỹ năng thực h&agrave;nh cao trong hầu hết c&aacute;c lĩnh vực của C&ocirc;ng nghệ th&ocirc;ng tin, cho ph&eacute;p sinh vi&ecirc;n tốt nghiệp dễ d&agrave;ng h&ograve;a nhập v&agrave; ph&aacute;t triển trong m&ocirc;i trường l&agrave;m việc mới. Ngo&agrave;i ra, trang bị&nbsp;cho sinh vi&ecirc;n kỹ năng tự học tập, nghi&ecirc;n cứu, l&agrave;m việc theo nh&oacute;m v&agrave; kỹ năng giao tiếp.&nbsp;</div>\r\n', 22, '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3723.8611135522906!2d105.78051891437845!3d21.038242492831603!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ab354920c233%3A0x5d0313a3bfdc4f37!2zVHLGsOG7nW5nIMSQ4bqhaSBo4buNYyBDw7RuZyBuZ2jhu4csIMSQ4bqhaSBo4buNYyBRdeG7kWMgZ2lhIEjDoCBO4buZaQ!5e0!3m2!1svi!2s!4v1594633357339!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>', 'dhcnqg.jpg', 4, '2020-06-26 14:27:43', '2020-07-12 16:55:52'),
(4, 'Đại học Công nghệ Đông Á', 'Đại học C&ocirc;ng nghệ Đ&ocirc;ng &Aacute;', '&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh:&lt;/strong&gt; C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Tổng quan giới thiệu trường + Cơ sở hạ tầng:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;Trường Đại Học C&amp;ocirc;ng Ngh&amp;ecirc;̣ Đ&amp;ocirc;ng Á là trường đại học đa ngành nằm trong h&amp;ecirc;̣ th&amp;ocirc;́ng giáo dục qu&amp;ocirc;́c d&amp;acirc;n. Triết l&amp;yacute; gi&amp;aacute;o dục của nh&amp;agrave; trường&amp;nbsp;&lt;strong&gt;&amp;ldquo;Học phải c&amp;oacute; việc l&amp;agrave;m&amp;rdquo;&lt;/strong&gt;, EAUT khẳng định l&amp;agrave; trường đại học đ&amp;agrave;o tạo sinh vi&amp;ecirc;n ph&amp;aacute;t triển to&amp;agrave;n diện kiến thức chuy&amp;ecirc;n m&amp;ocirc;n v&amp;agrave; kỹ năng nghề nghiệp, năng động &amp;ndash; bản lĩnh &amp;ndash; tự tin hội nhập với cộng đồng quốc tế, đảm nhận tốt c&amp;ocirc;ng việc trong m&amp;ocirc;i trường đa lĩnh vực, đa văn h&amp;oacute;a. Kh&amp;ocirc;ng gian học tập 4.0 gồm c&amp;aacute;c ph&amp;ograve;ng học m&amp;aacute;y chiếu, điều ho&amp;agrave; nhiệt độ hai chiều, hệ thống chiếu s&amp;aacute;ng, thang m&amp;aacute;y&amp;hellip;Ph&amp;ograve;ng thực h&amp;agrave;nh th&amp;iacute; nghiệm do Tập đo&amp;agrave;n ROCK-WELL v&amp;agrave; tập đo&amp;agrave;n SIEMENS t&amp;agrave;i trợ, gồm c&amp;aacute;c thiết bị điều khiển, thực h&amp;agrave;nh tự động ho&amp;aacute; tối t&amp;acirc;n nhất.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align: center;&quot;&gt;&lt;a href=&quot;https://eaut.edu.vn/wp-content/uploads/2017/05/mat-dung-toa-nha-2.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://eaut.edu.vn/wp-content/uploads/2017/05/mat-dung-toa-nha-2.jpg&quot; style=&quot;height:305px; width:457px&quot; /&gt;&lt;/a&gt;&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Địa điểm:&lt;/strong&gt; T&amp;ograve;a nh&amp;agrave; Polyco, Đường Trịnh Văn B&amp;ocirc;, Nam Từ Li&amp;ecirc;m, H&amp;agrave; Nội.&amp;Agrave;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo:&lt;/strong&gt; 4 năm&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Mục ti&amp;ecirc;u đ&amp;agrave;o tạo:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Chương tr&amp;igrave;nh gi&amp;aacute;o dục tr&amp;igrave;nh độ đ&amp;agrave;o tạo đại học ng&amp;agrave;nh C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin tại Trường Đại học C&amp;ocirc;ng nghệ Đ&amp;ocirc;ng &amp;Aacute; tạo ra m&amp;ocirc;i trường học tập l&amp;yacute; tưởng gi&amp;uacute;p người học trau dồi, r&amp;egrave;n luyện v&amp;agrave; tu dưỡng đạo đức nghề nghiệp; cung cấp cho người học những điều kiện cần v&amp;agrave; đủ để ho&amp;agrave;n thiện những kỹ năng cơ bản, thiết yếu phục vụ c&amp;ocirc;ng t&amp;aacute;c trong lĩnh vực chuy&amp;ecirc;n m&amp;ocirc;n nhằm đ&amp;aacute;p ứng y&amp;ecirc;u cầu của cuộc c&amp;aacute;ch mạng c&amp;ocirc;ng nghi&amp;ecirc;p 4.0; đồng thời l&amp;agrave; cơ sở để người học tiếp tục học tập n&amp;acirc;ng cao năng lực v&amp;agrave; tr&amp;igrave;nh độ ở c&amp;aacute;c bậc đ&amp;agrave;o tạo sau đại học.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Được trang bị c&amp;aacute;c kiến thức nền tảng về khoa học tự nhi&amp;ecirc;n, kiến thức cơ bản như mạng m&amp;aacute;y t&amp;iacute;nh, hệ thống th&amp;ocirc;ng tin, lập tr&amp;igrave;nh, cơ sở dữ liệu&amp;hellip;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Chương tr&amp;igrave;nh ng&amp;agrave;nh CNTT đ&amp;agrave;o tạo c&amp;aacute;c kiến thức gi&amp;uacute;p sinh vi&amp;ecirc;n hướng tới đạt chuẩn C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin của Nhật Bản (FE), ti&amp;ecirc;u chuẩn được c&amp;ocirc;ng nhận tại rất nhiều nước tr&amp;ecirc;n to&amp;agrave;n thế giới.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Sinh vi&amp;ecirc;n CNTT hướng đến học tập v&amp;agrave; trau dồi cả Tiếng Anh v&amp;agrave; Tiếng Nhật, l&amp;agrave;m nền tảng ng&amp;ocirc;n ngữ, hướng tới cơ hội l&amp;agrave;m việc tại Nhật Bản&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Sinh vi&amp;ecirc;n được thực h&amp;agrave;nh v&amp;agrave; tiếp x&amp;uacute;c với những c&amp;ocirc;ng nghệ ti&amp;ecirc;n tiến nhất tại c&amp;aacute;c doanh nghiệp trong ng&amp;agrave;nh CNTT, đồng h&amp;agrave;nh nghi&amp;ecirc;n cứu ra những sản phẩm mới c&amp;oacute; t&amp;iacute;nh thượng mại cao.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Điểm kh&amp;aacute;c biệt tại Đại học C&amp;ocirc;ng nghệ Đ&amp;ocirc;ng &amp;Aacute; l&amp;agrave; sinh vi&amp;ecirc;n sẽ được trải qua 3 kỳ thực tập tại c&amp;aacute;c C&amp;ocirc;ng ty c&amp;ocirc;ng nghệ, tham gia trực tiếp v&amp;agrave;o hoạt động chuy&amp;ecirc;n m&amp;ocirc;n tại c&amp;aacute;c c&amp;ocirc;ng ty n&amp;agrave;y.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Sinh vi&amp;ecirc;n được trang bị c&amp;aacute;c kĩ năng phỏng vấn, viết CV c&amp;ugrave;ng c&amp;aacute;c cơ hội nghề nghiệp được giới thiệu th&amp;ocirc;ng qua chương tr&amp;igrave;nh học v&amp;agrave; hoạt động ngoại kh&amp;oacute;a.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://upload.wikimedia.org/wikipedia/commons/b/b6/Tr%C6%B0%E1%BB%9Dng_%C4%91%E1%BA%A1i_h%E1%BB%8Dc_%C4%90%C3%B4ng_%C3%81_%E1%BB%9F_B%E1%BA%AFc_Ninh.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://upload.wikimedia.org/wikipedia/commons/b/b6/Tr%C6%B0%E1%BB%9Dng_%C4%91%E1%BA%A1i_h%E1%BB%8Dc_%C4%90%C3%B4ng_%C3%81_%E1%BB%9F_B%E1%BA%AFc_Ninh.jpg&quot; style=&quot;height:306px; width:554px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Chất lượng giảng vi&amp;ecirc;n:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;Với mục ti&amp;ecirc;u trở th&amp;agrave;nh một trong những đơn vị đ&amp;agrave;o tạo kỹ sư C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin h&amp;agrave;ng đầu tại Việt Nam, khoa C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin &amp;ndash; Đại học C&amp;ocirc;ng nghệ Đ&amp;ocirc;ng &amp;Aacute; l&amp;agrave; nơi quy tụ c&amp;aacute;c Gi&amp;aacute;o sư, Tiến sỹ đầu ng&amp;agrave;nh với nhiều năm kinh nghiệm nghi&amp;ecirc;n cứu v&amp;agrave; giảng dạy c&amp;ugrave;ng đội ngũ giảng vi&amp;ecirc;n trẻ trung, nhiệt t&amp;igrave;nh &amp;aacute;p dụng những phương ph&amp;aacute;p giảng dạy hấp dẫn, lấy sinh vi&amp;ecirc;n l&amp;agrave;m trung t&amp;acirc;m, gi&amp;uacute;p c&amp;aacute;c em ph&amp;aacute;t huy tối đa khả năng của m&amp;igrave;nh.&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Học ph&amp;iacute;: &lt;/strong&gt;14 triệu đồng/năm.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Chuẩn đầu ra:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;1. Kiến thức&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Hiểu c&amp;aacute;c nguy&amp;ecirc;n l&amp;yacute; cơ bản của chủ nghĩa M&amp;aacute;c &amp;ndash; L&amp;ecirc;nin, tư tưởng Hồ Ch&amp;iacute; Minh v&amp;agrave; đường lối c&amp;aacute;ch mạng của Đảng Cộng sản Việt Nam;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Hiểu v&amp;agrave; vận dụng kiến thức To&amp;aacute;n, khoa học tự nhi&amp;ecirc;n, khoa học x&amp;atilde; hội &amp;ndash; nh&amp;acirc;n văn, c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin v&amp;agrave; kiến thức cơ sở v&amp;agrave;o ng&amp;agrave;nh đ&amp;agrave;o tạo;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Tr&amp;igrave;nh độ ngoại ngữ: TOEIC 450&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Hiểu biết c&amp;aacute;c vấn đề thuộc lĩnh vực chuy&amp;ecirc;n m&amp;ocirc;n: Được trang bị kiến thức li&amp;ecirc;n quan đến nghi&amp;ecirc;n cứu ph&amp;aacute;t triển, gia c&amp;ocirc;ng hay ứng dụng c&amp;aacute;c hệ thống phần mềm; hiểu c&amp;aacute;c nguy&amp;ecirc;n tắc quản l&amp;yacute; dự &amp;aacute;n CNTT, đảm bảo chất lượng phần mềm; Được trang bị kiến thức về đa phương tiện v&amp;agrave; c&amp;aacute;c c&amp;ocirc;ng cụ xử l&amp;yacute; đa phương tiện; C&amp;oacute; kiến thức về thiết kế, x&amp;acirc;y dựng, c&amp;agrave;i đặt, vận h&amp;agrave;nh v&amp;agrave; bảo tr&amp;igrave; c&amp;aacute;c th&amp;agrave;nh phần phần cứng, phần mềm của hệ thống m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; c&amp;aacute;c hệ thống thiết bị dựa tr&amp;ecirc;n m&amp;aacute;y t&amp;iacute;nh; C&amp;oacute; kiến thức về mạng m&amp;aacute;y t&amp;iacute;nh, c&amp;aacute;c phương ph&amp;aacute;p v&amp;agrave; quy tr&amp;igrave;nh đảm bảo an to&amp;agrave;n cho hệ thống mạng m&amp;aacute;y t&amp;iacute;nh.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;2. Kỹ năng&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;oacute; c&amp;aacute;c kỹ năng nghề nghiệp sau: Khảo s&amp;aacute;t, thiết kế, x&amp;acirc;y dựng, vận h&amp;agrave;nh, bảo tr&amp;igrave; v&amp;agrave; ph&amp;aacute;t triển hệ thống m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; mạng m&amp;aacute;y t&amp;iacute;nh cho c&amp;aacute;c cơ quan, trường học, doanh nghiệp&amp;hellip;, với quy m&amp;ocirc; vừa v&amp;agrave; nhỏ; Quản trị dữ liệu, quản trị mạng, quản trị website cho c&amp;aacute;c tổ chức, doanh nghiệp, c&amp;ocirc;ng ty; Nghi&amp;ecirc;n cứu, m&amp;ocirc; h&amp;igrave;nh h&amp;oacute;a c&amp;aacute;c b&amp;agrave;i to&amp;aacute;n thực tế, x&amp;acirc;y dựng thuật to&amp;aacute;n, cơ sở dữ liệu, thiết kế v&amp;agrave; c&amp;agrave;i đặt c&amp;aacute;c phần mềm ứng dụng; Tham gian quản l&amp;yacute; dự &amp;aacute;n; đảm bảo chất lượng phần mềm. X&amp;acirc;y dựng t&amp;agrave;i liệu kỹ thuật, t&amp;agrave;i liệu hướng dẫn sử dụng hệ thống một c&amp;aacute;ch hiệu quả; Đề xuất, phản biện v&amp;agrave; tư vấn c&amp;aacute;c vấn đề thuộc lĩnh vực CNTT; Tham gia nghi&amp;ecirc;n cứu v&amp;agrave; giảng dạy c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin ở c&amp;aacute;c bậc ph&amp;ugrave; hợp; Tham gia triển khai, vận h&amp;agrave;nh, khai th&amp;aacute;c c&amp;aacute;c ứng dụng về dịch vụ h&amp;agrave;nh ch&amp;iacute;nh c&amp;ocirc;ng (ch&amp;iacute;nh phủ điện tử), thương mại điện tử.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Được trang bị c&amp;aacute;c kỹ năng mềm sau: Khả năng tư duy, l&amp;agrave;m việc độc lập, giải quyết vấn đề li&amp;ecirc;n quan đến chuy&amp;ecirc;n m&amp;ocirc;n; Khả năng tự học, nghi&amp;ecirc;n cứu v&amp;agrave; tiếp thu c&amp;aacute;c c&amp;ocirc;ng nghệ mới; Khả năng l&amp;agrave;m việc theo nh&amp;oacute;m, giao tiếp v&amp;agrave; truyền đạt th&amp;ocirc;ng tin trong lĩnh vực chuy&amp;ecirc;n m&amp;ocirc;n; Thu thập, xử l&amp;yacute; th&amp;ocirc;ng tin để giải quyết vấn đề trong lĩnh vực chuy&amp;ecirc;n m&amp;ocirc;n; Sử dụng c&amp;ocirc;ng ngh&amp;ecirc;̣ th&amp;ocirc;ng tin v&amp;agrave; ngoại ngữ phục vụ c&amp;ocirc;ng việc chuy&amp;ecirc;n m&amp;ocirc;n v&amp;agrave; quản l&amp;yacute;; Quản l&amp;yacute;, đi&amp;ecirc;̀u hành tri&amp;ecirc;̃n khai các dự án, c&amp;ocirc;ng trình li&amp;ecirc;n quan đ&amp;ecirc;́n chuy&amp;ecirc;n m&amp;ocirc;n.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&amp;nbsp;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m:&lt;/strong&gt; Học ng&amp;agrave;nh CNTT tại Đại học C&amp;ocirc;ng nghệ Đ&amp;ocirc;ng &amp;Aacute;, sinh vi&amp;ecirc;n sẽ c&amp;oacute; cơ hội l&amp;agrave;m việc ở c&amp;aacute;c vị tr&amp;iacute;:&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 40px;&quot;&gt;+ Kỹ sư ph&amp;aacute;t triển phần mềm (Software Developer).&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 40px;&quot;&gt;+ Quản trị dữ liệu (Database Adminstrator).&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 40px;&quot;&gt;+ Chuy&amp;ecirc;n vi&amp;ecirc;n lập tr&amp;igrave;nh website (Web Developer).&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 40px;&quot;&gt;+ Ph&amp;acirc;n t&amp;iacute;ch hệ thống m&amp;aacute;y t&amp;iacute;nh (Computer System Analyst).&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 40px;&quot;&gt;+ Quản trị hệ thống th&amp;ocirc;ng tin (Information System Mangement).&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 40px;&quot;&gt;+ Kỹ sư m&amp;aacute;y t&amp;iacute;nh (Computer Engineering).&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 40px;&quot;&gt;+ Tư vấn vi&amp;ecirc;n CNTT (IT Consultant).&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 40px;&quot;&gt;+ Kỹ sư CNTT l&amp;agrave;m việc tại Nhật Bản.&lt;/div&gt;\r\n', '<ul>\r\n	<li style=\"text-align: justify;\">\r\n	<div><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Mạng m&aacute;y t&iacute;nh &amp; truyền th&ocirc;ng</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ C&ocirc;ng nghệ phần mềm</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Khoa học m&aacute;y t&iacute;nh</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Hệ thống th&ocirc;ng tin quản l&yacute;</div>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\">\r\n	<div><strong>Nội dung đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Chương tr&igrave;nh đ&agrave;o tạo ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin tại EAUT ch&uacute; trọng c&ocirc;ng t&aacute;c thực h&agrave;nh ( 30% lý thuy&ecirc;́t, 70% thực hành) đ&ocirc;̀ng thời li&ecirc;n kết với doanh nghiệp để n&acirc;ng cao chất lượng đ&agrave;o tạo. trường c&oacute; phương &aacute;n đ&agrave;o tạo tr&ecirc;n giảng đường kết hợp với thực h&agrave;nh trong c&aacute;c ph&ograve;ng máy, doanh nghiệp, tr&ecirc;n m&ocirc;i trường mạng&hellip; một c&aacute;ch hợp l&yacute;, để trong một thời gian ngắn c&oacute; thể gi&uacute;p sinh vi&ecirc;n trang bị được c&aacute;c kỹ năng l&agrave;m việc cần thiết, có khả năng ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin v&agrave;o trong các những lĩnh vực cụ thể của c&aacute;ch mạng c&ocirc;ng nghiệp 4.0 hiện nay ví dụ như: điện to&aacute;n đ&aacute;m m&acirc;y, mạng x&atilde; hội, học m&aacute;y, ứng dụng web v&agrave; di động, ph&acirc;n t&iacute;ch dữ liệu lớn tr&ecirc;n nền tảng mạng kết nối vạn vật&hellip;</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Hai năm đầu, sinh vi&ecirc;n được đ&agrave;o tạo khối kiến thức đại cương v&agrave; cơ sở ng&agrave;nh gắn với định hướng ứng dụng C&ocirc;ng nghệ th&ocirc;ng tin, sớm tiếp cận c&aacute;c hệ th&ocirc;ng CPS, sớm l&agrave;m vi&ecirc;̣c với c&aacute;c m&ocirc; h&igrave;nh c&ocirc;ng nghệ 4.0, ph&aacute;t triển kỹ năng lập tr&igrave;nh phần mềm, xử l&yacute; dữ liệu số&hellip;</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Năm 3 sinh vi&ecirc;n được đ&agrave;o tạo chuy&ecirc;n s&acirc;u theo định hướng 4 chuy&ecirc;n ng&agrave;nh: (1) Mạng m&aacute;y t&iacute;nh &amp; truyền th&ocirc;ng, (2) C&ocirc;ng nghệ phần mềm (3) Khoa học m&aacute;y t&iacute;nh (4) Hệ thống th&ocirc;ng tin quản l&yacute;.</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Năm 4 sinh vi&ecirc;n sẽ đi thực t&acirc;̣p trực ti&ecirc;́p tại các doanh nghi&ecirc;̣p, áp dụng những ki&ecirc;́n thức lý thuy&ecirc;́t cùng với thực hành được đào tại trong trường vào c&ocirc;ng vi&ecirc;̣c thực ti&ecirc;̃n. Nhi&ecirc;̀u sinh vi&ecirc;n năng đ&ocirc;̣ng còn tham gia thực t&acirc;̣p trong doanh nghi&ecirc;̣p từ những năm đ&acirc;̀u đại học. Nhà trường đã đ&acirc;̀u tư h&ecirc;̣ th&ocirc;́ng máy tính đ&ocirc;̀ng b&ocirc;̣ với màn hình ASUS ips27, c&acirc;́u hình chip core i5 đời mới nh&acirc;́t ram 8G, Card màn hình rời 2G chuy&ecirc;n dụng cho học đ&ocirc;̀ họa 3D&hellip;giúp sinh vi&ecirc;n học t&acirc;̣p trong đi&ecirc;̀u ki&ecirc;̣n t&ocirc;́t nh&acirc;́t.</div>\r\n', 22.1, '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3723.8167319197814!2d105.73975371437847!3d21.04001779277099!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135096b31fa7abb%3A0xff645782804911af!2zVHLGsOG7nW5nIMSR4bqhaSBo4buNYyBDw7RuZyBuZ2jhu4cgxJDDtG5nIMOB!5e0!3m2!1svi!2s!4v1594633328896!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>', 'donga.JPG', 4, '2020-06-26 14:34:03', '2020-07-12 16:52:54'),
(5, 'Học viện Kỹ thuật Mật mã', 'Học viện Kỹ thuật Mật m&atilde;', '&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;Địa điểm:&lt;/strong&gt; 141 đường Chiến Thắng, T&amp;acirc;n Triều, Thanh Tr&amp;igrave;, H&amp;agrave; Nội.&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh: &lt;/strong&gt;Ng&amp;agrave;nh C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin Học viện kỹ thuật qu&amp;acirc;n sự&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://caodangytetphcm.edu.vn/wp-content/uploads/2019/01/hocvienktmatma2.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://caodangytetphcm.edu.vn/wp-content/uploads/2019/01/hocvienktmatma2.jpg&quot; style=&quot;height:281px; width:500px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;Giới thiệu về trường:&amp;nbsp;&lt;/strong&gt;Học viện Kỹ thuật Qu&amp;acirc;n sự&amp;nbsp;l&amp;agrave; m&amp;ocirc;i trường nghi&amp;ecirc;n cứu v&amp;agrave; đ&amp;agrave;o tạo đội ngũ kỹ sư khoa học, kỹ thuật, c&amp;ocirc;ng nghệ h&amp;agrave;ng đầu của qu&amp;acirc;n đội v&amp;agrave; đất nước. Với mục ti&amp;ecirc;u x&amp;acirc;y dựng Khoa C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin theo m&amp;ocirc; h&amp;igrave;nh định hướng nghi&amp;ecirc;n cứu, tăng cường t&amp;iacute;ch hợp giữa đ&amp;agrave;o tạo v&amp;agrave; nghi&amp;ecirc;n cứu khoa học, hiện tại. Đến nay, c&amp;aacute;c chương tr&amp;igrave;nh đ&amp;agrave;o tạo ng&amp;agrave;nh CNTT của Khoa thường xuy&amp;ecirc;n được cập nhật để ph&amp;ugrave; hợp với sự ph&amp;aacute;t triển c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin tr&amp;ecirc;n thế giới v&amp;agrave; nhu cầu x&amp;atilde; hội. Hiện nay, Khoa CNTT đang triển khai x&amp;acirc;y dựng chương tr&amp;igrave;nh đ&amp;agrave;o tạo theo c&amp;aacute;c chương tr&amp;igrave;nh đ&amp;agrave;o tạo ti&amp;ecirc;n tiến v&amp;agrave; quản l&amp;yacute;, tổ chức đ&amp;agrave;o tạo theo c&amp;aacute;c ti&amp;ecirc;u chuẩn kiểm định quốc tế (AUN).&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;Học ph&amp;iacute;:&amp;nbsp;&lt;/strong&gt;Hệ qu&amp;acirc;n sự được miễn giảm to&amp;agrave;n bộ. Học ph&amp;iacute; hệ d&amp;acirc;n sự được t&amp;iacute;nh cụ thể như sau:&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;- Đối với mức thu học ph&amp;iacute; hệ đại học ch&amp;iacute;nh quy:&amp;nbsp;225.000 đồng/t&amp;iacute;n chỉ&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;- Đối với đồ &amp;aacute;n tốt nghiệp:&amp;nbsp;3.000.000 đồng/đồ &amp;aacute;n&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;- Đối với hệ Cao đẳng của trường:&amp;nbsp;630.000 đồng/th&amp;aacute;ng/sinh vi&amp;ecirc;n. Tương đương&amp;nbsp;6.300.000 đồng/năm.&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo: &lt;/strong&gt;5 năm&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;Đối tượng tuyển sinh:&amp;nbsp;&lt;/strong&gt;\r\n	&lt;div&gt;C&amp;ocirc;ng d&amp;acirc;n Việt Nam tr&amp;uacute;ng tuyển kỳ thi (hoặc x&amp;eacute;t tuyển) đại học khối ng&amp;agrave;nh ph&amp;ugrave; hợp theo qui chế tuyển sinh của Bộ Gi&amp;aacute;o dục v&amp;agrave; Đ&amp;agrave;o tạo v&amp;agrave; c&amp;aacute;c đối tượng kh&amp;aacute;c theo quy định của Ban Cơ yếu Ch&amp;iacute;nh phủ.&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Mục ti&amp;ecirc;u đ&amp;agrave;o tạo:&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Mục ti&amp;ecirc;u của Chương tr&amp;igrave;nh gi&amp;aacute;o dục đại học ch&amp;iacute;nh quy ng&amp;agrave;nh c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin, chuy&amp;ecirc;n ng&amp;agrave;nh Kỹ thuật phần mềm nh&amp;uacute;ng v&amp;agrave; di động (Mobile and Embedded Software Engineering - MESE) l&amp;agrave; trang bị cho người học: Phẩm chất ch&amp;iacute;nh trị, đạo đức nghề nghiệp để đ&amp;aacute;p ứng y&amp;ecirc;u cầu hoạt động trong khu vực an ninh, quốc ph&amp;ograve;ng, v&amp;agrave; kinh tế x&amp;atilde; hội. Kiến thức đại cương về khoa học tự nhi&amp;ecirc;n, x&amp;atilde; hội v&amp;agrave; kiến thức cơ sở, nền tảng về điện tử viễn th&amp;ocirc;ng v&amp;agrave; c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Khối kiến thức nền tảng về c&amp;ocirc;ng nghệ phần mềm, đ&amp;aacute;p ứng được c&amp;aacute;c c&amp;ocirc;ng việc của kỹ sư ph&amp;aacute;t triển phần mềm. Kiến thức l&amp;yacute; thuyết v&amp;agrave; kỹ năng thực h&amp;agrave;nh về chuy&amp;ecirc;n ng&amp;agrave;nh Kỹ thuật phần mềm nh&amp;uacute;ng v&amp;agrave; di động theo hai hướng chuy&amp;ecirc;n s&amp;acirc;u: kỹ sư ph&amp;aacute;t triển phần mềm cho c&amp;aacute;c thiết bị di động v&amp;agrave; kỹ sư ph&amp;aacute;t triển phần mềm trong c&amp;aacute;c hệ thống nh&amp;uacute;ng. Kỹ năng x&amp;atilde; hội cần thiết, khả năng tự học, tự nghi&amp;ecirc;n cứu để th&amp;agrave;nh c&amp;ocirc;ng trong cuộc sống v&amp;agrave; nghề nghiệp. Chương tr&amp;igrave;nh đ&amp;agrave;o tạo cho ph&amp;eacute;p khả năng chuyển đổi linh động giữa hai chuy&amp;ecirc;n ng&amp;agrave;nh &amp;ldquo;An to&amp;agrave;n th&amp;ocirc;ng tin&amp;rdquo; v&amp;agrave; &amp;ldquo;Kỹ thuật phần mềm nh&amp;uacute;ng v&amp;agrave; di động&amp;rdquo;.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://media.ex-cdn.com/EXP/media.tapchigiaothong.vn/files/thu.ha/2016/12/30/tuyen_sinh_hoc_vien_ky_thuat_mat_ma-0858.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://media.ex-cdn.com/EXP/media.tapchigiaothong.vn/files/thu.ha/2016/12/30/tuyen_sinh_hoc_vien_ky_thuat_mat_ma-0858.jpg&quot; style=&quot;height:415px; width:550px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Chuẩn đầu ra:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n\r\n	&lt;div&gt;Sinh vi&amp;ecirc;n ra trường c&amp;oacute; thể l&amp;agrave;m việc trong c&amp;aacute;c cơ quan nh&amp;agrave; nước, c&amp;aacute;c doanh nghiệp, c&amp;ocirc;ng ty phần mềm trong nước, quốc tế; c&amp;oacute; thể giao tiếp, sử dụng tiếng Anh trong c&amp;ocirc;ng việc; c&amp;oacute; kiến thức nền tảng vững chắc để tiếp tục học tập bậc cao hơn. Cụ thể, sinh vi&amp;ecirc;n phải đạt c&amp;aacute;c ti&amp;ecirc;u chuẩn ch&amp;iacute;nh sau:&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Đạt chuẩn kiến thức tương đương với chứng chỉ s&amp;aacute;t hạch kỹ sư c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin FE (Fundamentals of Engineering Certification) của Nhật Bản v&amp;agrave; 11 nước ch&amp;acirc;u &amp;Aacute;.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Đạt chuẩn kiến thức tương đương với chứng chỉ quốc tế về ph&amp;aacute;t triển phần mềm di động ACE (Android Engineer Certification Exam System) của OESF.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Đạt chuẩn kiến thức tương đương với chứng chỉ quốc tế về ph&amp;aacute;t triển ứng dụng chip l&amp;otilde;i ARM (chứng chỉ AAE &amp;ndash; Arm Accreditted Engineer) do tập đo&amp;agrave;n ARM cấp.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Đạt chuẩn tiếng Anh tương đương Toeic 400 trở l&amp;ecirc;n.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&amp;nbsp;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Cơ hội nghề nghiệp:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n\r\n	&lt;div&gt;Sinh vi&amp;ecirc;n tốt nghiệp chuy&amp;ecirc;n ng&amp;agrave;nh Kỹ thuật phần mềm nh&amp;uacute;ng v&amp;agrave; di động c&amp;oacute; thể:&lt;/div&gt;\r\n\r\n	&lt;div&gt;L&amp;agrave;m lập tr&amp;igrave;nh vi&amp;ecirc;n, nh&amp;acirc;n vi&amp;ecirc;n kiểm thử, quản trị dự &amp;aacute;n, người ph&amp;acirc;n t&amp;iacute;ch, thiết kế, bảo tr&amp;igrave; hệ thống trong c&amp;aacute;c tổ chức, c&amp;ocirc;ng ty c&amp;oacute; ứng dụng c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin: c&amp;ocirc;ng ty sản xuất, gia c&amp;ocirc;ng phần mềm trong nước cũng như nước ngo&amp;agrave;i; c&amp;ocirc;ng ty tư vấn về đề xuất giải ph&amp;aacute;p, x&amp;acirc;y dựng v&amp;agrave; bảo tr&amp;igrave; c&amp;aacute;c hệ thống phần mềm hoặc l&amp;agrave;m việc tại bộ phận c&amp;ocirc;ng nghệ th&amp;ocirc;ng ở tất cả c&amp;aacute;c đơn vị c&amp;oacute; nhu cầu (h&amp;agrave;nh ch&amp;iacute;nh sự nghiệp, ng&amp;acirc;n h&amp;agrave;ng, viễn th&amp;ocirc;ng, h&amp;agrave;ng kh&amp;ocirc;ng, x&amp;acirc;y dựng&amp;hellip;). R21:&lt;/div&gt;\r\n\r\n	&lt;div&gt;L&amp;agrave;m lập tr&amp;igrave;nh vi&amp;ecirc;n, nh&amp;acirc;n vi&amp;ecirc;n kiểm thử, quản trị dự &amp;aacute;n, người ph&amp;acirc;n t&amp;iacute;ch, thiết kế, bảo tr&amp;igrave; hệ thống trong c&amp;aacute;c tổ chức, c&amp;ocirc;ng ty chuy&amp;ecirc;n về hệ thống nh&amp;uacute;ng, phần mềm nh&amp;uacute;ng với c&amp;aacute;c lĩnh vực như: tự động h&amp;oacute;a, c&amp;aacute;c hệ thống điều khiển, mạng c&amp;ocirc;ng nghiệp,...&lt;/div&gt;\r\n\r\n	&lt;div&gt;L&amp;agrave;m lập tr&amp;igrave;nh vi&amp;ecirc;n, nh&amp;acirc;n vi&amp;ecirc;n kiểm thử, quản trị dự &amp;aacute;n, người ph&amp;acirc;n t&amp;iacute;ch, thiết kế, bảo tr&amp;igrave; hệ thống trong c&amp;aacute;c tổ chức, c&amp;ocirc;ng ty chuy&amp;ecirc;n về c&amp;aacute;c thiết bi di động v&amp;agrave; phần mềm tr&amp;ecirc;n thiết bị di động như: Android, iOS, .Net Micro Framework, v.v. v&amp;agrave; trong c&amp;aacute;c h&amp;atilde;ng sản xuất thiết bị di động như Samsung, Nokia,...&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">\r\n	<div><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong>&nbsp;Hệ thống th&ocirc;ng tin, C&ocirc;ng nghệ phần mềm, Mạng m&aacute;y t&iacute;nh v&agrave; truyền th&ocirc;ng.</div>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<div><strong>Nội dung đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"margin-left: 18pt; text-align: justify;\">Đối với chuy&ecirc;n ng&agrave;nh Hệ thống th&ocirc;ng tin, sinh vi&ecirc;n sau khi tốt nghiệp c&oacute; thể thiết kế v&agrave; quản trị c&aacute;c hệ cơ sở dữ liệu, ph&aacute;t triển c&aacute;c phần mềm v&agrave; giải quyết c&aacute;c vấn đề đặt ra trong thực tế. C&oacute; thể khảo s&aacute;t, ph&acirc;n t&iacute;ch thiết kế hệ thống, lập tr&igrave;nh, triển khai ứng dụng v&agrave; quản trị c&aacute;c dự &aacute;n c&ocirc;ng nghệ th&ocirc;ng tin.</div>\r\n\r\n<div style=\"margin-left: 18pt; text-align: justify;\">Đối với chuy&ecirc;n ng&agrave;nh C&ocirc;ng nghệ phần mềm, sinh vi&ecirc;n sau khi tốt nghiệp c&oacute; thể sử dụng được c&aacute;c c&ocirc;ng cụ hiện đại trong quy tr&igrave;nh ph&aacute;t triển phần mềm. C&oacute; khả năng x&acirc;y dựng c&aacute;c phần mềm theo kiến tr&uacute;c hiện đại v&agrave; tiếp cận c&aacute;c chuẩn c&ocirc;ng nghệ mới trong ph&aacute;t triển phần mềm. C&oacute; khả năng quản l&yacute; c&aacute;c dự &aacute;n ph&aacute;t triển phần mềm theo một số chuẩn hiện đại.</div>\r\n\r\n<div style=\"margin-left: 18pt; text-align: justify;\">Đối với chuy&ecirc;n ng&agrave;nh Mạng m&aacute;y t&iacute;nh v&agrave; truyền th&ocirc;ng, sinh vi&ecirc;n sau khi tốt nghiệp c&oacute; thể tự thiết kế c&aacute;c hệ thống mạng theo nhu cầu thực tế, giải quyết c&aacute;c vấn đề li&ecirc;n quan đến bảo đảm chất lượng, an to&agrave;n, an ninh v&agrave; khai th&aacute;c hiệu quả c&aacute;c mạng truyền th&ocirc;ng.</div>\r\n', 24, '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3725.3117090828014!2d105.79362951437743!3d20.98013859481354!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135acc508f938fd%3A0x883e474806a2d1f2!2zSOG7jWMgVmnhu4duIEvhu7kgVGh14bqtdCBN4bqtdCBNw6M!5e0!3m2!1svi!2s!4v1594633283141!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>', 'hocvienktmatma2.jpg', 4, '2020-06-26 14:39:28', '2020-07-12 16:47:44');
INSERT INTO `tbl_truongdaihoc` (`id_truong`, `tentruong`, `mota`, `gioithieu`, `chuyennganhdaotao`, `diemchuan`, `diachi`, `anh`, `manguoidung`, `ngayviet`, `ngaycapnhat`) VALUES
(6, 'Trường Đại học Kinh tế Kỹ thuật Công nghiệp', 'Trường Đại học Kinh tế Kỹ thuật C&ocirc;ng nghiệp', '&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh:&lt;/strong&gt; C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;Tổng quan giới thiệu trường + Cơ sở hạ tầng:&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;Trường Đại học Kinh tế - Kỹ thuật C&amp;ocirc;ng nghiệp&amp;nbsp;(University of Economics - Technology for Industries), l&amp;agrave; trường đại học c&amp;ocirc;ng lập,&amp;nbsp;định hướng nghề nghiệp ứng dụng, đ&amp;agrave;o tạo cử nh&amp;acirc;n v&amp;agrave; kỹ sư thực h&amp;agrave;nh hệ ch&amp;iacute;nh quy 4 năm, trực thuộc Bộ C&amp;ocirc;ng Thương Việt Nam, được th&amp;agrave;nh lập năm 2007, tr&amp;ecirc;n cơ sở n&amp;acirc;ng cấp Trường Cao đẳng Kinh tế Kỹ thuật C&amp;ocirc;ng nghiệp I, th&amp;agrave;nh lập từ năm 1956, đ&amp;agrave;o tạo đa cấp, đa ng&amp;agrave;nh. Trường Đại học Kinh tế Kỹ thuật C&amp;ocirc;ng nghiệp c&amp;oacute; 3 cơ sở, một cơ sở ở Nam Định, hai cơ sở tại H&amp;agrave; Nội. Tổng diện t&amp;iacute;ch mặt bằng tại cả hai cơ sở Minh Khai v&amp;agrave; Nam Định l&amp;agrave; 30 ha, khu nh&amp;agrave; l&amp;agrave;m việc 9 tầng tại H&amp;agrave; Nội, giảng đường 15 tầng tại Nam Định, khu thực h&amp;agrave;nh v&amp;agrave; k&amp;yacute; t&amp;uacute;c x&amp;aacute; tại Mỹ X&amp;aacute; - Nam Định.&lt;/div&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://khoacntt.uneti.edu.vn/storage/posts/1592206254_Hinhanh_truongsuamoi.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://khoacntt.uneti.edu.vn/storage/posts/1592206254_Hinhanh_truongsuamoi.jpg&quot; style=&quot;height:324px; width:498px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Địa điểm:&lt;/strong&gt; 3 cở sở&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ CS1: Số 456 Minh Khai, Hai B&amp;agrave; Trưng, H&amp;agrave; Nội&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ CS2: Ng&amp;otilde; 218/296 Lĩnh Nam, Ho&amp;agrave;ng Mai, H&amp;agrave; Nội&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ CS3: Số 353 Trần Hưng Đạo, TP.Nam Định&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo:&lt;/strong&gt; 4 năm&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Chuẩn đầu ra:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Quản l&amp;yacute;, vận h&amp;agrave;nh c&amp;aacute;c phần mềm ứng dụng C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin (CNTT) hiện c&amp;oacute;.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Tư vấn cho c&amp;aacute;c chương tr&amp;igrave;nh CNTT của c&amp;aacute;c ng&amp;agrave;nh, c&amp;aacute;c cơ quan, doanh nghiệp.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Đ&amp;aacute;nh gi&amp;aacute;, ph&amp;acirc;n t&amp;iacute;ch hiện trạng ứng dụng CNTT; hệ thống m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; mạng m&amp;aacute;y t&amp;iacute;nh hiện tại của c&amp;aacute;c cơ quan, doanh nghiệp, trường học v&amp;agrave; đề xuất c&amp;aacute;c giải ph&amp;aacute;p cải tiến mới mang lại hiệu quả kinh tế, năng suất khi khai th&amp;aacute;c sử dụng v&amp;agrave; nhằm n&amp;acirc;ng cao hiệu quả c&amp;aacute;c phần mềm ứng dụng CNTT hiện c&amp;oacute;.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Thiết kế, x&amp;acirc;y dựng c&amp;aacute;c phần mềm ứng dụng CNTT.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Cập nhật thường xuy&amp;ecirc;n những kiến thức v&amp;agrave; c&amp;ocirc;ng nghệ mới về CNTT.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://d3av3o1z276gfa.cloudfront.net/images/place/CXyrcvfBMc4Nc8e0V4YpdOppr48rBiWh.jpeg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://d3av3o1z276gfa.cloudfront.net/images/place/CXyrcvfBMc4Nc8e0V4YpdOppr48rBiWh.jpeg&quot; style=&quot;height:350px; width:622px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;Chất lượng giảng vi&amp;ecirc;n:&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div&gt;Khoa c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin được th&amp;agrave;nh lập từ năm 2004, tiền th&amp;acirc;n l&amp;agrave; tổ m&amp;ocirc;n Tin học của khoa Khoa học cơ bản. Đến nay, khoa đ&amp;atilde; c&amp;oacute; 32 C&amp;aacute;n bộ, Giảng vi&amp;ecirc;n, Kỹ thuật vi&amp;ecirc;n, trong đ&amp;oacute; c&amp;oacute; 2 tiến sĩ, 4 nghi&amp;ecirc;n cứu sinh, 22 thạc sĩ, 2 kỹ sư v&amp;agrave; 2 kỹ thuật vi&amp;ecirc;n ph&amp;ograve;ng m&amp;aacute;y.&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;Học ph&amp;iacute;: &lt;/strong&gt;16 triệu đồng/năm.&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m:&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;L&amp;agrave;m việc ở bộ phận CNTT hoặc ứng dụng CNTT của c&amp;aacute;c đơn vị c&amp;oacute; nhu cầu (h&amp;agrave;nh ch&amp;iacute;nh sự nghiệp, viễn th&amp;ocirc;ng, điện lực, ng&amp;acirc;n h&amp;agrave;ng, t&amp;agrave;i ch&amp;iacute;nh, thương mại, &amp;hellip;).&lt;/li&gt;\r\n	&lt;li&gt;L&amp;agrave;m việc trong c&amp;aacute;c c&amp;ocirc;ng ty sản xuất, gia c&amp;ocirc;ng phần mềm trong nước cũng như nước ngo&amp;agrave;i.&lt;/li&gt;\r\n	&lt;li&gt;L&amp;agrave;m việc trong c&amp;aacute;c c&amp;ocirc;ng ty tư vấn về đề xuất giải ph&amp;aacute;p, x&amp;acirc;y dựng v&amp;agrave; bảo tr&amp;igrave; c&amp;aacute;c hệ thống th&amp;ocirc;ng tin, hệ thống mạng v&amp;agrave; truyền th&amp;ocirc;ng.&lt;/li&gt;\r\n	&lt;li&gt;Giảng dạy CNTT tại c&amp;aacute;c trường Cao đẳng, Trung học chuy&amp;ecirc;n nghiệp, Dạy nghề v&amp;agrave; c&amp;aacute;c trường Phổ th&amp;ocirc;ng (&lt;em&gt;cần th&amp;ecirc;m chứng chỉ nghiệp vụ sư phạm&lt;/em&gt;).&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong> C&ocirc;ng nghệ th&ocirc;ng tin</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><strong>Nội dung đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"text-align: justify;\"><em>+ Về kiến thức:</em> Trang bị kiến thức nền tảng trong c&ocirc;ng nghệ th&ocirc;ng tin như cơ sở dữ liệu, kiến tr&uacute;c m&aacute;y t&iacute;nh, nguy&ecirc;n l&yacute; hệ điều h&agrave;nh,&hellip;giảng dạy c&aacute;c phương ph&aacute;p, giải thuật v&agrave; c&ocirc;ng cụ để ph&acirc;n t&iacute;ch, thiết kế, ph&aacute;t triển v&agrave; triển khai sản phẩm hay giải ph&aacute;p phần mềm. Ngo&agrave;i ra l&agrave; đ&agrave;o tạo kiến thức chuy&ecirc;n ng&agrave;nh trong c&aacute;c lĩnh vưc c&ocirc;ng nghệ th&ocirc;ng tin, một số nghiệp vụ quản l&yacute; v&agrave; x&atilde; hội c&oacute; thể được kết hợp với ng&agrave;nh c&ocirc;ng nghệ th&ocirc;ng tin.</div>\r\n\r\n<div style=\"text-align: justify;\"><em>+ Về kỹ năng:</em> Được đ&agrave;o tạo sử dụng th&agrave;nh thạo tối thiểu 2 ng&ocirc;n ngữ lập tr&igrave;nh (C++, Java/C#), vận dụng nguy&ecirc;n l&yacute; v&agrave; cơ chế hoạt động của c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh kh&aacute;c nhau để giải quyết b&agrave;i to&aacute;n thực tế, c&oacute; kỹ năng tham gia triển khai một hệ thống hay giải ph&aacute;p c&ocirc;ng nghệ th&ocirc;ng tin cho c&aacute;c cơ quan, đơn vị.</div>\r\n', 22, '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3724.92345108654!2d105.86335631437775!3d20.995705394283114!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ac05404c1b1d%3A0xd865d467337f19d0!2zVHLGsOG7nW5nIMSQ4bqhaSBo4buNYyBLaW5oIHThur8gS-G7uSB0aHXhuq10IEPDtG5nIG5naGnhu4dwIChVTkVUSSk!5e0!3m2!1svi!2s!4v1594633263878!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>', 'ktktcn.jpg', 4, '2020-06-26 21:25:19', '2020-07-12 16:42:42'),
(7, 'Trường Học viện Kỹ thuật Quân sự', 'Trường Học viện Kỹ thuật Qu&acirc;n sự', '&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh:&lt;/strong&gt; Ng&amp;agrave;nh C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin Học viện kỹ thuật qu&amp;acirc;n sự&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Giới thiệu về trường:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;Học viện Kỹ thuật Qu&amp;acirc;n sự&amp;nbsp;l&amp;agrave; m&amp;ocirc;i trường nghi&amp;ecirc;n cứu v&amp;agrave; đ&amp;agrave;o tạo đội ngũ kỹ sư khoa học, kỹ thuật, c&amp;ocirc;ng nghệ h&amp;agrave;ng đầu của qu&amp;acirc;n đội v&amp;agrave; đất nước. Với mục ti&amp;ecirc;u x&amp;acirc;y dựng Khoa C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin theo m&amp;ocirc; h&amp;igrave;nh định hướng nghi&amp;ecirc;n cứu, tăng cường t&amp;iacute;ch hợp giữa đ&amp;agrave;o tạo v&amp;agrave; nghi&amp;ecirc;n cứu khoa học, hiện tại. Đến nay, c&amp;aacute;c chương tr&amp;igrave;nh đ&amp;agrave;o tạo ng&amp;agrave;nh CNTT của Khoa thường xuy&amp;ecirc;n được cập nhật để ph&amp;ugrave; hợp với sự ph&amp;aacute;t triển c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin tr&amp;ecirc;n thế giới v&amp;agrave; nhu cầu x&amp;atilde; hội. Hiện nay, Khoa CNTT đang triển khai x&amp;acirc;y dựng chương tr&amp;igrave;nh đ&amp;agrave;o tạo theo c&amp;aacute;c chương tr&amp;igrave;nh đ&amp;agrave;o tạo ti&amp;ecirc;n tiến v&amp;agrave; quản l&amp;yacute;, tổ chức đ&amp;agrave;o tạo theo c&amp;aacute;c ti&amp;ecirc;u chuẩn kiểm định quốc tế (AUN).&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: center;&quot;&gt;&lt;a href=&quot;https://icdn.dantri.com.vn/ZnmrEyihQodbfhR9C8h5/Image/2013/03/hoc_viet_ky_thuat_quan_su_1-e685d.JPG&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://icdn.dantri.com.vn/ZnmrEyihQodbfhR9C8h5/Image/2013/03/hoc_viet_ky_thuat_quan_su_1-e685d.JPG&quot; style=&quot;height:420px; width:427px&quot; /&gt;&lt;/a&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;&lt;strong&gt;- Học ph&amp;iacute;:&lt;/strong&gt; Hệ qu&amp;acirc;n sự được miễn giảm to&amp;agrave;n bộ. Học ph&amp;iacute; hệ d&amp;acirc;n sự được t&amp;iacute;nh cụ thể như sau:&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;- Đối với mức thu học ph&amp;iacute; hệ đại học ch&amp;iacute;nh quy:&amp;nbsp;225.000 đồng/t&amp;iacute;n chỉ&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;- Đối với đồ &amp;aacute;n tốt nghiệp:&amp;nbsp;3.000.000 đồng/đồ &amp;aacute;n&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;- Đối với hệ Cao đẳng của trường:&amp;nbsp;630.000 đồng/th&amp;aacute;ng/sinh vi&amp;ecirc;n. Tương đương&amp;nbsp;6.300.000 đồng/năm.&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo:&lt;/strong&gt; 5 năm&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Mục ti&amp;ecirc;u đ&amp;agrave;o tạo:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Chương tr&amp;igrave;nh đ&amp;agrave;o tạo ng&amp;agrave;nh C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin gồm 03 chuy&amp;ecirc;n ng&amp;agrave;nh: Hệ thống th&amp;ocirc;ng tin, C&amp;ocirc;ng nghệ phần mềm, Mạng m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; truyền th&amp;ocirc;ng.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Đối với chuy&amp;ecirc;n ng&amp;agrave;nh Hệ thống th&amp;ocirc;ng tin, sinh vi&amp;ecirc;n sau khi tốt nghiệp c&amp;oacute; thể thiết kế v&amp;agrave; quản trị c&amp;aacute;c hệ cơ sở dữ liệu, ph&amp;aacute;t triển c&amp;aacute;c phần mềm v&amp;agrave; giải quyết c&amp;aacute;c vấn đề đặt ra trong thực tế. C&amp;oacute; thể khảo s&amp;aacute;t, ph&amp;acirc;n t&amp;iacute;ch thiết kế hệ thống, lập tr&amp;igrave;nh, triển khai ứng dụng v&amp;agrave; quản trị c&amp;aacute;c dự &amp;aacute;n c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Đối với chuy&amp;ecirc;n ng&amp;agrave;nh C&amp;ocirc;ng nghệ phần mềm, sinh vi&amp;ecirc;n sau khi tốt nghiệp c&amp;oacute; thể sử dụng được c&amp;aacute;c c&amp;ocirc;ng cụ hiện đại trong quy tr&amp;igrave;nh ph&amp;aacute;t triển phần mềm. C&amp;oacute; khả năng x&amp;acirc;y dựng c&amp;aacute;c phần mềm theo kiến tr&amp;uacute;c hiện đại v&amp;agrave; tiếp cận c&amp;aacute;c chuẩn c&amp;ocirc;ng nghệ mới trong ph&amp;aacute;t triển phần mềm. C&amp;oacute; khả năng quản l&amp;yacute; c&amp;aacute;c dự &amp;aacute;n ph&amp;aacute;t triển phần mềm theo một số chuẩn hiện đại.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Đối với chuy&amp;ecirc;n ng&amp;agrave;nh Mạng m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; truyền th&amp;ocirc;ng, sinh vi&amp;ecirc;n sau khi tốt nghiệp c&amp;oacute; thể tự thiết kế c&amp;aacute;c hệ thống mạng theo nhu cầu thực tế, giải quyết c&amp;aacute;c vấn đề li&amp;ecirc;n quan đến bảo đảm chất lượng, an to&amp;agrave;n, an ninh v&amp;agrave; khai th&amp;aacute;c hiệu quả c&amp;aacute;c mạng truyền th&amp;ocirc;ng.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://i.pinimg.com/736x/74/7f/43/747f434f9d72e618e861790b980ac339.jpg&quot; style=&quot;height:440px; width:587px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Chất lượng giảng vi&amp;ecirc;n:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt; text-align: justify;&quot;&gt;Hơn 1300 c&amp;aacute;n bộ với hơn 900 giảng vi&amp;ecirc;n c&amp;oacute; tr&amp;igrave;nh độ cao, gi&amp;agrave;u kinh nghiệm, trong đ&amp;oacute;: gần 100 giảng vi&amp;ecirc;n được phong học h&amp;agrave;m Gi&amp;aacute;o sư, Ph&amp;oacute; gi&amp;aacute;o sư, 450 c&amp;aacute;n bộ đạt học vị Tiến sĩ Khoa học v&amp;agrave; Tiến sĩ chuy&amp;ecirc;n ng&amp;agrave;nh, 45 nh&amp;agrave; gi&amp;aacute;o được phong tặng danh hiệu Nh&amp;agrave; gi&amp;aacute;o Nh&amp;acirc;n d&amp;acirc;n v&amp;agrave; Nh&amp;agrave; gi&amp;aacute;o Ưu t&amp;uacute;.&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Giảng dạy c&amp;aacute;c m&amp;ocirc;n li&amp;ecirc;n quan đến c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin tại c&amp;aacute;c trường đại học, cao đẳng, trung học chuy&amp;ecirc;n nghiệp, dạy nghề v&amp;agrave; c&amp;aacute;c trường phổ th&amp;ocirc;ng.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Nghi&amp;ecirc;n cứu khoa học thuộc c&amp;aacute;c lĩnh vực về khoa học m&amp;aacute;y t&amp;iacute;nh, hệ thống th&amp;ocirc;ng tin, c&amp;ocirc;ng nghệ phần mềm, mạng m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; truyền th&amp;ocirc;ng ở c&amp;aacute;c viện nghi&amp;ecirc;n cứu, c&amp;aacute;c trung t&amp;acirc;m v&amp;agrave; cơ quan nghi&amp;ecirc;n cứu của c&amp;aacute;c bộ, ng&amp;agrave;nh, c&amp;aacute;c trường đại học v&amp;agrave; cao đẳng.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;L&amp;agrave;m việc ở bộ phận c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin hoặc cần ứng dụng c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin của tất cả c&amp;aacute;c đơn vị c&amp;oacute; nhu cầu (h&amp;agrave;nh ch&amp;iacute;nh sự nghiệp, ng&amp;acirc;n h&amp;agrave;ng, viễn th&amp;ocirc;ng, h&amp;agrave;ng kh&amp;ocirc;ng, x&amp;acirc;y dựng,&amp;hellip;).&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;L&amp;agrave;m việc trong c&amp;aacute;c c&amp;ocirc;ng ty sản xuất, gia c&amp;ocirc;ng phần mềm trong nước cũng như nước ngo&amp;agrave;i.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;L&amp;agrave;m việc tại c&amp;aacute;c c&amp;ocirc;ng ty tư vấn về đề xuất giải ph&amp;aacute;p, x&amp;acirc;y dựng v&amp;agrave; bảo tr&amp;igrave; c&amp;aacute;c hệ thống th&amp;ocirc;ng tin, hệ thống mạng v&amp;agrave; truyền th&amp;ocirc;ng.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Chuần đầu ra cho sinh vi&amp;ecirc;n:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;oacute; kiến thức cơ bản tốt, đ&amp;aacute;p ứng nhu cầu thực tế của x&amp;atilde; hội.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;oacute; khả năng giao tiếp tốt bằng tiếng Anh.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;oacute; khả năng l&amp;agrave;m việc độc lập, s&amp;aacute;ng tạo, tư duy logic.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;oacute; khả năng tổ chức v&amp;agrave; điều h&amp;agrave;nh c&amp;oacute; hiệu quả trong l&amp;agrave;m việc nh&amp;oacute;m.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;oacute; khả năng x&amp;acirc;y dựng, điều h&amp;agrave;nh v&amp;agrave; ph&amp;aacute;t triển c&amp;aacute;c ứng dụng c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin v&amp;agrave; c&amp;aacute;c chương tr&amp;igrave;nh đ&amp;agrave;o tạo tin học, hệ thống học tập trực tuyến cho c&amp;aacute;c tổ chức c&amp;oacute; ứng dụng c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;oacute; khả năng tr&amp;igrave;nh b&amp;agrave;y, giải đ&amp;aacute;p v&amp;agrave; phản biện c&amp;aacute;c vấn đề thuộc lĩnh vực c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;oacute; khả năng tư vấn về bảo mật, giải ph&amp;aacute;p kỹ thuật v&amp;agrave; c&amp;ocirc;ng nghệ; tư vấn thiết kế hệ thống.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;oacute; khả năng học tập, nghi&amp;ecirc;n cứu để tự trang bị, bổ sung th&amp;ecirc;m những tri thức mới trong lĩnh vực c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>\r\n	<div><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div>+ C&ocirc;ng nghệ dữ liệu</div>\r\n\r\n<div>+ Ph&aacute;t triển ứng dụng đa phương tiện</div>\r\n\r\n<div>+ An to&agrave;n th&ocirc;ng tin</div>\r\n\r\n<ul>\r\n	<li>\r\n	<div><strong>Nội dung đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div><em>+ C&ocirc;ng nghệ dữ liệu:</em> Đ&agrave;o tạo cử nh&acirc;n, kỹ sư ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin, chuy&ecirc;n ng&agrave;nh C&ocirc;ng nghệ dữ liệu, c&oacute; khả năng thu thập, xử l&yacute; v&agrave; ph&acirc;n t&iacute;ch dữ liệu nhằm tr&iacute;ch xuất tri thức từ c&aacute;c nguồn th&ocirc;ng tin kh&aacute;c nhau; c&oacute; khả năng nắm bắt, triển khai, ph&aacute;t triển c&aacute;c c&ocirc;ng cụ v&agrave; đề xuất c&aacute;c giải ph&aacute;p kỹ thuật nhằm thu thập, xử l&yacute; v&agrave; ph&acirc;n t&iacute;ch dữ liệu cho doanh nghiệp v&agrave; tổ chức; c&oacute; kiến thức nền tảng vững chắc về quy tr&igrave;nh nghiệp vụ v&agrave; quy tr&igrave;nh xử l&yacute; dữ liệu trong c&aacute;c cơ quan tổ chức, c&oacute; năng lực ứng dụng th&agrave;nh quả mới nhất của c&ocirc;ng nghệ thu thập, lưu trữ v&agrave; xử l&yacute; dữ liệu v&agrave;o c&ocirc;ng việc nhằm n&acirc;ng cao hiệu quả hoạt động của doanh nghiệp v&agrave; tổ chức.</div>\r\n\r\n<div><em>+ Ph&aacute;t triển ứng dụng đa phương tiện:</em> Đ&agrave;o tạo cử nh&acirc;n, kỹ sư ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin, chuy&ecirc;n ng&agrave;nh Ph&aacute;t triển ứng dụng đa phương tiện c&oacute; khả năng khả năng khảo s&aacute;t, ph&acirc;n t&iacute;ch, thiết kế, x&acirc;y dựng, quản trị c&aacute;c ứng dụng đa phương tiện cho doanh nghiệp v&agrave; tổ chức; c&oacute; kiến thức nền tảng vững chắc về quy tr&igrave;nh ph&aacute;t triển ứng dụng đa phương tiện, c&aacute;c c&ocirc;ng cụ v&agrave; c&ocirc;ng nghệ cần thiết để ph&aacute;t triển ứng dụng; c&oacute; khả năng triển khai, ph&aacute;t triển c&aacute;c c&ocirc;ng cụ v&agrave; đề xuất c&aacute;c giải ph&aacute;p kỹ thuật nhằm x&acirc;y dựng ứng dụng đa phương tiện; c&oacute; năng lực ứng dụng th&agrave;nh tựu mới nhất của C&ocirc;ng nghệ th&ocirc;ng tin v&agrave;o c&ocirc;ng việc nhằm n&acirc;ng cao hiệu quả hoạt động của cơ quan, tổ chức, doanh nghiệp.</div>\r\n\r\n<div><em>+ An to&agrave;n th&ocirc;ng tin: </em>Đ&agrave;o tạo ra c&aacute;c cử nh&acirc;n, kỹ sư ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin, chuy&ecirc;n ng&agrave;nh An to&agrave;n th&ocirc;ng tin c&oacute; khả năng nắm bắt, triển khai, ph&aacute;t triển c&aacute;c c&ocirc;ng cụ v&agrave; đề xuất c&aacute;c giải ph&aacute;p kỹ thuật nhằm bảo đảm an to&agrave;n th&ocirc;ng tin cho doanh nghiệp v&agrave; tổ chức; c&oacute; năng lực ứng dụng th&agrave;nh quả mới nhất của lĩnh vực v&agrave;o bảo đảm an to&agrave;n th&ocirc;ng tin nhằm n&acirc;ng cao t&iacute;nh an to&agrave;n, bảo mật của th&ocirc;ng tin b&ecirc;n trong doanh nghiệp v&agrave; tổ chức.</div>\r\n', 25, '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3723.667816215761!2d105.79310681437856!3d21.045973492567573!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ab863fe91815%3A0x4ae46f59290424d7!2zSOG7jWMgVmnhu4duIEvhu7kgVGh14bqtdCBRdcOibiBT4bux!5e0!3m2!1svi!2s!4v1594633226978!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>', 'ktqs.jpg', 4, '2020-06-26 23:29:52', '2020-07-12 16:39:38'),
(8, 'Trường Đại học Công nghiệp Hà Nội', 'Trường Đại học C&ocirc;ng nghiệp H&agrave; Nội', '&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh:&lt;/strong&gt; C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;&lt;strong&gt;Tổng quan giới thiệu trường + Cơ sở hạ tầng:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;Trường Đại học C&amp;ocirc;ng nghiệp H&amp;agrave; Nội&amp;nbsp;(&lt;em&gt;Hanoi University of Industry&lt;/em&gt;) l&amp;agrave; một trường đại học c&amp;ocirc;ng lập, đa cấp, đa ng&amp;agrave;nh nghề, định hướng thực h&amp;agrave;nh&amp;nbsp;trực thuộc&lt;span style=&quot;color:#000000&quot;&gt;&amp;nbsp;Bộ C&amp;ocirc;ng Thương&lt;/span&gt;. Trường được n&amp;acirc;ng cấp l&amp;ecirc;n đại học năm 2005 tr&amp;ecirc;n cơ sở Trường Cao Đẳng C&amp;ocirc;ng Nghiệp H&amp;agrave; Nội. Tiền th&amp;acirc;n s&amp;aacute;p nhập từ hai trường do thực d&amp;acirc;n Ph&amp;aacute;p&amp;nbsp;th&amp;agrave;nh lập l&amp;agrave; Trường Chuy&amp;ecirc;n nghiệp H&amp;agrave; Nội&amp;nbsp;năm 1898&lt;strong&gt;,&lt;/strong&gt;&amp;nbsp;v&amp;agrave; Trường Chuy&amp;ecirc;n nghiệp Hải Ph&amp;ograve;ng&amp;nbsp;năm 1913. Trường đ&amp;agrave;o tạo tiến sĩ cơ kh&amp;iacute;, thạc sĩ, kỹ sư thực h&amp;agrave;nh hệ 4 năm, cử nh&amp;acirc;n kinh tế, cao đẳng kỹ thuật, cao đẳng nghề, trung cấp kỹ thuật, trung cấp nghề v&amp;agrave; sơ cấp nghề.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;Cơ sở vật chất g&amp;ocirc;̀m&amp;nbsp;12 ph&amp;ograve;ng m&amp;aacute;y&amp;nbsp;với&amp;nbsp;tr&amp;ecirc;n 370 m&amp;aacute;y vi t&amp;iacute;nh; c&amp;aacute;c ph&amp;ograve;ng LAB: Hệ thống nh&amp;uacute;ng v&amp;agrave; Đa phương tiện; Ph&amp;aacute;t triển ứng dụng mobile; T&amp;iacute;nh to&amp;aacute;n hiệu năng cao.&lt;/div&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://lh3.googleusercontent.com/2EmAVp7sUYEv7faQDK3xkdcpuk2S_V31PfC04cYN1ulZ598TDrB630-jHaKR2zdIBl-ehEzq=w1080-h608-p-no-v0&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://lh3.googleusercontent.com/2EmAVp7sUYEv7faQDK3xkdcpuk2S_V31PfC04cYN1ulZ598TDrB630-jHaKR2zdIBl-ehEzq=w1080-h608-p-no-v0&quot; style=&quot;height:308px; width:547px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Địa điểm:&lt;/strong&gt; số 298 đường Cầu Diễn, quận Bắc Từ Li&amp;ecirc;m, H&amp;agrave; Nội.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo:&lt;/strong&gt; 4 năm&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Mục ti&amp;ecirc;u đ&amp;agrave;o tạo:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Được trang bị kiến thức li&amp;ecirc;n quan đến nghi&amp;ecirc;n cứu ph&amp;aacute;t triển, gia c&amp;ocirc;ng hay ứng dụng c&amp;aacute;c hệ thống phần mềm; hiểu c&amp;aacute;c nguy&amp;ecirc;n tắc quản l&amp;yacute; dự &amp;aacute;n CNTT, đảm bảo chất lượng phần mềm.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Được trang bị kiến thức về đa phương tiện v&amp;agrave; c&amp;aacute;c c&amp;ocirc;ng cụ xử l&amp;yacute; đa phương tiện.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;C&amp;oacute; kiến thức về thiết kế, x&amp;acirc;y dựng, c&amp;agrave;i đặt, vận h&amp;agrave;nh v&amp;agrave; bảo tr&amp;igrave; c&amp;aacute;c th&amp;agrave;nh phần phần cứng, phần mềm của hệ thống m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; c&amp;aacute;c hệ thống thiết bị dựa tr&amp;ecirc;n m&amp;aacute;y t&amp;iacute;nh.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: center;&quot;&gt;C&amp;oacute; kiến thức về mạng m&amp;aacute;y t&amp;iacute;nh, c&amp;aacute;c phương ph&amp;aacute;p v&amp;agrave; quy tr&amp;igrave;nh đảm bảo an to&amp;agrave;n cho hệ thống mạng m&amp;aacute;y t&amp;iacute;nh.&lt;a href=&quot;https://icdn.dantri.com.vn/thumb_w/640/2018/2/27/dh-cong-nghiep-ha-noi-15197254655532104513221.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://icdn.dantri.com.vn/thumb_w/640/2018/2/27/dh-cong-nghiep-ha-noi-15197254655532104513221.jpg&quot; style=&quot;height:427px; width:640px&quot; /&gt;&lt;/a&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Chất lượng giảng vi&amp;ecirc;n:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;Hiện nay nh&amp;agrave; trường c&amp;oacute; gần 1.500 c&amp;aacute;n bộ, giảng vi&amp;ecirc;n, trong đ&amp;oacute; c&amp;oacute; tr&amp;ecirc;n 200 giảng vi&amp;ecirc;n c&amp;oacute; tr&amp;igrave;nh độ Tiến sĩ, PGS, GS. Quy m&amp;ocirc; đ&amp;agrave;o tạo của trường cũng được mở rộng đ&amp;aacute;ng kể l&amp;ecirc;n đến tr&amp;ecirc;n 30.000 học vi&amp;ecirc;n, sinh vi&amp;ecirc;n mỗi năm.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Học ph&amp;iacute;:&lt;/strong&gt; 18 triệu đồng/năm&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;\r\n	&lt;div&gt;C&amp;oacute; thể l&amp;agrave;m việc trong c&amp;aacute;c tổ chức, c&amp;ocirc;ng ty c&amp;oacute; ứng dụng c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin như:&lt;/div&gt;\r\n	&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ol style=&quot;margin-left: 40px;&quot;&gt;\r\n	&lt;li&gt;C&amp;aacute;c doanh nghiệp thiết kế, gia c&amp;ocirc;ng, ph&amp;aacute;t triển phần mềm.&lt;/li&gt;\r\n	&lt;li&gt;C&amp;aacute;c c&amp;ocirc;ng ty cung cấp nội dung số, sản xuất games.&lt;/li&gt;\r\n	&lt;li&gt;C&amp;aacute;c c&amp;ocirc;ng ty tư vấn: tư vấn thiết kế c&amp;aacute;c giải ph&amp;aacute;p về mạng, c&amp;aacute;c giải ph&amp;aacute;p c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin cho doanh nghiệp.&lt;/li&gt;\r\n	&lt;li&gt;C&amp;aacute;c c&amp;ocirc;ng ty kinh doanh dịch vụ v&amp;agrave; c&amp;aacute;c sản phẩm CNTT, bao gồm cả phần cứng lẫn phần mềm.&lt;/li&gt;\r\n	&lt;li&gt;Bộ phận vận h&amp;agrave;nh v&amp;agrave; ph&amp;aacute;t triển c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin của c&amp;aacute;c cơ quan, nh&amp;agrave; m&amp;aacute;y, trường học, ng&amp;acirc;n h&amp;agrave;ng&amp;hellip; c&amp;aacute;c doanh nghiệp c&amp;oacute; ứng dụng c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/li&gt;\r\n	&lt;li&gt;C&amp;aacute;c trường cao đẳng, trung học chuy&amp;ecirc;n nghiệp, c&amp;aacute;c trường phổ th&amp;ocirc;ng (chỉ cần bổ sung chứng chỉ nghiệp vụ sư phạm theo quy định).&lt;/li&gt;\r\n	&lt;li&gt;C&amp;aacute;c Viện nghi&amp;ecirc;n cứu v&amp;agrave; chuyển giao c&amp;ocirc;ng nghệ thuộc lĩnh vực c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;\r\n	&lt;div&gt;Cụ thể sinh vi&amp;ecirc;n c&amp;oacute; năng lực để l&amp;agrave;m việc tại c&amp;aacute;c vị tr&amp;iacute;, chức danh sau:&lt;/div&gt;\r\n	&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ol style=&quot;margin-left:40px&quot;&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Lập tr&amp;igrave;nh vi&amp;ecirc;n; Chuy&amp;ecirc;n vi&amp;ecirc;n ph&amp;acirc;n t&amp;iacute;ch nghiệp vụ; Chuy&amp;ecirc;n vi&amp;ecirc;n thiết kế phần mềm; Chuy&amp;ecirc;n vi&amp;ecirc;n kiểm thử phần mềm; Chuy&amp;ecirc;n vi&amp;ecirc;n hỗ trợ, tư vấn, cung cấp c&amp;aacute;c dịch vụ, sản phẩm CNTT; Chuy&amp;ecirc;n vi&amp;ecirc;n quản trị mạng, cơ sở dữ liệu, website.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;Nghi&amp;ecirc;n cứu vi&amp;ecirc;n/ Giảng vi&amp;ecirc;n CNTT.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ol&gt;\r\n', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Khoa học m&aacute;y t&iacute;nh.</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Hệ thống th&ocirc;ng tin.</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ C&ocirc;ng nghệ phần mềm.</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Kỹ thuật v&agrave; mạng m&aacute;y t&iacute;nh.</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ C&ocirc;ng nghệ đa phương tiện.</div>\r\n\r\n<div style=\"margin-left: 40px; text-align: justify;\">+ Kỹ thuật &ndash; H&agrave;nh ch&iacute;nh.</div>\r\n\r\n<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><strong>Nội dung đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"text-align: justify;\"><em>+ Về kiến thức:</em> Sinh vi&ecirc;n hiểu kiến tr&uacute;c, nguy&ecirc;n l&yacute; hoạt động của m&aacute;y t&iacute;nh v&agrave; c&aacute;c thiết bị ngoại vi, nguy&ecirc;n tắc biểu diễn v&agrave; xử l&yacute; th&ocirc;ng tin tr&ecirc;n m&aacute;y t&iacute;nh, nguy&ecirc;n l&yacute; v&agrave; phương ph&aacute;p thiết kế hệ điều h&agrave;nh; nắm được c&aacute;c kỹ thuật v&agrave; ng&ocirc;n ngữ lập tr&igrave;nh cơ bản; c&oacute; kiến thức về cấu tr&uacute;c dữ liệu v&agrave; giải thuật, cơ sở dữ liệu, mạng m&aacute;y t&iacute;nh; c&oacute; kiến thức chuy&ecirc;n s&acirc;u để tự nghi&ecirc;n cứu ph&aacute;t triển, gia c&ocirc;ng hay ứng dụng c&aacute;c hệ thống phần mềm; hiểu c&aacute;c nguy&ecirc;n tắc quản l&yacute; dự &aacute;n CNTT, đảm bảo chất lượng phần mềm; C&oacute; kiến thức về đa phương tiện v&agrave; c&aacute;c c&ocirc;ng cụ xử l&yacute; đa phương tiện; C&oacute; kiến thức về thiết kế, x&acirc;y dựng, c&agrave;i đặt, vận h&agrave;nh v&agrave; bảo tr&igrave; c&aacute;c th&agrave;nh phần phần cứng, phần mềm của hệ thống m&aacute;y t&iacute;nh v&agrave; c&aacute;c hệ thống thiết bị dựa tr&ecirc;n m&aacute;y t&iacute;nh; C&oacute; kiến thức về mạng m&aacute;y t&iacute;nh, c&aacute;c phương ph&aacute;p v&agrave; quy tr&igrave;nh đảm bảo an to&agrave;n cho hệ thống mạng m&aacute;y t&iacute;nh.</div>\r\n\r\n<div style=\"text-align: justify;\"><em>+ Về kỹ năng:</em> Khảo s&aacute;t, thiết kế, x&acirc;y dựng, vận h&agrave;nh, bảo tr&igrave; v&agrave; ph&aacute;t triển hệ thống m&aacute;y t&iacute;nh v&agrave; mạng m&aacute;y t&iacute;nh cho c&aacute;c cơ quan, trường học, doanh nghiệp,&hellip;với quy m&ocirc; vừa v&agrave; nhỏ. Quản trị dữ liệu, quản trị mạng, quản trị website cho c&aacute;c tổ chức, doanh nghiệp, c&ocirc;ng ty. Nghi&ecirc;n cứu, m&ocirc; h&igrave;nh h&oacute;a c&aacute;c b&agrave;i to&aacute;n thực tế, x&acirc;y dựng thuật to&aacute;n, cơ sở dữ liệu, thiết kế v&agrave; c&agrave;i đặt c&aacute;c phần mềm ứng dụng; c&oacute; kỹ năng quản l&yacute; dự &aacute;n, đảm bảo chất lượng phần mềm,&hellip;</div>\r\n', 22, '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3723.4556912348494!2d105.7329533143787!3d21.05445439227778!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31345457e292d5bf%3A0x20ac91c94d74439a!2zVHLGsOG7nW5nIMSQ4bqhaSBo4buNYyBDw7RuZyBuZ2hp4buHcCBIw6AgTuG7mWk!5e0!3m2!1svi!2s!4v1594633198516!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>', 'dhcnhn.jpg', 4, '2020-06-26 23:46:23', '2020-07-12 16:35:54'),
(10, 'Học viện Công nghệ Bưu chính Viễn thông', 'Học viện C&ocirc;ng nghệ Bưu ch&iacute;nh Viễn th&ocirc;ng', '&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh: &lt;/strong&gt;C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Tổng quan giới thiệu trường + Cơ sở hạ tầng:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ Học viện C&amp;ocirc;ng nghệ Bưu ch&amp;iacute;nh Viễn th&amp;ocirc;ng&amp;nbsp;(Posts and Telecommunications Institute of Technology) l&amp;agrave; một tổ chức Nghi&amp;ecirc;n cứu - Gi&amp;aacute;o dục Đ&amp;agrave;o tạo c&amp;oacute; thương hiệu, uy t&amp;iacute;n với thế mạnh về Nghi&amp;ecirc;n cứu v&amp;agrave; đ&amp;agrave;o tạo Đại học, Sau Đại học trong lĩnh vực C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng. Học viện l&amp;agrave; cơ sở đ&amp;agrave;o tạo c&amp;ocirc;ng lập trực thuộc Bộ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng.Trường được th&amp;agrave;nh lập từ năm 1953 nhưng đến ng&amp;agrave;y 11/07/1997&amp;nbsp;được sắp xếp lại tr&amp;ecirc;n cơ sở s&amp;aacute;p nhập Viện Khoa học Kỹ thuật Bưu điện, Viện Kinh tế Bưu điện, Trung t&amp;acirc;m Đ&amp;agrave;o tạo Bưu ch&amp;iacute;nh - Viễn th&amp;ocirc;ng I v&amp;agrave; Trung t&amp;acirc;m Đ&amp;agrave;o tạo Bưu ch&amp;iacute;nh - Viễn th&amp;ocirc;ng II.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ Học viện c&amp;oacute; 2 cơ sở đ&amp;agrave;o tạo Đại học tại H&amp;agrave; Nội v&amp;agrave; Th&amp;agrave;nh phố Hồ Ch&amp;iacute; Minh với quy m&amp;ocirc; 29.000 sinh vi&amp;ecirc;n. Học viện cũng c&amp;oacute; 3 Viện nghi&amp;ecirc;n cứu chuy&amp;ecirc;n ng&amp;agrave;nh CNTT &amp;amp; Truyền th&amp;ocirc;ng, Kinh tế với h&amp;agrave;ng trăm đề t&amp;agrave;i, nhiệm vụ KHCN h&amp;agrave;ng năm theo cơ chế đặt h&amp;agrave;ng từ doanh nghiệp. Hai trung t&amp;acirc;m đ&amp;agrave;o tạo bồi dưỡng của Học viện cung cấp c&amp;aacute;c kh&amp;oacute;a đ&amp;agrave;o tạo ngắn hạn với lưu lượng người học đạt 10.000 người/năm.&lt;/div&gt;\r\n\r\n&lt;p style=&quot;margin-left:36pt; text-align:center&quot;&gt;&lt;a href=&quot;https://sieuthigiake.com/img/Ke-trung-tai-lap-dat-cho-Hoc-vien-Cong-nghe-Buu-chinh-Vien-Thong-4.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://sieuthigiake.com/img/Ke-trung-tai-lap-dat-cho-Hoc-vien-Cong-nghe-Buu-chinh-Vien-Thong-4.jpg&quot; style=&quot;height:410px; width:547px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Địa điểm:&lt;/strong&gt; Tầng 9,T&amp;ograve;a nh&amp;agrave; A2, Km10, Đường Nguyễn Tr&amp;atilde;i, H&amp;agrave; Đ&amp;ocirc;ng, H&amp;agrave; Nội.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo&lt;/strong&gt;: 4 năm&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://i.ytimg.com/vi/gyHkK1l0dOU/maxresdefault.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://i.ytimg.com/vi/gyHkK1l0dOU/maxresdefault.jpg&quot; style=&quot;height:320px; width:569px&quot; /&gt;&lt;/a&gt;​&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Đối tượng đ&amp;agrave;o tạo:&lt;/strong&gt; Đ&amp;atilde; tốt nghiệp THPT hoặc tương đương, tham dự v&amp;agrave; tr&amp;uacute;ng tuyển trong k&amp;igrave; thi tuyển sinh đại học&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Học ph&amp;iacute;:&lt;/strong&gt; 18 triệu đồng/năm.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m:&amp;nbsp;&lt;/strong&gt;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Sau khi tốt nghiệp sinh vi&amp;ecirc;n c&amp;oacute; thể đảm nhận tốt c&amp;aacute;c vị tr&amp;iacute; l&amp;agrave; c&amp;aacute;n bộ kỹ thuật, quản l&amp;yacute;, điều h&amp;agrave;nh trong lĩnh vực c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;C&amp;oacute; thể trở th&amp;agrave;nh c&amp;aacute;c lập tr&amp;igrave;nh vi&amp;ecirc;n, c&amp;aacute;c nh&amp;agrave; quản trị hệ thống, quản trị bảo mật hệ thống c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin trong bất kỳ doanh nghiệp n&amp;agrave;o;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;C&amp;oacute; thể l&amp;agrave;m việc trong c&amp;aacute;c dự &amp;aacute;n với vai tr&amp;ograve; l&amp;agrave; người quản trị dự &amp;aacute;n về c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;C&amp;oacute; thể trở th&amp;agrave;nh c&amp;aacute;n bộ nghi&amp;ecirc;n cứu, c&amp;aacute;n bộ giảng dạy về c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin tại c&amp;aacute;c Viện, Trung t&amp;acirc;m nghi&amp;ecirc;n cứu v&amp;agrave; c&amp;aacute;c Cơ sở đ&amp;agrave;o tạo;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;C&amp;oacute; thể tiếp tục học tiếp l&amp;ecirc;n tr&amp;igrave;nh độ Sau đại học ở trong nước v&amp;agrave; nước ngo&amp;agrave;i.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Sau khi tốt nghiệp sinh vi&amp;ecirc;n c&amp;oacute; thể l&amp;agrave;m việc tại c&amp;aacute;c vị tr&amp;iacute; cụ thể:&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;aacute;c Cục, Vụ: Cục C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin, Cục Viễn th&amp;ocirc;ng, Cục quản l&amp;yacute; ph&amp;aacute;t thanh truyền h&amp;igrave;nh v&amp;agrave; th&amp;ocirc;ng tin điện tử, Cục Ứng dụng c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin, Cục C&amp;ocirc;ng nghệ tin học nghiệp vụ, Cục thương mại điện tử v&amp;agrave; c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin, Cục C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin v&amp;agrave; thống k&amp;ecirc; hải quan, Vụ C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin, Vụ Bưu ch&amp;iacute;nh&amp;hellip;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;aacute;c Viện, Trung t&amp;acirc;m: Viện C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin, Viện C&amp;ocirc;ng nghệ phần mềm v&amp;agrave; nội dung số Việt Nam, Viện Nghi&amp;ecirc;n cứu điện tử - tin học &amp;ndash; tự động h&amp;oacute;a, Viện Chiến lược th&amp;ocirc;ng tin v&amp;agrave; truyền th&amp;ocirc;ng, Trung t&amp;acirc;m Internet Việt Nam, Trung t&amp;acirc;m Ứng cứu khẩn cấp m&amp;aacute;y t&amp;iacute;nh Việt Nam, c&amp;aacute;c Trung t&amp;acirc;m Th&amp;ocirc;ng tin trực thuộc c&amp;aacute;c Bộ, Tổng cục&amp;hellip;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;aacute;c Tập đo&amp;agrave;n, Tổng c&amp;ocirc;ng ty: Tập đo&amp;agrave;n Bưu ch&amp;iacute;nh Viễn th&amp;ocirc;ng Việt Nam (VNPT), Tập đo&amp;agrave;n Viễn th&amp;ocirc;ng qu&amp;acirc;n đội (VIETEL), Tổng c&amp;ocirc;ng ty Truyền th&amp;ocirc;ng đa phương tiện Việt Nam (VTC), Tổng c&amp;ocirc;ng ty Viễn th&amp;ocirc;ng to&amp;agrave;n cầu (GTEL), Tập đo&amp;agrave;n FPT v&amp;agrave; c&amp;aacute;c c&amp;ocirc;ng ty, đơn vị trực thuộc: C&amp;ocirc;ng ty Điện to&amp;aacute;n v&amp;agrave; truyền số liệu, C&amp;ocirc;ng ty Th&amp;ocirc;ng tin đi&amp;ecirc;n tử h&amp;agrave;ng hải Việt Nam, C&amp;aacute;c C&amp;ocirc;ng ty Viễn th&amp;ocirc;ng, li&amp;ecirc;n tỉnh, quốc tế, c&amp;aacute;c C&amp;ocirc;ng ty hoạt động trong lĩnh vực viễn th&amp;ocirc;ng, c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin &amp;hellip;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;C&amp;aacute;c ph&amp;ograve;ng chức năng: C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin, Bưu ch&amp;iacute;nh viễn th&amp;ocirc;ng, Quản l&amp;yacute; c&amp;ocirc;ng nghệ, Khoa học c&amp;ocirc;ng nghệ, Hệ thống quản trị, An ninh mạng &amp;hellip; trực thuộc c&amp;aacute;c Sở như: Sở Th&amp;ocirc;ng tin v&amp;agrave; truyền th&amp;ocirc;ng, Bưu điện, Sở Khoa học v&amp;agrave; c&amp;ocirc;ng nghệ&amp;hellip; ở c&amp;aacute;c tỉnh, th&amp;agrave;nh phố.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><em>Chuy&ecirc;n ng&agrave;nh C&ocirc;ng nghệ phần mềm:&nbsp;</em></div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;- Thu thập, ph&acirc;n t&iacute;ch t&igrave;m hiểu v&agrave; tổng hợp c&aacute;c y&ecirc;u cầu từ đối tượng sử dụng sản phẩm phần mềm để phục vụ c&ocirc;ng t&aacute;c thiết kế</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;- Thiết kế, triển khai thực hiện v&agrave; quản l&yacute; c&aacute;c dự &aacute;n phần mềm c&oacute; quy m&ocirc; vừa v&agrave; nhỏ, đ&aacute;p ứng c&aacute;c y&ecirc;u cầu kỹ thuật đặt ta trong điều kiện thực tế</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;- C&aacute;c kỹ năng về đ&aacute;nh gi&aacute; chi ph&iacute;, đảm bảo chất lượng phần mềm</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;- C&aacute;c kỹ năng về kiểm thử, bảo tr&igrave; v&agrave; x&acirc;y dựng t&agrave;i liệu kỹ thuật, t&agrave;i liệu hướng dẫn sử dụng hệ thống hiệu quả v&agrave; dễ d&ugrave;ng.</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;- &Aacute;p dụng tri thức Khoa học m&aacute;y t&iacute;nh, quản l&yacute; dự &aacute;n để nhận biết, ph&acirc;n t&iacute;ch v&agrave; giải quyết s&aacute;ng tạo hiệu quả những vấn đề kỹ thuật trong lĩnh vực x&acirc;y dựng v&agrave; ph&aacute;t triển phần mềm m&aacute;y t&iacute;nh.</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><em>Chuy&ecirc;n ng&agrave;nh Kỹ thuật m&aacute;y t&iacute;nh:&nbsp;</em></div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;- Thiết kế, x&acirc;y dựng, c&agrave;i đặt v&agrave; bảo tr&igrave; cả phần cứng v&agrave; phần mềm của hệ thống hiện tử số bao gồm hệ thống truyền th&ocirc;ng, m&aacute;y t&iacute;nh,..</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;-&nbsp; Thiết kế, x&acirc;y dựng v&agrave; ứng dụng c&aacute;c hệ thống nh&uacute;ng</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">Chuy&ecirc;n ng&agrave;nh Hệ thống th&ocirc;ng tin</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;- X&aacute;c định v&agrave; ph&acirc;n t&iacute;ch y&ecirc;u cầu đối với c&aacute;c vấn đề cụ thể, l&ecirc;n kế hoạch v&agrave; t&igrave;m giải ph&aacute;p cho vấn đề</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;-&nbsp; Đ&aacute;nh gi&aacute; v&agrave; thử nghiệm giải ph&aacute;p</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><em>Chuy&ecirc;n ng&agrave;nh Khoa học m&aacute;y t&iacute;nh</em></div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;- C&oacute; khả năng vận dụng c&aacute;c c&ocirc;ng cụ trong việc đặc tả, ph&acirc;n t&iacute;ch, x&acirc;y dựng, triển khai, bảo tr&igrave; c&aacute;c hệ thống dựa tr&ecirc;n m&aacute;y t&iacute;nh.</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><em>Chuy&ecirc;n ng&agrave;nh Mạng m&aacute;y t&iacute;nh v&agrave; truyền th&ocirc;ng: </em>Ph&acirc;n t&iacute;ch, thiết kế, c&agrave;i đặt, bảo tr&igrave; cũng như quản l&yacute; c&aacute;c hệ thống mạng truyền th&ocirc;ng m&aacute;y t&iacute;nh.</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><em>Chuy&ecirc;n ng&agrave;nh An ninh th&ocirc;ng tin mạng:&nbsp;</em></div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;-&nbsp; Quản trị bảo mật mạng m&aacute;y t&iacute;nh v&agrave; cơ sở dữ liệu</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;-&nbsp; Ph&acirc;n t&iacute;ch, tư vấn, thiết kế hệ thống th&ocirc;ng tin đảm bảo an to&agrave;n</div>\r\n	</li>\r\n	<li>\r\n	<div style=\"text-align: justify;\">&nbsp;- Kiểm tra, đ&aacute;nh gi&aacute; an to&agrave;n th&ocirc;ng tin cho mạng v&agrave; hệ th&ocirc;ng</div>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>​</strong></p>\r\n', 22, '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3725.2900372544773!2d105.7849323143775!3d20.981007794783928!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ade1a3c1bb5d%3A0x5672057fb1892ed3!2zSOG7jWMgVmnhu4duIEPDtG5nIG5naOG7hyBCxrB1IENow61uaCBWaeG7hW4gVGjDtG5n!5e0!3m2!1svi!2s!4v1594633172791!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>', 'bcvt.jpg', 4, '2020-06-27 13:10:56', '2020-07-12 16:30:24');
INSERT INTO `tbl_truongdaihoc` (`id_truong`, `tentruong`, `mota`, `gioithieu`, `chuyennganhdaotao`, `diemchuan`, `diachi`, `anh`, `manguoidung`, `ngayviet`, `ngaycapnhat`) VALUES
(11, 'Trường Đại học Thủy Lợi', 'Trường Đại học Thủy Lợi', '&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh:&lt;/strong&gt; C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Tổng quan giới thiệu trường + Cơ sở hạ tầng:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;+ Trường Đại học Thủy lợi&amp;nbsp;(tiếng Anh:&amp;nbsp;&lt;em&gt;ThuyLoi University&lt;/em&gt;) l&amp;agrave; trường đại học&amp;nbsp;số 1 trong việc đ&amp;agrave;o tạo nguồn nh&amp;acirc;n lực chất lượng cao, nghi&amp;ecirc;n cứu khoa học, ph&amp;aacute;t triển v&amp;agrave; chuyển giao c&amp;ocirc;ng nghệ ti&amp;ecirc;n tiến trong c&amp;aacute;c ng&amp;agrave;nh khoa học, kỹ thuật, kinh tế v&amp;agrave; quản l&amp;yacute;, đặc biệt trong lĩnh vực thuỷ lợi, m&amp;ocirc;i trường, ph&amp;ograve;ng chống v&amp;agrave; giảm nhẹ thi&amp;ecirc;n tai.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;+ Cho đến nay, về cơ sở vật chất nh&amp;agrave; trường hiện c&amp;oacute;: 9,78 ha khu&amp;ocirc;n vi&amp;ecirc;n; 6700 m&amp;sup2; v&amp;agrave; cơ sở mới tại Hưng Y&amp;ecirc;n; 27 cơ sở th&amp;iacute; nghiệm thực h&amp;agrave;nh với 5713 m&amp;sup2;; 8900 m&amp;sup2; k&amp;yacute; t&amp;uacute;c x&amp;aacute;; 1089 m&amp;sup2; thư viện; 8222 m&amp;sup2; gi&amp;aacute;o dục thể chất. C&amp;aacute;c hạng mục c&amp;ocirc;ng tr&amp;igrave;nh lu&amp;ocirc;n được n&amp;acirc;ng cấp v&amp;agrave; x&amp;acirc;y mới theo quy hoạch tổng thể của trường.&lt;/div&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://upload.wikimedia.org/wikipedia/commons/8/89/Nh%C3%A0_A1%2C_%C4%90%E1%BA%A1i_h%E1%BB%8Dc_Thu%E1%BB%B7_L%E1%BB%A3i%2C_H%C3%A0_N%E1%BB%99i_001.JPG&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://upload.wikimedia.org/wikipedia/commons/8/89/Nh%C3%A0_A1%2C_%C4%90%E1%BA%A1i_h%E1%BB%8Dc_Thu%E1%BB%B7_L%E1%BB%A3i%2C_H%C3%A0_N%E1%BB%99i_001.JPG&quot; style=&quot;height:448px; width:597px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Địa điểm: &lt;/strong&gt;Nh&amp;agrave; C1, Đại học Thủy Lợi, 175 T&amp;acirc;y Sơn, Đống Đa, H&amp;agrave; Nội.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo:&lt;/strong&gt; 4,5 năm.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;Mục ti&amp;ecirc;u đ&amp;agrave;o tạo:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Cung cấp c&amp;aacute;c nền tảng về c&amp;aacute;c nguy&amp;ecirc;n l&amp;iacute; v&amp;agrave; phương ph&amp;aacute;p của khoa học v&amp;agrave; kỹ thuật m&amp;aacute;y t&amp;iacute;nh theo diện rộng chuẩn bị cho sinh vi&amp;ecirc;n ra trường l&amp;agrave;m việc ở c&amp;aacute;c vị tr&amp;iacute; của C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin hay tiếp tục học l&amp;ecirc;n c&amp;aacute;c bậc cao hơn&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Cung cấp c&amp;aacute;c kỹ năng cần thiết trong thiết kế v&amp;agrave; thực hiện c&amp;aacute;c hệ thống m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; c&amp;aacute;c ứng dụng, tiến tới giải quyết được c&amp;aacute;c b&amp;agrave;i to&amp;aacute;n mở v&amp;agrave; biết c&amp;aacute;ch tư duy s&amp;aacute;ng tạo.&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Cung cấp cơ hội l&amp;agrave;m việc theo nh&amp;oacute;m c&amp;oacute; hiệu quả, giao tiếp trực tiếp hay bằng văn bản, biết nguy&amp;ecirc;n tắc ứng xử v&amp;agrave; nhận thức x&amp;atilde; hội trong việc học tập trau dồi nghề nghiệp v&amp;agrave; thăng tiến ở c&amp;aacute;c vị tr&amp;iacute; l&amp;atilde;nh đạo.&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;Cung cấp cho sinh vi&amp;ecirc;n cơ hội t&amp;igrave;m hiểu s&amp;acirc;u về một lĩnh vực chuy&amp;ecirc;n ng&amp;agrave;nh th&amp;ocirc;ng qua việc lựa chọn c&amp;aacute;c m&amp;ocirc;n học li&amp;ecirc;n quan hoặc tiếp nhận c&amp;aacute;c kiến thức rộng về To&amp;aacute;n v&amp;agrave; Khoa học c&amp;ocirc;ng nghệ th&amp;ocirc;ng qua việc tự chọn mềm dẻo c&amp;aacute;c m&amp;ocirc;n học.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://tttd.ex-cdn.com/files/f3/files/ngocanh/nam_2018/thang_5/ngay_6/31946842_387812705029253_5814495204559814656_n.jpg&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://tttd.ex-cdn.com/files/f3/files/ngocanh/nam_2018/thang_5/ngay_6/31946842_387812705029253_5814495204559814656_n.jpg&quot; style=&quot;height:420px; width:560px&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Chất lượng giảng vi&amp;ecirc;n:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;Khoa c&amp;oacute; 60 giảng vi&amp;ecirc;n trực tiếp tham gia giảng dạy, trong đ&amp;oacute; c&amp;oacute; 20 Tiến sỹ v&amp;agrave; 12 nghi&amp;ecirc;n cứu sinh với&amp;nbsp;năng lực c&amp;ocirc;ng bố quốc tế tốt.&amp;nbsp;Phần lớn c&amp;aacute;c Tiến sỹ của Khoa đều tu nghiệp tại nước ngo&amp;agrave;i c&amp;oacute; nền khoa học ti&amp;ecirc;n tiến về CNTT, c&amp;aacute;c giảng vi&amp;ecirc;n c&amp;ograve;n lại của Khoa đều c&amp;oacute; tr&amp;igrave;nh độ Thạc sỹ.&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Học ph&amp;iacute;: &lt;/strong&gt;10 triệu/năm.&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m:&lt;/strong&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ X&amp;acirc;y dựng, ph&amp;aacute;t triển c&amp;aacute;c hệ thống c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin ứng dụng trong thực tế tổ chức v&amp;agrave; doanh nghiệp.&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ Nghi&amp;ecirc;n cứu khoa học thuộc c&amp;aacute;c lĩnh vực về c&amp;ocirc;ng nghệ phần mềm, c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin&amp;hellip;ở c&amp;aacute;c viện nghi&amp;ecirc;n cứu, c&amp;aacute;c trung t&amp;acirc;m v&amp;agrave; cơ quan nghi&amp;ecirc;n cứu của c&amp;aacute;c Bộ, ng&amp;agrave;nh, c&amp;aacute;c trường Đại học v&amp;agrave; Cao đẳng;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ L&amp;agrave;m chuy&amp;ecirc;n vi&amp;ecirc;n ở bộ phận c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin hoặc cần ứng dụng c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin của tất cả c&amp;aacute;c đơn vị c&amp;oacute; nhu cầu (h&amp;agrave;nh ch&amp;iacute;nh sự nghiệp, ng&amp;acirc;n h&amp;agrave;ng, viễn th&amp;ocirc;ng, h&amp;agrave;ng kh&amp;ocirc;ng, x&amp;acirc;y dựng, thủy lợi&amp;hellip;);&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ L&amp;agrave;m việc trong c&amp;aacute;c c&amp;ocirc;ng ty sản xuất, gia c&amp;ocirc;ng phần mềm trong nước cũng như nước ngo&amp;agrave;i;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ L&amp;agrave;m việc tại c&amp;aacute;c c&amp;ocirc;ng ty tư vấn về đề xuất giải ph&amp;aacute;p, x&amp;acirc;y dựng v&amp;agrave; bảo tr&amp;igrave; c&amp;aacute;c hệ thống th&amp;ocirc;ng tin, hệ thống mạng v&amp;agrave; truyền th&amp;ocirc;ng&amp;hellip;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 36pt;&quot;&gt;+ Giảng dạy c&amp;aacute;c m&amp;ocirc;n li&amp;ecirc;n quan đến c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin tại c&amp;aacute;c trường đại học, cao đẳng, trung học chuy&amp;ecirc;n nghiệp, dạy nghề v&amp;agrave; c&amp;aacute;c trường phổ th&amp;ocirc;ng;&lt;/div&gt;\r\n', '<ul>\r\n	<li><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></li>\r\n</ul>\r\n\r\n<div style=\"margin-left: 40px;\">+ Kỹ thuật phần mềm</div>\r\n\r\n<div style=\"margin-left: 40px;\">+ Hệ thống th&ocirc;ng tin</div>\r\n\r\n<div style=\"margin-left: 40px;\">+ Khoa học m&aacute;y t&iacute;nh</div>\r\n\r\n<div style=\"margin-left: 40px;\">+ Kỹ thuật m&aacute;y t&iacute;nh v&agrave; Mạng</div>\r\n\r\n<ul>\r\n	<li>\r\n	<div><strong>Nội dung đ&agrave;o tạo:</strong></div>\r\n	</li>\r\n</ul>\r\n\r\n<div>Đ&agrave;o tạo kỹ sư c&oacute; những kiến thức nền tảng trong lĩnh vực C&ocirc;ng nghệ th&ocirc;ng tin, cập nhật c&aacute;c vấn đề mới v&agrave; hiện đại li&ecirc;n quan đến nghi&ecirc;n cứu ph&aacute;t triển c&aacute;c hệ thống th&ocirc;ng tin, gia c&ocirc;ng hay ứng dụng hệ thống phần mềm; kiến thức về mạng m&aacute;y t&iacute;nh v&agrave; truyền th&ocirc;ng.</div>\r\n', 21, '<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3724.631894911249!2d105.82253501437789!3d21.007387793884718!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ac8109765ba5%3A0xd84740ece05680ee!2zVHLGsOG7nW5nIMSQ4bqhaSBI4buNYyBUaOG7p3kgTOG7o2k!5e0!3m2!1svi!2s!4v1594633108747!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>', 'tl.jpg', 4, '2020-06-27 13:19:48', '2020-07-12 16:04:33'),
(12, 'Trường Đại học Bách khoa Hà Nội', 'Trường Đại học B&aacute;ch khoa H&agrave; Nội', '&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh:&lt;/strong&gt; Viện c&amp;ocirc;ng ngh&amp;ecirc; th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng &amp;ndash; Đại học B&amp;aacute;ch Khoa H&amp;agrave; Nội:&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Tổng quan giới thiệu:&lt;/strong&gt; Học viện C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin B&amp;aacute;ch Khoa (BKACAD)&amp;nbsp;l&amp;agrave; chương tr&amp;igrave;nh hợp t&amp;aacute;c giữa Trường Đại học B&amp;aacute;ch Khoa H&amp;agrave; Nội v&amp;agrave; c&amp;aacute;c đối t&amp;aacute;c nước ngo&amp;agrave;i như Cisco Systems, Microsoft, VUE (Hoa Kỳ) v&amp;agrave; Genetic Computer School (Singapore) nhằm đ&amp;aacute;p ứng nhu cầu ng&amp;agrave;y c&amp;agrave;ng tăng trong việc đ&amp;agrave;o tạo v&amp;agrave; cung cấp nh&amp;acirc;n lực c&amp;oacute; tr&amp;igrave;nh độ chuy&amp;ecirc;n m&amp;ocirc;n đạt chuẩn Quốc tế.Trải qua 25 năm h&amp;igrave;nh th&amp;agrave;nh v&amp;agrave; ph&amp;aacute;t triển, Khoa CNTT trước đ&amp;acirc;y, cũng như Viện CNTT&amp;amp;TT đ&amp;atilde; c&amp;oacute; những bước ph&amp;aacute;t triển vượt bậc, xứng đ&amp;aacute;ng l&amp;agrave; ngọn cờ đầu trong đ&amp;agrave;o tạo, nghi&amp;ecirc;n cứu v&amp;agrave; chuyển giao c&amp;ocirc;ng nghệ trong lĩnh vực CNTT&amp;amp;TT tại Việt Nam&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo:&lt;/strong&gt;&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Đ&amp;agrave;o tạo Cử nh&amp;acirc;n &amp;ndash; Thạc sĩ: 6 năm&lt;/span&gt;&lt;/div&gt;\r\n\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Đ&amp;agrave;o tạo Cử nh&amp;acirc;n &amp;ndash; Thạc sĩ &amp;ndash; Tiến sĩ: 9 năm&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://img.giaoduc.net.vn/w700/Uploaded/2020/bpcgtqvp/2020_06_02/bk-9499.jpg&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://img.giaoduc.net.vn/w700/Uploaded/2020/bpcgtqvp/2020_06_02/bk-9499.jpg&quot; style=&quot;height:411px; width:700px&quot; /&gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Chất lượng giảng vi&amp;ecirc;n&lt;/strong&gt;&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt; text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Hiện nay Viện c&amp;oacute; lực lượng c&amp;aacute;n bộ đ&amp;ocirc;ng đảo về số lượng, v&amp;agrave; mạnh về chất lượng, trong đ&amp;oacute; c&amp;oacute; 83% giảng vi&amp;ecirc;n c&amp;oacute; học vị Tiến sỹ (~90% tốt nghiệp tại c&amp;aacute;c nước c&amp;ocirc;ng nghiệp ph&amp;aacute;t triển), v&amp;agrave; 28% c&amp;oacute; học h&amp;agrave;m Ph&amp;oacute; Gi&amp;aacute;o sư. Viện cũng hợp t&amp;aacute;c v&amp;agrave; gắn kết với đội ngũ đ&amp;ocirc;ng đảo c&amp;aacute;c c&amp;aacute;n bộ thỉnh giảng l&amp;agrave; c&amp;aacute;c chuy&amp;ecirc;n gia, nh&amp;agrave; khoa học uy t&amp;iacute;n trong v&amp;agrave; ngo&amp;agrave;i nước.&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Học ph&amp;iacute;:&lt;/strong&gt; Học ph&amp;iacute; tầm 20 triệu / năm&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Kiến thức, kỹ năng đạt được sau tốt nghiệp&lt;/strong&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;em&gt;Kiến thức:&lt;/em&gt;&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;C&amp;oacute; kiến thức khoa học cơ bản v&amp;agrave; c&amp;aacute;c kiến thức cốt l&amp;otilde;i về ng&amp;agrave;nh C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin bao gồm hệ thống m&amp;aacute;y t&amp;iacute;nh, giải thuật v&amp;agrave; lập tr&amp;igrave;nh, cơ sở dữ liệu, ph&amp;acirc;n t&amp;iacute;ch thiết kế v&amp;agrave; ph&amp;aacute;t triển phần mềm, quản l&amp;yacute; dự &amp;aacute;n CNTT.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kiến thức lập tr&amp;igrave;nh chuy&amp;ecirc;n s&amp;acirc;u trong c&amp;aacute;c m&amp;ocirc;i trường ph&amp;aacute;t triển ứng dụng kh&amp;aacute;c nhau.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Qui tr&amp;igrave;nh ph&amp;aacute;t triển phần mềm, đảm bảo chất lượng phần mềm.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kiến thức hệ thống mạng.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kiến thức c&amp;aacute;c hệ thống m&amp;aacute;y chủ dịch vụ.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Qui tr&amp;igrave;nh ph&amp;acirc;n t&amp;iacute;ch thiết kế &amp;amp; t&amp;iacute;ch hợp hệ thống.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;em&gt;Kỹ năng: &lt;/em&gt;Sinh vi&amp;ecirc;n được trang bị kỹ năng tổ chức, l&amp;atilde;nh đạo v&amp;agrave; l&amp;agrave;m việc nh&amp;oacute;m.&lt;/span&gt;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;em&gt;Ngoại ngữ:&lt;/em&gt; Sử dụng tiếng Anh hiệu quả trong giao tiếp v&amp;agrave; c&amp;ocirc;ng việc, đạt điểm TOEIC 500 điểm trở l&amp;ecirc;n.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&amp;nbsp;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m: &lt;/strong&gt;Sinh vi&amp;ecirc;n ra trường c&amp;oacute; cơ hội l&amp;agrave;m trong c&amp;aacute;c tập đo&amp;agrave;n c&amp;ocirc;ng nghệ c&amp;oacute; sức ảnh hướng to&amp;agrave;n cầu như: Google, Microsoft, Facebook, Amazon, Adobe&amp;hellip;, v&amp;agrave; cũng một con số tương tự đang l&amp;agrave;m việc tại cường quốc c&amp;ocirc;ng nghệ Nhật Bản, cũng như một cộng đồng kh&amp;ocirc;ng nhỏ đang l&amp;agrave;m việc tại c&amp;aacute;c tập đo&amp;agrave;n c&amp;ocirc;ng nghệ to&amp;agrave;n cầu trải khắp 5 ch&amp;acirc;u. Th&amp;ecirc;m v&amp;agrave;o đ&amp;oacute;, h&amp;agrave;ng năm, c&amp;aacute;c sinh vi&amp;ecirc;n giỏi của Viện c&amp;ograve;n nhận được nhiều học bổng theo đuổi con đường sự nghiệp học vấn tại c&amp;aacute;c nước ph&amp;aacute;t triển: Bắc Mỹ, Ch&amp;acirc;u &amp;Acirc;u, Nhật Bản, H&amp;agrave;n Quốc&amp;hellip;, v&amp;agrave; rất nhiều người trong số đ&amp;oacute; đ&amp;atilde; ở lại để trở th&amp;agrave;nh c&amp;aacute;c gi&amp;aacute;o sư, v&amp;agrave; nh&amp;agrave; khoa học đang l&amp;agrave;m việc tại c&amp;aacute;c trường đại học, v&amp;agrave; c&amp;aacute;c viện nghi&amp;ecirc;n cứu nổi tiếng tr&amp;ecirc;n Thế giới&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;&amp;nbsp;&lt;/div&gt;\r\n', '<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#000000\"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:36pt; text-align:justify\"><span style=\"color:#000000\">Chuy&ecirc;n gia Quản trị mạng v&agrave; Bảo mật hệ thống Quốc tế</span></p>\r\n\r\n<p style=\"margin-left:36pt; text-align:justify\"><span style=\"color:#000000\">Chuy&ecirc;n gia Lập tr&igrave;nh Quốc tế</span></p>\r\n\r\n<p style=\"margin-left:36pt; text-align:justify\"><span style=\"color:#000000\">Chuy&ecirc;n gia C&ocirc;ng nghệ th&ocirc;ng tin - Quản trị kinh doanh Quốc tế</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#000000\"><strong>Nội dung đ&agrave;o tạo:</strong> &Aacute;p dụng m&ocirc; h&igrave;nh giảng dạy hiện đại, b&agrave;i giảng c&oacute; t&iacute;nh tương t&aacute;c cao. Gi&aacute;o tr&igrave;nh bản quyền Quốc tế, ph&aacute;t triển khả năng đọc hiểu tiếng Anh chuy&ecirc;n ng&agrave;nh. Hệ thống trực tuyến v&agrave; m&aacute;y chủ quốc tế hỗ trợ thi cử v&agrave; quản l&yacute; kết quả học tập. Sinh vi&ecirc;n học tập to&agrave;n thời gian trong c&aacute;c ph&ograve;ng Lab ti&ecirc;u chuẩn. Ph&aacute;t triển c&aacute;c kỹ năng mềm cho học vi&ecirc;n như khả năng diễn thuyết, l&agrave;m việc theo nh&oacute;m. Hỗ trợ th&ocirc;ng tin việc l&agrave;m cho sinh vi&ecirc;n th&ocirc;ng qua mạng lưới đối t&aacute;c. Sinh vi&ecirc;n đựoc học 100% thực h&agrave;nh - thực tiễn hand-on-lab. Sinh vi&ecirc;n được Học viện hỗ trợ thực tập v&agrave; việc l&agrave;m</span></li>\r\n</ul>\r\n', 25, '                                    <iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3724.6961996206833!2d105.84315191437786!3d21.00481169397252!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ac76ccab6dd7%3A0x55e92a5b07a97d03!2zVHLGsOG7nW5nIMSQ4bqhaSBo4buNYyBCw6FjaCBraG9hIEjDoCBO4buZaQ!5e0!3m2!1svi!2s!4v1594633428980!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>                                ', 'BKH.jpg', 4, '2020-06-27 13:37:34', '2020-07-14 21:08:52'),
(13, 'Trường Đại học Giao thông vận tải', 'Trường Đại học Giao th&ocirc;ng vận tải', '&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh:&lt;/strong&gt; Ng&amp;agrave;nh c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin &amp;ndash; Đại học Giao th&amp;ocirc;ng Vận tải.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Tổng quan giới thiệu: &lt;/strong&gt;ng&amp;agrave;y 21/11/2003 Trường Đại học Giao th&amp;ocirc;ng ra quyết định th&amp;agrave;nh lập Khoa C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin do Ph&amp;oacute; Gi&amp;aacute;o sư, Tiến sĩ Phạm Văn Ất l&amp;agrave;m Trưởng Khoa với cơ cấu 3&amp;nbsp;bộ m&amp;ocirc;n: Khoa học m&amp;aacute;y t&amp;iacute;nh C&amp;ocirc;ng nghệ phần mềm, Mạng v&amp;agrave; c&amp;aacute;c Hệ thống th&amp;ocirc;ng&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo:&lt;/strong&gt; &amp;nbsp;5 năm&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;a href=&quot;https://tuyensinhcunhan.com/wp-content/uploads/2018/09/dai-hoc-giao-thong-van-tai.jpg&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://tuyensinhcunhan.com/wp-content/uploads/2018/09/dai-hoc-giao-thong-van-tai.jpg&quot; style=&quot;height:400px; width:600px&quot; /&gt;&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Chất lượng giảng vi&amp;ecirc;n:&lt;/strong&gt; Từ ng&amp;agrave;y th&amp;agrave;nh lập đến nay đội ngũ gi&amp;aacute;o vi&amp;ecirc;n của Khoa ng&amp;agrave;y c&amp;agrave;ng ph&amp;aacute;t triển về số lượng v&amp;agrave; chất lượng, hiện nay Khoa c&amp;oacute; 34 gi&amp;aacute;o vi&amp;ecirc;n, trong đ&amp;oacute; c&amp;oacute; hai Ph&amp;oacute; Gi&amp;aacute;o sư, bốn Tiến sĩ, 22 thạc sĩ v&amp;agrave; t&amp;aacute;m nghi&amp;ecirc;n cứu sinh đang học tập nghi&amp;ecirc;n cứu ở trong v&amp;agrave; nước ngo&amp;agrave;i.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Học ph&amp;iacute;:&lt;/strong&gt; Học ph&amp;iacute; rơi v&amp;agrave;o tầm 10 &amp;ndash; 11 triệu/ 1 năm&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m:&amp;nbsp;&lt;/strong&gt;Khoa CNTT Đại học Giao th&amp;ocirc;ng vận tải l&amp;agrave; đối t&amp;aacute;c chiến lược với nhiều doanh nghiệp cntT Lớn như FPT, Samsung, Viettel, ICT,CMC,.. C&amp;aacute;c đối t&amp;aacute;c kh&amp;ocirc;ng ngừng đầu tư phối hợp tổ chức nhiều hội thảo giới thiệu nghề v&amp;agrave; tạo nhiều cơ hội việc l&amp;agrave;m cho sinh vi&amp;ecirc;n ra trường v&amp;agrave; nhiều cơ hội thực tập cho sinh vi&amp;ecirc;n học trong trường&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;div style=&quot;margin-left: 80px; text-align: justify;&quot;&gt;&amp;nbsp;&lt;/div&gt;\r\n', '<ul>\r\n	<li>\r\n	<div><span style=\"color:#000000\"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></span></div>\r\n	</li>\r\n</ul>\r\n\r\n<div style=\"margin-left: 36pt;\"><span style=\"color:#000000\">Chuy&ecirc;n ng&agrave;nh C&ocirc;ng nghệ phần mềm</span></div>\r\n\r\n<div style=\"margin-left: 36pt;\"><span style=\"color:#000000\">Chuy&ecirc;n ng&agrave;nh Hệ thống th&ocirc;ng tin</span></div>\r\n\r\n<div style=\"margin-left: 36pt;\"><span style=\"color:#000000\">Chuy&ecirc;n ng&agrave;nh Khoa học m&aacute;y t&iacute;nh</span></div>\r\n\r\n<div style=\"margin-left: 36pt;\"><span style=\"color:#000000\">Chuy&ecirc;n ng&agrave;nh Mạng m&aacute;y t&iacute;nh v&agrave; truyền th&ocirc;ng</span></div>\r\n\r\n<ul>\r\n	<li>\r\n	<div><span style=\"color:#000000\"><strong>&nbsp;Nội dung đ&agrave;o tạo: </strong>&nbsp;Chương tr&igrave;nh đ&agrave;o tạo ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin nhằm tạo ra c&aacute;c kỹ sư c&oacute; năng lực chuy&ecirc;n m&ocirc;n, phẩm chất ch&iacute;nh trị, đạo đức v&agrave; sức khoẻ tốt, c&oacute; tri thức v&agrave; kỹ năng về việc ph&acirc;n t&iacute;ch, thiết kế, lập tr&igrave;nh, đảm bảo kỹ thuật để x&acirc;y dựng c&aacute;c phần mềm v&agrave; c&aacute;c hệ thống th&ocirc;ng tin đ&aacute;p ứng nhu cầu ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin trong sự nghiệp x&acirc;y dựng ph&aacute;t triển của x&atilde; hội.&nbsp;</span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"color:#000000\">Chương tr&igrave;nh đ&agrave;o tạo được x&acirc;y dựng tr&ecirc;n cơ sở nghi&ecirc;n cứu &aacute;p dụng chương tr&igrave;nh khung của Bộ Gi&aacute;o dục - Đ&agrave;o tạo v&agrave; c&aacute;c chương tr&igrave;nh giảng dạy bậc đại học của c&aacute;c nước ti&ecirc;n tiến</span></div>\r\n	</li>\r\n</ul>\r\n', 23, '                                                                      <iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3724.095811992822!2d105.80238931437825!3d21.028851993152312!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ab421c77a26b%3A0x7da9b687aab8b3bf!2zVHLGsOG7nW5nIMSQ4bqhaSBI4buNYyBHaWFvIFRow7RuZyBW4bqtbiBU4bqjaQ!5e0!3m2!1svi!2s!4v1594632940728!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>                                                              ', '2.jpg', 4, '2020-06-27 13:41:49', '2020-07-14 21:06:53'),
(14, 'Trường Đại học Mỏ Địa chất Hà Nội', 'Trường Đại học Mỏ Địa chất H&agrave; Nội', '&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh: &lt;/strong&gt;Ng&amp;agrave;nh c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin Đại học Mỏ địa chất&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo:&lt;/strong&gt;&amp;nbsp;4&amp;nbsp;năm&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Đối tượng tuyển sinh:&lt;/strong&gt; Theo quy định của Bộ Gi&amp;aacute;o dục v&amp;agrave; Đ&amp;agrave;o tạo&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Mục ti&amp;ecirc;u đ&amp;agrave;o tạo:&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n	&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;em&gt;Về kiến thức:&lt;/em&gt;&amp;nbsp;Ho&amp;agrave;n th&amp;agrave;nh chương tr&amp;igrave;nh đ&amp;agrave;o tạo ng&amp;agrave;nh C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin được thiết kế theo chương tr&amp;igrave;nh khung của Bộ Gi&amp;aacute;o dục v&amp;agrave; Đ&amp;agrave;o tạo, bao gồm: Kiến thức gi&amp;aacute;o dục đại cương, kiến thức cơ sở ng&amp;agrave;nh, kiến thức chuy&amp;ecirc;n ng&amp;agrave;nh:&amp;nbsp;chuy&amp;ecirc;n ng&amp;agrave;nh C&amp;ocirc;ng nghệ Phần mềm, chuy&amp;ecirc;n ng&amp;agrave;nh Mạng m&amp;aacute;y t&amp;iacute;nh, chuy&amp;ecirc;n ng&amp;agrave;nh Khoa học m&amp;aacute;y t&amp;iacute;nh, chuy&amp;ecirc;n ng&amp;agrave;nh Tin học Kinh tế:&lt;/span&gt;&lt;/div&gt;\r\n\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;em&gt;Về kỹ năng&lt;/em&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;C&amp;aacute;c kỹ năng được x&amp;acirc;y dựng dựa theo chuẩn đầu ra của chương tr&amp;igrave;nh đ&amp;agrave;o tạo được x&amp;aacute;c định theo ABET như sau:&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ol&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Khả năng &amp;aacute;p dụng c&amp;aacute;c kiến thức to&amp;aacute;n học, khoa học, v&amp;agrave; kỹ thuật v&amp;agrave;o c&amp;aacute;c vấn đề thuộc lĩnh vực C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin v&amp;agrave; Truyền th&amp;ocirc;ng (ICT);&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Khả năng ph&amp;acirc;n t&amp;iacute;ch, thiết kế, x&amp;acirc;y dựng v&amp;agrave; vận h&amp;agrave;nh c&amp;aacute;c hệ thống th&amp;ocirc;ng tin;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Khả năng xử l&amp;yacute; c&amp;aacute;c vấn đề li&amp;ecirc;n quan đến phần cứng, phần mềm m&amp;aacute;y t&amp;iacute;nh của hệ thống ứng dụng CNTT trong c&amp;aacute;c cơ quan, tổ chức, doanh nghiệp, tập đo&amp;agrave;n;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Khả năng ph&amp;acirc;n t&amp;iacute;ch, thiết kế, x&amp;acirc;y dựng v&amp;agrave; vận h&amp;agrave;nh c&amp;aacute;c hệ thống mạng;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Khả năng nghi&amp;ecirc;n cứu khoa học, giảng dạy C&amp;ocirc;ng nghệ Th&amp;ocirc;ng tin ở c&amp;aacute;c bậc đại học, cao đẳng, trung học chuy&amp;ecirc;n nghiệp v&amp;agrave; dạy nghề;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Khả năng hoạt động hiệu quả trong c&amp;aacute;c nh&amp;oacute;m để ho&amp;agrave;n th&amp;agrave;nh một mục đ&amp;iacute;ch chung;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Khả năng nhận diện, diễn đạt v&amp;agrave; giải quyết c&amp;aacute;c vấn đề kỹ thuật trong lĩnh vực ICT;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;C&amp;oacute; hiểu biết về tr&amp;aacute;ch nhiệm đạo đức v&amp;agrave; nghề nghiệp;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;C&amp;oacute; khả năng giao tiếp hiệu quả;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Được trang bị kiến thức đủ rộng để hiểu r&amp;otilde; t&amp;aacute;c động của c&amp;aacute;c giải ph&amp;aacute;p kỹ thuật trong bối cảnh kinh tế, m&amp;ocirc;i trường v&amp;agrave; x&amp;atilde; hội to&amp;agrave;n cầu;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Nhận thức về sự cần thiết v&amp;agrave; c&amp;oacute; khả năng học trọn đời;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;C&amp;oacute; kiến thức về c&amp;aacute;c vấn đề đương đại;&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;C&amp;oacute; khả năng sử dụng c&amp;aacute;c phương ph&amp;aacute;p, kỹ năng v&amp;agrave; c&amp;ocirc;ng cụ kỹ thuật hiện đại cần thiết cho thực h&amp;agrave;nh kỹ thuật, đặc biệt l&amp;agrave; trong c&amp;ocirc;ng nghiệp 4.0.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Học ph&amp;iacute;: &lt;/strong&gt;Học ph&amp;iacute; rơi v&amp;agrave;o kho&amp;agrave;ng 10 -11 triệu một năm&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Chuẩn đầu ra cho sinh vi&amp;ecirc;n:&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;Chuẩn đầu ra ngoại ngữ v&amp;agrave; tin học: Ph&amp;ugrave; hợp với y&amp;ecirc;u cầu của Th&amp;ocirc;ng tư số 01/2014/TT-BGDĐT về khung năng lực ngoại ngữ 6 bậc d&amp;ugrave;ng cho Việt Nam v&amp;agrave; Th&amp;ocirc;ng tư số 03/2014/TT-BTTTT của Bộ Th&amp;ocirc;ng tin v&amp;agrave; truyền th&amp;ocirc;ng, cụ thể khi sv tốt nghiệp phải đạt tr&amp;igrave;nh độ bậc 3 khung năng lực ngoại ngữ VN v&amp;agrave; chuẩn kỹ năng sử dụng CNTT cơ bản.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;T&amp;iacute;nh chuy&amp;ecirc;n nghiệp v&amp;agrave; phẩm chất c&amp;aacute; nh&amp;acirc;n: c&amp;oacute; t&amp;iacute;nh năng động, s&amp;aacute;ng tạo, nghi&amp;ecirc;m t&amp;uacute;c v&amp;agrave; c&amp;oacute; tr&amp;aacute;ch nhiệm trong c&amp;ocirc;ng việc; c&amp;oacute; khả năng lập luận, ph&amp;acirc;n t&amp;iacute;ch v&amp;agrave; giải quyết vấn đề; c&amp;oacute; khả năng tự bồi dưỡng, nắm bắt được c&amp;aacute;c tiến bộ khoa học kỹ thuật v&amp;agrave; &amp;yacute; thức học suốt đời; c&amp;oacute; khả năng tự học, tự nghi&amp;ecirc;n cứu, l&amp;agrave;m việc độc lập v&amp;agrave; khả năng chịu &amp;aacute;p lực trong c&amp;ocirc;ng việc;&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;L&amp;agrave;m việc theo nh&amp;oacute;m: c&amp;oacute; kỹ năng l&amp;agrave;m việc hiệu quả theo nh&amp;oacute;m; kỹ năng giao tiếp hiệu quả th&amp;ocirc;ng qua viết, thuyết tr&amp;igrave;nh, thảo luận, sử dụng hiệu quả c&amp;aacute;c c&amp;ocirc;ng cụ v&amp;agrave; phương tiện hiện đại.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;Ngoại ngữ v&amp;agrave; Tin học: Ph&amp;ugrave; hợp với y&amp;ecirc;u cầu của Th&amp;ocirc;ng tư số 01/2014/TT-BGDĐT về khung năng lực ngoại ngữ 6 bậc d&amp;ugrave;ng cho Việt Nam v&amp;agrave; Th&amp;ocirc;ng tư số 03/2014/TT-BTTTT của Bộ Th&amp;ocirc;ng tin v&amp;agrave; truyền th&amp;ocirc;ng, cụ thể khi sv tốt nghiệp phải đạt tr&amp;igrave;nh độ bậc 3 khung năng lực ngoại ngữ VN v&amp;agrave; chuẩn kỹ năng sử dụng CNTT cơ bản.&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&amp;nbsp;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n	&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Cơ hội nghề nghiệp:&lt;/strong&gt; Khoa đ&amp;atilde; đa dạng h&amp;oacute;a c&amp;aacute;c quan hệ hợp t&amp;aacute;c đ&amp;agrave;o tạo, nghi&amp;ecirc;n cứu, v&amp;agrave; cung cấp nguồn nh&amp;acirc;n lực c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin cho c&amp;aacute;c tập đo&amp;agrave;n, c&amp;ocirc;ng ty v&amp;agrave; bộ, ban, ng&amp;agrave;nh như: FPT, Viettel, VNPT, SamSung, ESRI Việtnam, Luvina, YS Flora, Infiniq, Jobchoice, Tập đo&amp;agrave;n CN Than v&amp;agrave; Kho&amp;aacute;ng sản VN, Cục C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin Bộ T&amp;agrave;i nguy&amp;ecirc;n &amp;amp; M&amp;ocirc;i trường, Cục bản đồ Bộ Tổng tham mưu, Viện H&amp;agrave;n L&amp;acirc;m Khoa học v&amp;agrave; C&amp;ocirc;ng nghệ Việt Nam, Viện KH Địa chất v&amp;agrave; Kho&amp;aacute;ng sản, Viện Địa chất...&lt;/span&gt;&lt;/div&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n', '<ul>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"color:#000000\"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong> C&ocirc;ng nghệ phần mềm, Mạng m&aacute;y t&iacute;nh, Khoa học m&aacute;y t&iacute;nh, Hệ thống th&ocirc;ng tin, C&ocirc;ng nghệ th&ocirc;ng tin địa học, Tin học kinh tế.</span></div>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"color:#000000\"><strong>Nội dung đ&agrave;o tạo:</strong> Khoa đang tiếp tục cải tiến n&acirc;ng cao chất lượng đ&agrave;o tạo, thực hiện đ&agrave;o tạo theo y&ecirc;u cầu của x&atilde; hội v&agrave; doanh nghiệp, gắn chương tr&igrave;nh đ&agrave;o tạo với thực tiễn, nhằm đ&aacute;p ứng nhu cầu về nguồn nh&acirc;n lực c&ocirc;ng nghệ th&ocirc;ng tin chất lượng cao v&agrave; hội nhập quốc tế. B&ecirc;n cạnh đ&oacute;, Khoa c&ograve;n l&agrave; một trong những địa chỉ h&agrave;ng đầu trong cả nước về đ&agrave;o tạo sau đại học trong lĩnh vực Địa tin học, với mục ti&ecirc;u đ&agrave;o tạo nguồn nh&acirc;n lực c&oacute; chuy&ecirc;n m&ocirc;n s&acirc;u về ứng dụng c&aacute;c tiến bộ của c&ocirc;ng nghệ th&ocirc;ng tin trong lĩnh vực khoa học tr&aacute;i đất, mỏ v&agrave; m&ocirc;i trường. Trong thời gian tới, Khoa sẽ ch&uacute; trọng mở ng&agrave;nh đ&agrave;o tạo mới ở bậc đại học theo th&ocirc;ng lệ quốc tế, tăng cường kết hợp với doanh nghiệp trong đ&agrave;o tạo, tiếp tục mở đ&agrave;o tạo đại học ng&agrave;nh CNTT chất lượng cao, khoa học dữ liệu,&nbsp;sau đại học ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin v&agrave; Tiến sĩ ng&agrave;nh Địa tin học.</span></div>\r\n	</li>\r\n</ul>\r\n', 20, '                                                                                                                                         <iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3723.0239485296456!2d105.77170921437896!3d21.07170569168791!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3134552defbed8e9%3A0x1584f79c805eb017!2zVHLGsOG7nW5nIMSQ4bqhaSBo4buNYyBN4buPIC0gxJDhu4thIGNo4bqldA!5e0!3m2!1svi!2s!4v1594632882484!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>                                                                                                                               ', 'modc.jpg', 4, '2020-06-27 13:53:37', '2020-07-14 21:08:27');
INSERT INTO `tbl_truongdaihoc` (`id_truong`, `tentruong`, `mota`, `gioithieu`, `chuyennganhdaotao`, `diemchuan`, `diachi`, `anh`, `manguoidung`, `ngayviet`, `ngaycapnhat`) VALUES
(15, 'Trường Đại học Xây dựng', 'Trường Đại học X&acirc;y dựng', '&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh: &lt;/strong&gt;Ng&amp;agrave;nh c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin Đại học X&amp;acirc;y Dựng&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo: &lt;/strong&gt;5 năm&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Đối tượng tuyển sinh:&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align:justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Th&amp;iacute; sinh đ&amp;atilde; tốt nghiệp THPT (theo h&amp;igrave;nh thức gi&amp;aacute;o dục ch&amp;iacute;nh quy hoặc gi&amp;aacute;o dục thường xuy&amp;ecirc;n) hoặc đ&amp;atilde; tốt nghiệp trung cấp, sau đ&amp;acirc;y gọi chung l&amp;agrave; tốt nghiệp trung học; người tốt nghiệp trung cấp nhưng chưa c&amp;oacute; bằng tốt nghiệp THPT phải học v&amp;agrave; được c&amp;ocirc;ng nhận đ&amp;atilde; ho&amp;agrave;n th&amp;agrave;nh c&amp;aacute;c m&amp;ocirc;n văn h&amp;oacute;a THPT theo quy định.&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align:justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Th&amp;iacute; sinh C&amp;oacute; đủ sức khoẻ để học tập theo quy định hiện h&amp;agrave;nh. Đối với người khuyết tật được UBND cấp tỉnh c&amp;ocirc;ng nhận bị dị dạng, dị tật, suy giảm khả năng tự lực trong sinh hoạt v&amp;agrave; học tập do hậu quả của chất độc ho&amp;aacute; học l&amp;agrave; con đẻ của người hoạt động kh&amp;aacute;ng chiến bị nhiễm chất độc ho&amp;aacute; học: Hiệu trưởng xem x&amp;eacute;t, quyết định cho dự tuyển sinh v&amp;agrave;o c&amp;aacute;c ng&amp;agrave;nh học ph&amp;ugrave; hợp với t&amp;igrave;nh trạng sức khoẻ.&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align:justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Qu&amp;acirc;n nh&amp;acirc;n tại ngũ sắp hết hạn nghĩa vụ qu&amp;acirc;n sự theo quy định, nếu được Thủ trưởng từ cấp trung đo&amp;agrave;n trở l&amp;ecirc;n cho ph&amp;eacute;p, th&amp;igrave; được dự tuyển theo nguyện vọng c&amp;aacute; nh&amp;acirc;n, nếu tr&amp;uacute;ng tuyển phải nhập học ngay năm đ&amp;oacute;, kh&amp;ocirc;ng được bảo lưu sang năm học sau.&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align:justify;&quot;&gt;&amp;nbsp;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Giới thiệu về trường:&lt;/strong&gt; Khoa C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin của trường Đại học Điện lực được th&amp;agrave;nh lập ng&amp;agrave;y 02 th&amp;aacute;ng 5 năm 2002. Hiện nay, khoa đ&amp;agrave;o tạo 04 chuy&amp;ecirc;n ng&amp;agrave;nh (C&amp;ocirc;ng nghệ phần mềm, Hệ thống thương mại điện tử, quản trị v&amp;agrave; an ninh mạng, Tr&amp;iacute; tuệ nh&amp;acirc;n tạo &amp;amp; Thị gi&amp;aacute;c m&amp;aacute;y t&amp;iacute;nh) với c&amp;aacute;c bậc đ&amp;agrave;o tạo l&amp;agrave; đại học, cao học v&amp;agrave; tiến sĩ.&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Trải qua mười lăm năm hoạt động với phương ch&amp;acirc;m c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin phải gắn liền với ứng dụng trong thực tế&amp;nbsp;v&amp;agrave; lấy sinh vi&amp;ecirc;n l&amp;agrave;m trung t&amp;acirc;m của qu&amp;aacute; tr&amp;igrave;nh ph&amp;aacute;t&amp;nbsp;triển, Khoa đ&amp;atilde; đạt được những th&amp;agrave;nh tựu đ&amp;aacute;ng kh&amp;iacute;ch lệ về chất lượng đ&amp;agrave;o tạo, ph&amp;aacute;t triển ứng dụng, đề t&amp;agrave;i khoa học, b&amp;agrave;i b&amp;aacute;o khoa học, olympic tin học v&amp;agrave; kỹ năng mềm cho sinh vi&amp;ecirc;n.&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;text-align: justify;&quot;&gt;&amp;nbsp;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Học ph&amp;iacute;:&lt;/strong&gt; Học ph&amp;iacute; tầm kho&amp;aacute;ng 9 -10 triệu đồng mỗi năm&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Chuẩn đầu ra cho sinh vi&amp;ecirc;n:&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;em&gt;Về kiến thức&lt;/em&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;ol style=&quot;margin-left:40px&quot;&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;C&amp;oacute; kiến thức cơ bản về Tin học (Cơ sở dữ liệu, Lập tr&amp;igrave;nh, Kiến tr&amp;uacute;c m&amp;aacute;y t&amp;iacute;nh, Cấu tr&amp;uacute;c dữ liệu v&amp;agrave; giải thuật, Mạng m&amp;aacute;y t&amp;iacute;nh,...).&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Trang bị cho sinh vi&amp;ecirc;n những kiến thức cơ sở về Khoa học m&amp;aacute;y t&amp;iacute;nh, Lập tr&amp;igrave;nh m&amp;aacute;y t&amp;iacute;nh, Hệ thống m&amp;aacute;y t&amp;iacute;nh, C&amp;aacute;c ứng dụng quan trọng của c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin,...&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Trang bị cho sinh vi&amp;ecirc;n những kiến thức li&amp;ecirc;n quan đến nghi&amp;ecirc;n cứu ph&amp;aacute;t triển, gia c&amp;ocirc;ng hay ứng dụng hệ thống phần mềm; kiến thức về thiết kế, x&amp;acirc;y dựng, c&amp;agrave;i đặt, vận h&amp;agrave;nh v&amp;agrave; bảo tr&amp;igrave; c&amp;aacute;c th&amp;agrave;nh phần phần cứng, phần mềm của hệ thống m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; c&amp;aacute;c hệ thống thiết bị dựa tr&amp;ecirc;n m&amp;aacute;y t&amp;iacute;nh, kiến thức về mạng m&amp;aacute;y t&amp;iacute;nh v&amp;agrave; truyền th&amp;ocirc;ng&amp;hellip;;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;em&gt;Về kỹ năng&lt;/em&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;em&gt;Kỹ năng nghề nghiệp&lt;/em&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;ol style=&quot;margin-left:40px&quot;&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kỹ năng lập luận nghề nghiệp, ph&amp;aacute;t hiện v&amp;agrave; giải quyết vấn đề.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Khả năng lập luận tư duy v&amp;agrave; giải quyết vấn đề.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Khả năng nghi&amp;ecirc;n cứu v&amp;agrave; kh&amp;aacute;m ph&amp;aacute; kiến thức.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kỹ năng tư duy một c&amp;aacute;ch hệ thống.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Khả năng nhận biết v&amp;agrave; ph&amp;acirc;n t&amp;iacute;ch bối cảnh v&amp;agrave; ngoại cảnh t&amp;aacute;c động đến cơ sở l&amp;agrave;m việc, ng&amp;agrave;nh nghề.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kỹ năng nhận biết v&amp;agrave; ph&amp;acirc;n t&amp;iacute;ch t&amp;igrave;nh h&amp;igrave;nh trong v&amp;agrave; ngo&amp;agrave;i đơn vị l&amp;agrave;m việc (cơ quan, doanh nghiệp, tổ chức).&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kỹ năng vận dụng kiến thức v&amp;agrave;o thực tiễn.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Năng lực s&amp;aacute;ng tạo, ph&amp;aacute;t triển v&amp;agrave; dẫn dắt sự thay đổi trong nghề nghiệp.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;em&gt;Kỹ năng mềm&lt;/em&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;ol style=&quot;margin-left:40px&quot;&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kỹ năng tự chủ.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kỹ năng l&amp;agrave;m việc theo nh&amp;oacute;m.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kỹ năng quản l&amp;yacute; v&amp;agrave; l&amp;atilde;nh đạo.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kỹ năng giao tiếp.&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;span style=&quot;color:#000000&quot;&gt;Kỹ năng giao tiếp sử dụng ngoại ngữ&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;strong&gt;Cơ hội nghề nghiệp:&lt;/strong&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Sinh vi&amp;ecirc;n tốt nghiệp ng&amp;agrave;nh CNTT trường ĐHXD c&amp;oacute; thể đảm nhận tốt c&amp;aacute;c vị tr&amp;iacute; sau:&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ C&amp;aacute;n bộ kỹ thuật, quản l&amp;yacute; điều h&amp;agrave;nh, quản trị dự &amp;aacute;n trong lĩnh vực CNTT&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Chuy&amp;ecirc;n vi&amp;ecirc;n tin học trong c&amp;aacute;c cơ quan Nh&amp;agrave; nước, tổ chức doanh nghiệp&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Kỹ sư ph&amp;aacute;t triển phần mềm, chuy&amp;ecirc;n vi&amp;ecirc;n kiểm thử phần mềm tại c&amp;aacute;c c&amp;ocirc;ng ty giải ph&amp;aacute;p phần mềm&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Kỹ sư hệ thống th&amp;ocirc;ng tin, chuy&amp;ecirc;n vi&amp;ecirc;n quản trị hệ thống mạng, chuy&amp;ecirc;n gia an ninh mạng v&amp;agrave; bảo mật&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Lập tr&amp;igrave;nh vi&amp;ecirc;n ứng dụng di động, ph&amp;aacute;t triển game&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ C&amp;aacute;n bộ nghi&amp;ecirc;n cứu v&amp;agrave; giảng dạy về CNTT tại c&amp;aacute;c Viện, Trường v&amp;agrave; Trung t&amp;acirc;m nghi&amp;ecirc;n cứu&amp;hellip;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Kỹ sư nghi&amp;ecirc;n cứu v&amp;agrave; ph&amp;aacute;t triển c&amp;ocirc;ng nghệ cho c&amp;aacute;c doanh nghiệp ứng dụng CNTT&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Nhiều sinh vi&amp;ecirc;n tốt nghiệp từ&amp;nbsp; kh&amp;oacute;a 46 đến nay hiện đang nắm giữ những vị tr&amp;iacute; quan trọng như gi&amp;aacute;m đốc kỹ thuật, quản trị dự &amp;aacute;n, trưởng nh&amp;oacute;m, chuy&amp;ecirc;n vi&amp;ecirc;n cấp cao tại c&amp;aacute;c doanh nghiệp h&amp;agrave;ng đầu trong lĩnh vực C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin như: VNPT, Viettel, Samsung Electronics, C&amp;ocirc;ng ty phần mềm FTP Software,&amp;nbsp; C&amp;ocirc;ng ty hệ thống th&amp;ocirc;ng tin FPT Information System, Tập đo&amp;agrave;n CMC, C&amp;ocirc;ng ty phần mềm VietSoftware International, Tập đo&amp;agrave;n truyền th&amp;ocirc;ng đa phương tiện VTC Multimedia Corporation, Tập đo&amp;agrave;n truyền th&amp;ocirc;ng An Vi&amp;ecirc;n AVG Group, Ng&amp;acirc;n h&amp;agrave;ng SeaBank, Techcombank, Bảo Việt, H&amp;atilde;ng h&amp;agrave;ng kh&amp;ocirc;ng Vietnam Airline&amp;hellip;Một số sinh vi&amp;ecirc;n đang l&amp;agrave; c&amp;aacute;n bộ giảng dạy tại Khoa C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin của c&amp;aacute;c Trường Đại học v&amp;agrave; Cao đẳng như: Trường Đại học N&amp;ocirc;ng nghiệp, Trường Đại học X&amp;acirc;y dựng, Trường Cao Đẳng Kỹ thuật v&amp;agrave; C&amp;ocirc;ng nghệ H&amp;agrave; Nội, Học viện quản l&amp;yacute; gi&amp;aacute;o dục&amp;hellip;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div style=&quot;margin-left: 18pt;&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;Ngo&amp;agrave;i ra, sinh vi&amp;ecirc;n sau khi tốt nghiệp c&amp;ograve;n tham gia v&amp;agrave;o c&amp;aacute;c c&amp;ocirc;ng ty x&amp;acirc;y dựng, doanh nghiệp sản xuất, tổ chức t&amp;agrave;i ch&amp;iacute;nh, ng&amp;acirc;n h&amp;agrave;ng, bảo hiểm&amp;hellip; &amp;nbsp;với vai tr&amp;ograve; chuy&amp;ecirc;n vi&amp;ecirc;n C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin, quản trị hệ thống, chuy&amp;ecirc;n vi&amp;ecirc;n ph&amp;acirc;n t&amp;iacute;ch nghiệp vụ.&lt;/span&gt;&lt;/div&gt;\r\n', '<div><span style=\"color:#000000\"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+ C&ocirc;ng nghệ phần mềm</span></div>\r\n\r\n<div><span style=\"color:#000000\">&nbsp; &nbsp; &nbsp; &nbsp;+ Mạng v&agrave; hệ thống th&ocirc;ng tin</span></div>\r\n\r\n<div><span style=\"color:#000000\"><strong>Nội dung đ&agrave;o tạo:</strong></span></div>\r\n\r\n<div><span style=\"color:#000000\"><em>Chuy&ecirc;n ng&agrave;nh C&ocirc;ng nghệ phần mềm&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</em></span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+ Thu thập, ph&acirc;n t&iacute;ch v&agrave; tổng hợp y&ecirc;u cầu về phần mềm từ người sử dụng</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+ Thiết kế/ph&aacute;t triển phần mềm</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+ Triển khai thực hiện v&agrave; quản l&yacute; c&aacute;c dự &aacute;n ph&aacute;t triển phần mềm, đ&aacute;p ứng c&aacute;c y&ecirc;u cầu kỹ thuật đặt ra trong điều kiện thực tế</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+C&aacute;c kỹ thuật về đ&aacute;nh gi&aacute; chi ph&iacute;, đảm bảo chất lượng của phần mềm</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+C&aacute;c kỹ thuật kiểm thử, bảo tr&igrave; v&agrave; x&acirc;y dựng t&agrave;i liệu kỹ thuật, t&agrave;i liệu hướng dẫn sử dụng hệ thống</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+ &Aacute;p dụng tri thức Khoa học m&aacute;y t&iacute;nh, quản l&yacute; dự &aacute;n để nhận biết, ph&acirc;n t&iacute;ch v&agrave; giải quyết s&aacute;ng tạo v&agrave; hiệu quả những vấn đề kỹ thuật trong lĩnh vực x&acirc;y dựng v&agrave; ph&aacute;t triển phần mềm m&aacute;y t&iacute;nh</span></div>\r\n\r\n<div><span style=\"color:#000000\"><em>Chuy&ecirc;n ng&agrave;nh Mạng v&agrave; hệ thống th&ocirc;ng tin</em></span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+ Nghi&ecirc;n cứu, thi&ecirc;́t k&ecirc;́, tri&ecirc;̉n khai và quản trị các h&ecirc;̣ th&ocirc;́ng mạng</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+ Ph&aacute;t triển các ứng dụng đa phương ti&ecirc;̣n tr&ecirc;n mạng m&aacute;y t&iacute;nh, mạng di động và thi&ecirc;́t bị di đ&ocirc;̣ng, kết hợp với xu hướng c&ocirc;ng nghệ mới như điện to&aacute;n đ&aacute;m m&acirc;y</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+ Ph&acirc;n t&iacute;ch, thiết kế, x&acirc;y dựng v&agrave; điều h&agrave;nh hoạt động của c&aacute;c hệ thống th&ocirc;ng tin, thiết kế v&agrave; tri&ecirc;̉n khai đề &aacute;n CNTT</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+ Sử dụng c&ocirc;ng ngh&ecirc;̣ tri thức trong tổ chức kho dữ liệu, khai ph&aacute; dữ liệu</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">+ Đ&ecirc;̀ xu&acirc;́t v&agrave; triển khai các giải pháp sử dụng h&ecirc;̣ th&ocirc;́ng th&ocirc;ng tin hi&ecirc;̣u quả, đảm bảo an toàn th&ocirc;ng tin cho h&ecirc;̣ th&ocirc;́ng th&ocirc;ng tin và mạng máy tính</span></div>\r\n', 24.1, '                                                                                                                                         <iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3724.7334694906726!2d105.84113201437786!3d21.003318494023528!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ac773026b415%3A0x499b8b613889f78a!2zVHLGsOG7nW5nIMSQ4bqhaSBI4buNYyBYw6J5IEThu7FuZw!5e0!3m2!1svi!2s!4v1594632815370!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>                                                                                                ', 'dhxd.jpg', 4, '2020-06-27 14:02:43', '2020-07-14 21:06:11'),
(16, 'Viện Đại học Mở Hà Nội', 'Viện Đại học Mở H&agrave; Nội', '&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;strong&gt;T&amp;ecirc;n ng&amp;agrave;nh:&lt;/strong&gt; Ng&amp;agrave;nh C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin &amp;ndash; Viện đại học Mở H&amp;agrave; Nội&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;strong&gt;Thời gian đ&amp;agrave;o tạo&lt;/strong&gt;: 5 năm&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;strong&gt;Đối tượng tuyển sinh: &lt;/strong&gt;Viện Đại học Mở H&amp;agrave; Nội x&amp;eacute;t tuyển th&amp;iacute; sinh đ&amp;atilde; tốt nghiệp THPT hoặc tương đương&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;strong&gt;Mục ti&amp;ecirc;u đ&amp;agrave;o tạo:&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;Ng&amp;agrave;nh C&amp;ocirc;ng ngh&amp;ecirc;̣ th&amp;ocirc;ng tin đ&amp;agrave;o tạo tạo c&amp;aacute;c kỹ sư CNTT c&amp;oacute; tr&amp;igrave;nh đ&amp;ocirc;̣ vững v&amp;agrave;ng, kỹ năng thực h&amp;agrave;nh chuy&amp;ecirc;n s&amp;acirc;u để nghi&amp;ecirc;n cứu, x&amp;acirc;y dựng v&amp;agrave; ph&amp;aacute;t triển ứng dụng CNTT, đ&amp;aacute;p ứng nhu cầu ph&amp;aacute;t triển kinh tế &amp;ndash; x&amp;atilde; hội trong nước v&amp;agrave; hội nhập quốc tế.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;Sinh vi&amp;ecirc;n học ng&amp;agrave;nh CNTT được trang bị những kiến thức chung về khoa học tự nhi&amp;ecirc;n như to&amp;aacute;n học cao cấp, đại số tuyến t&amp;iacute;nh v&amp;agrave; phương ph&amp;aacute;p t&amp;iacute;nh to&amp;aacute;n khoa học,&amp;hellip; v&amp;agrave; những kiến thức cơ bản về ng&amp;agrave;nh c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin như to&amp;aacute;n học rời rạc, tin học đại cương, kỹ thuật điện tử số, kiến tr&amp;uacute;c m&amp;aacute;y t&amp;iacute;nh, nguy&amp;ecirc;n l&amp;yacute; của c&amp;aacute;c hệ điều h&amp;agrave;nh, mạng m&amp;aacute;y t&amp;iacute;nh, phương ph&amp;aacute;p lập tr&amp;igrave;nh cho m&amp;aacute;y t&amp;iacute;nh, phương ph&amp;aacute;p tổ chức v&amp;agrave; khai th&amp;aacute;c xử l&amp;yacute; dữ liệu tr&amp;ecirc;n m&amp;aacute;y t&amp;iacute;nh phục vụ cho t&amp;iacute;nh to&amp;aacute;n v&amp;agrave; lưu trữ&amp;hellip;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;Đặc biệt, sinh vi&amp;ecirc;n ng&amp;agrave;nh CNTT được trang bị những kiến thức chuy&amp;ecirc;n ng&amp;agrave;nh chuy&amp;ecirc;n s&amp;acirc;u theo c&amp;aacute;c hướng như c&amp;ocirc;ng nghệ phần mềm, hệ thống th&amp;ocirc;ng tin, mạng v&amp;agrave; bảo mật hệ thống, c&amp;ocirc;ng nghệ đa phương tiện; đảm bảo sinh vi&amp;ecirc;n sau khi tốt nghiệp đ&amp;aacute;p ứng tốt c&amp;aacute;c vị tr&amp;iacute; c&amp;ocirc;ng việc trong thực tiễn, c&amp;oacute; khả năng chuy&amp;ecirc;n m&amp;ocirc;n h&amp;oacute;a cao v&amp;agrave; năng lực th&amp;iacute;ch ứng với sự ph&amp;aacute;t triển của c&amp;ocirc;ng nghệ tốt.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&amp;nbsp;&lt;/div&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;a href=&quot;https://photo-1-baomoi.zadn.vn/w1000_r1/2018_07_17_30_26925219/6e0d24f349b6a0e8f9a7.jpg&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://photo-1-baomoi.zadn.vn/w1000_r1/2018_07_17_30_26925219/6e0d24f349b6a0e8f9a7.jpg&quot; style=&quot;height:450px; width:467px&quot; /&gt;&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;strong&gt;Chất lượng giảng vi&amp;ecirc;n:&lt;/strong&gt; Đội ngũ c&amp;aacute;n bộ giảng dạy của Khoa bao gồm hơn 80 c&amp;aacute;n bộ cơ hữu (bi&amp;ecirc;n chế, hợp đồng d&amp;agrave;i hạn) v&amp;agrave; cộng t&amp;aacute;c vi&amp;ecirc;n, gi&amp;aacute;o vi&amp;ecirc;n thỉnh giảng trong đ&amp;oacute; c&amp;oacute; 10 GS, giảng vi&amp;ecirc;n cao cấp v&amp;agrave; PGS, 13 Tiến sĩ, hơn 50 Thạc sĩ, GVC... với tr&amp;igrave;nh độ chuy&amp;ecirc;n m&amp;ocirc;n s&amp;acirc;u, kiến thức v&amp;agrave; kỹ năng vững v&amp;agrave;ng,lu&amp;ocirc;n t&amp;acirc;m huyết với nghề v&amp;agrave; sẵn s&amp;agrave;ng đ&amp;aacute;p ứng c&amp;aacute;c y&amp;ecirc;u cầu nhiệm vụ giảng dạy theo nhu cầu thực tế.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;strong&gt;Học ph&amp;iacute;:&lt;/strong&gt; Học ph&amp;iacute; rơi v&amp;agrave;o kho&amp;aacute;ng 12 triệu/ 1 năm&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;a href=&quot;https://scontent.fhan2-4.fna.fbcdn.net/v/t1.0-9/s960x960/92241332_816306375545519_8357856339103318016_o.jpg?_nc_cat=105&amp;amp;_nc_sid=110474&amp;amp;_nc_ohc=uks6CPxXL6oAX93CK1I&amp;amp;_nc_ht=scontent.fhan2-4.fna&amp;amp;_nc_tp=7&amp;amp;oh=9c8f6ea45d2ffa11d42f8d3f09330664&amp;amp;oe=5F1BEA75&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://scontent.fhan2-4.fna.fbcdn.net/v/t1.0-9/s960x960/92241332_816306375545519_8357856339103318016_o.jpg?_nc_cat=105&amp;amp;_nc_sid=110474&amp;amp;_nc_ohc=uks6CPxXL6oAX93CK1I&amp;amp;_nc_ht=scontent.fhan2-4.fna&amp;amp;_nc_tp=7&amp;amp;oh=9c8f6ea45d2ffa11d42f8d3f09330664&amp;amp;oe=5F1BEA75&quot; style=&quot;height:397px; width:767px&quot; /&gt;&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;strong&gt;Cơ hội việc l&amp;agrave;m:&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kĩ sư an ninh mạng, đảm bảo an to&amp;agrave;n th&amp;ocirc;ng tin, ph&amp;ograve;ng chống c&amp;aacute;c cuộc tấn c&amp;ocirc;ng, bảo vệ dữ liệu người d&amp;ugrave;ng.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kĩ sư kiểm thử/kiểm so&amp;aacute;t chất lượng/đảm bảo chất lượng.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kĩ sư lập tr&amp;igrave;nh/ph&amp;aacute;t triển c&amp;aacute;c hệ thống tr&amp;ecirc;n c&amp;aacute;c nền tảng như Desktop, Web, Mobile, IoT, Cloud Computing, Hệ thống nh&amp;uacute;ng, AI,&amp;hellip;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kĩ sư ph&amp;acirc;n t&amp;iacute;ch thiết kế hệ thống, ph&amp;acirc;n t&amp;iacute;ch nghiệp vụ (BA).&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kĩ sư quản trị CSDL, l&amp;agrave;m việc với dữ liệu lớn (Big Data).&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Nghi&amp;ecirc;n cứu, giảng dạy, huấn luyện CNTT.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Ph&amp;acirc;n t&amp;iacute;ch, thiết kế, cải tiến trải nghiệm người d&amp;ugrave;ng (UX).&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Quản l&amp;yacute; nh&amp;oacute;m, quản l&amp;yacute; dự &amp;aacute;n.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Quản trị, vận h&amp;agrave;nh hệ thống, hỗ trợ kh&amp;aacute;ch h&amp;agrave;ng,&amp;hellip;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Thiết kế website v&amp;agrave; c&amp;aacute;c sản phẩm truyền th&amp;ocirc;ng kh&amp;aacute;c như standee, pa n&amp;ocirc;, &amp;aacute;p ph&amp;iacute;ch,&amp;hellip; phục vụ việc quảng b&amp;aacute; sản phẩm.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;strong&gt;Chuẩn đầu ra cho sinh vi&amp;ecirc;n:&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;em&gt;Về kiến thức&lt;/em&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kiến thức căn bản về to&amp;aacute;n ứng dụng trong C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin nhằm gi&amp;uacute;p cho người học c&amp;oacute; thể tiếp thu được những kiến thức cơ sơ ng&amp;agrave;nh v&amp;agrave; chuy&amp;ecirc;n ng&amp;agrave;nh, cũng như khả năng nghi&amp;ecirc;n cứu, học n&amp;acirc;ng cao tr&amp;igrave;nh độ.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kiến thức căn bản về nền tảng CNTT, kiến thức về kiến tr&amp;uacute;c của m&amp;aacute;y t&amp;iacute;nh, kiến thức về hệ điều h&amp;agrave;nh m&amp;aacute;y t&amp;iacute;nh, kiến thức về c&amp;aacute;c hệ điều h&amp;agrave;nh th&amp;ocirc;ng dụng đương đại gi&amp;uacute;p cho người học nắm được những nguy&amp;ecirc;n l&amp;yacute; cơ bản của một hệ thống m&amp;aacute;y t&amp;iacute;nh.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kiến thức căn bản về cơ sở dữ liệu v&amp;agrave; hệ quản trị cơ sở dữ liệu, kiến thức về hệ thống th&amp;ocirc;ng tin của c&amp;aacute;c tổ chức v&amp;agrave; doanh nghiệp, kiến thức về ph&amp;acirc;n t&amp;iacute;ch v&amp;agrave; thiết kế hệ thống gi&amp;uacute;p cho người học nắm được nguy&amp;ecirc;n l&amp;yacute; cơ bản v&amp;agrave; giải ph&amp;aacute;p trong c&amp;aacute;c hệ thống th&amp;ocirc;ng tin ở c&amp;aacute;c tổ chức v&amp;agrave; doanh nghiệp.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kiến thức cơ bản về cấu tr&amp;uacute;c dữ liệu, về giải thuật, kiến thức về lập tr&amp;igrave;nh cấu tr&amp;uacute;c v&amp;agrave; lập tr&amp;igrave;nh hướng đối tượng nhằm gi&amp;uacute;p cho người học c&amp;oacute; thể hiểu căn bản về lập tr&amp;igrave;nh, x&amp;acirc;y dựng c&amp;aacute;c chương tr&amp;igrave;nh m&amp;aacute;y t&amp;iacute;nh.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kiến thức về C&amp;ocirc;ng nghệ phần mềm nhằm gi&amp;uacute;p cho người học nắm được qui tr&amp;igrave;nh x&amp;acirc;y dựng phần mềm trong c&amp;ocirc;ng nghiệp c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kiến thức cơ bản về mạng m&amp;aacute;y t&amp;iacute;nh nhằm gi&amp;uacute;p cho người học nắm được nguy&amp;ecirc;n l&amp;yacute; căn bản của mạng m&amp;aacute;y t&amp;iacute;nh, mạng của c&amp;aacute;c doanh nghiệp v&amp;agrave; tổ chức, dịch vụ mạng cũng như kiến thức về mạng Internet.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kiến thức về lập tr&amp;igrave;nh t&amp;iacute;ch hợp v&amp;agrave; kỹ thuật lập tr&amp;igrave;nh t&amp;iacute;ch hợp.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kiến thức về quản trị v&amp;agrave; bảo tr&amp;igrave; c&amp;aacute;c hệ thống CNTT của một tổ chức hay c&amp;aacute; nh&amp;acirc;n.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kiến thức về nguy&amp;ecirc;n l&amp;yacute; an ninh v&amp;agrave; bảo to&amp;agrave;n th&amp;ocirc;ng tin.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Kiến thức về sản phẩm v&amp;agrave; giải ph&amp;aacute;p c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin đương đại.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;em&gt;Về kỹ năng&lt;/em&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;&lt;em&gt;Kỹ năng cứng&lt;/em&gt;&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Vận dụng kiến thức to&amp;aacute;n học v&amp;agrave; c&amp;aacute;c khoa học cơ bản được sử dụng trong lĩnh vực CNTT.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Sử dụng m&amp;aacute;y vi t&amp;iacute;nh phục vụ cho học tập, cho c&amp;ocirc;ng việc, cũng như cuộc sống hằng ng&amp;agrave;y; c&amp;oacute; khả năng x&amp;acirc;y dựng chương tr&amp;igrave;nh ứng dụng tin học căn bản.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Quản trị hệ thống CNTT của một tổ chức hay doanh nghiệp.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Tư vấn cho c&amp;aacute;c tổ chức v&amp;agrave; c&amp;aacute;c doanh nghiệp trong việc lựa chọn giải ph&amp;aacute;p v&amp;agrave; sản phẩm CNTT ph&amp;ugrave; hợp.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Ti&amp;ecirc;n đo&amp;aacute;n xu hướng ph&amp;aacute;t triển của CNTT để đưa ra quyết định ph&amp;ugrave; hợp cho tổ chức hay doanh nghiệp trong việc lựa chọn c&amp;ocirc;ng nghệ v&amp;agrave; giải ph&amp;aacute;p cũng như ch&amp;iacute;nh s&amp;aacute;ch ph&amp;aacute;t triển CNTT.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Lập tr&amp;igrave;nh theo những hướng ph&amp;aacute;t triển ứng dụng kh&amp;aacute;c nhau.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Quản trị c&amp;aacute;c hệ thống CNTT kh&amp;aacute;c nhau của c&amp;aacute;c tổ chức hay c&amp;aacute; nh&amp;acirc;n.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;em&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;Kỹ năng mềm&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Đạt tr&amp;igrave;nh độ Tiếng Anh/Tiếng Ph&amp;aacute;p tương đương tr&amp;igrave;nh độ B1&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- Viết t&amp;agrave;i liệu m&amp;ocirc; tả kỹ thuật, b&amp;aacute;o c&amp;aacute;o khoa học hay viết dự &amp;aacute;n, v&amp;agrave; thuyết tr&amp;igrave;nh.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;div&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;span style=&quot;font-family:arial,helvetica,sans-serif&quot;&gt;- L&amp;agrave;m việc nh&amp;oacute;m, kỹ năng l&amp;atilde;nh đạo nh&amp;oacute;m l&amp;agrave;m việc, kỹ năng đ&amp;agrave;m ph&amp;aacute;n, giải quyết xung đột, sử dụng th&amp;agrave;nh thạo những c&amp;ocirc;ng cụ hỗ trợ cho việc hợp t&amp;aacute;c l&amp;agrave;m việc nh&amp;oacute;m, kỹ năng đ&amp;aacute;nh gi&amp;aacute; đ&amp;oacute;ng g&amp;oacute;p của th&amp;agrave;nh vi&amp;ecirc;n nh&amp;oacute;m.&lt;/span&gt;&lt;/span&gt;&lt;/div&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n', '<p><span style=\"color:#000000\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:arial,helvetica,sans-serif\">C&ocirc;ng nghệ đa phương tiện</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:arial,helvetica,sans-serif\">C&ocirc;ng nghệ phần mềm</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:arial,helvetica,sans-serif\">Hệ thống th&ocirc;ng tin</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:arial,helvetica,sans-serif\">Mạng v&agrave; an to&agrave;n hệ thống</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Nội dung đ&agrave;o tạo:</strong></span></span></p>\r\n\r\n<div style=\"text-align: justify;\"><span style=\"color:#000000\">Ng&agrave;nh C&ocirc;ng ngh&ecirc;̣ th&ocirc;ng tin đ&agrave;o tạo tạo c&aacute;c kỹ sư CNTT c&oacute; tr&igrave;nh đ&ocirc;̣ vững v&agrave;ng, kỹ năng thực h&agrave;nh chuy&ecirc;n s&acirc;u để nghi&ecirc;n cứu, x&acirc;y dựng v&agrave; ph&aacute;t triển ứng&nbsp;dụng&nbsp;CNTT, đ&aacute;p ứng nhu cầu&nbsp; ph&aacute;t&nbsp; triển&nbsp; kinh tế - x&atilde; hội trong nước v&agrave; hội nhập quốc tế.Sinh vi&ecirc;n ng&agrave;nh CNTT được trang bị những kiến thức chuy&ecirc;n ng&agrave;nh chuy&ecirc;n s&acirc;u theo c&aacute;c hướng như c&ocirc;ng nghệ phần mềm, hệ thống th&ocirc;ng tin, mạng v&agrave; bảo mật hệ thống, c&ocirc;ng nghệ đa phương tiện; đảm bảo sinh vi&ecirc;n sau khi tốt nghiệp đ&aacute;p ứng tốt c&aacute;c vị tr&iacute; c&ocirc;ng việc trong thực tiễn, c&oacute; khả năng chuy&ecirc;n m&ocirc;n h&oacute;a cao v&agrave; năng lực th&iacute;ch ứng với sự ph&aacute;t triển của c&ocirc;ng nghệ tốt.</span></div>\r\n', 21, '                                                                                                                                                                                                                                                                                              <iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3724.7885110534326!2d105.84548681437778!3d21.001113094098734!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135adb408df61f5%3A0xa65d4bc77bab639a!2zVHLGsOG7nW5nIMSQ4bqhaSBo4buNYyBN4bufIEjDoCBO4buZaQ!5e0!3m2!1svi!2s!4v1594632748872!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>                                                                                                                                                                                                                                                               ', 'dhmo.jpg', 4, '2020-06-27 14:25:02', '2020-07-14 21:05:48'),
(17, 'Trường Đại học Điện lực', 'Trường Đại học Điện lực', '&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;T&amp;ecirc;n ng&amp;agrave;nh: Ng&amp;agrave;nh C&amp;ocirc;ng nghệ th&amp;ocirc;ng tin Đại học Điện lực&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;Giới thiệu về trường: Trải qua mười lăm năm hoạt động với phương ch&amp;acirc;m c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin phải gắn liền với ứng dụng trong thực tế&amp;nbsp;v&amp;agrave; lấy sinh vi&amp;ecirc;n l&amp;agrave;m trung t&amp;acirc;m của qu&amp;aacute; tr&amp;igrave;nh ph&amp;aacute;t&amp;nbsp;triển, Khoa đ&amp;atilde; đạt được những th&amp;agrave;nh tựu đ&amp;aacute;ng kh&amp;iacute;ch lệ về chất lượng đ&amp;agrave;o tạo, ph&amp;aacute;t triển ứng dụng, đề t&amp;agrave;i khoa học, b&amp;agrave;i b&amp;aacute;o khoa học, olympic tin học v&amp;agrave; kỹ năng mềm cho sinh vi&amp;ecirc;n. C&amp;aacute;c th&amp;agrave;nh t&amp;iacute;ch n&amp;agrave;y đ&amp;atilde; được Nh&amp;agrave; nước ghi nhận bằng việc tặng thưởng hu&amp;acirc;n chương lao động hạng ba v&amp;agrave; sự ghi nhận của c&amp;aacute;c đơn vị sử dụng&amp;nbsp;nguồn nh&amp;acirc;n lực v&amp;agrave; khả năng thu h&amp;uacute;t sinh vi&amp;ecirc;n theo học&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;https://congthuong.vn/stores/news_dataimages/nguyenly/082019/27/09/croped/truong-dai-hoc-dien-luc-phan-hoi-cac-thong-tin-sai-pham-duoc-cac-co-quan-bao-chi-dang-tai-03-.8786.jpg?noCached=1566873859&quot; style=&quot;height:414px; width:644px&quot; /&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;Học ph&amp;iacute;: Học ph&amp;iacute; rơi tầm 14-16 triệu t&amp;ugrave;y từng ng&amp;agrave;nh nghề&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#000000&quot;&gt;Cơ hội việc l&amp;agrave;m:&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin-left:18.0pt&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Sau khi tốt nghiệp sinh vi&amp;ecirc;n c&amp;oacute; thể l&amp;agrave;m được c&amp;aacute;c c&amp;ocirc;ng việc:&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin-left:18.0pt&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&amp;nbsp;+ Lập tr&amp;igrave;nh&amp;nbsp;vi&amp;ecirc;n&amp;nbsp;-&amp;nbsp;Programmer/Developer&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin-left:18.0pt&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;&amp;nbsp;+ Thiết kế Web/Mobile &amp;ndash;&amp;nbsp;Designer&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin-left:18.0pt&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Nghiệp vụ vi&amp;ecirc;n - Business Analysis (BA)&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin-left:18.0pt&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Quản trị cơ sở dữ liệu&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin-left:18.0pt&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Tester/QA&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin-left:18.0pt&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Quản trị hệ thống/ Quản l&amp;yacute; sản phẩm&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin-left:18.0pt&quot;&gt;&lt;span style=&quot;color:#000000&quot;&gt;+ Ph&amp;acirc;n t&amp;iacute;ch hệ thống&lt;/span&gt;&lt;/p&gt;\r\n', '<div><span style=\"color:#000000\"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo:</strong> C&ocirc;ng nghệ phần mềm, Hệ thống thương mại điện tử, quản trị v&agrave; an ninh mạng, Tr&iacute; tuệ nh&acirc;n tạo &amp; Thị gi&aacute;c m&aacute;y t&iacute;nh</span></div>\r\n\r\n<div style=\"text-align:justify;\"><span style=\"color:#000000\"><strong>Nội dung đ&agrave;o tạo:</strong> Trang bị cho sinh vi&ecirc;n c&aacute;c kiến thức về thuật to&aacute;n, thiết kế hệ thống c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; lập tr&igrave;nh x&acirc;y dựng hệ thống phần mềm. B&ecirc;n cạnh đ&oacute;, sinh vi&ecirc;n c&ograve;n được trang bị kiến thức về quản trị dự &aacute;n c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; vận h&agrave;nh hệ thống c&ocirc;ng nghệ th&ocirc;ng tin</span></div>\r\n\r\n<div><span style=\"color:#000000\"><strong>Chuy&ecirc;n ng&agrave;nh C&ocirc;ng nghệ phần mềm đ&agrave;o tạo kiến thức chuy&ecirc;n s&acirc;u về:&nbsp;</strong></span></div>\r\n\r\n<div style=\"margin-left: 40px;\"><span style=\"color:#000000\">&nbsp; &nbsp; &nbsp; &nbsp;- C&aacute;c thuật to&aacute;n phục vụ x&acirc;y dựng phần mềm</span></div>\r\n\r\n<div style=\"margin-left: 40px;\"><span style=\"color:#000000\">&nbsp; &nbsp; &nbsp; &nbsp;- C&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh</span></div>\r\n\r\n<div style=\"margin-left: 40px;\"><span style=\"color:#000000\">&nbsp; &nbsp; &nbsp; &nbsp;- Phương ph&aacute;p ph&acirc;n t&iacute;ch, thiết kế hệ thống th&ocirc;ng tin.</span></div>\r\n\r\n<div style=\"margin-left: 40px;\"><span style=\"color:#000000\">&nbsp; &nbsp; &nbsp; &nbsp;- Quản trị dự &aacute;n c&ocirc;ng nghệ th&ocirc;ng tin</span></div>\r\n\r\n<div style=\"margin-left: 40px;\"><span style=\"color:#000000\">&nbsp; &nbsp; &nbsp; &nbsp;- C&aacute;c nền tảng c&ocirc;ng nghệ để ph&aacute;t triển phần mềm.</span></div>\r\n\r\n<div><span style=\"color:#000000\"><strong>Chuy&ecirc;n ng&agrave;nh Quản trị v&agrave; an ninh mạng với kiến thức chuy&ecirc;n s&acirc;u về:</strong></span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Ph&acirc;n t&iacute;ch v&agrave; thiết kế hệ thống mạng m&aacute;y t&iacute;nh.</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Giải ph&aacute;p đảm bảo an to&agrave;n hệ thống mạng.</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Đảm bảo an to&agrave;n cơ sở dữ liệu v&agrave; hệ thống th&ocirc;ng tin cho doanh nghiệp.</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Quản trị hệ thống mạng.</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Đ&aacute;nh gi&aacute; hiệu năng của một hệ thống mạng m&aacute;y t&iacute;nh.</span></div>\r\n\r\n<div><span style=\"color:#000000\"><strong>Chuy&ecirc;n ng&agrave;nh Hệ thống thương mại điện tử đ&agrave;o tạo kiến thức chuy&ecirc;n s&acirc;u về:</strong></span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- M&ocirc; h&igrave;nh kinh doanh trong thương mại điện tử</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Phương ph&aacute;p lập kế hoạch v&agrave; ph&aacute;t triển chiến lược thương mại điện tử</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- M&ocirc; h&igrave;nh thanh to&aacute;n qua mạng.</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Nghiệp vụ trong thương mại điện tử.</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- An to&agrave;n v&agrave; bảo mật trong thương mại điện tử.</span></div>\r\n\r\n<div><span style=\"color:#000000\"><strong>Chuy&ecirc;n ng&agrave;nh Tr&iacute; tuệ nh&acirc;n tạo v&agrave; thị gi&aacute;c m&aacute;y t&iacute;nh đ&agrave;o tạo kiến thức chuy&ecirc;n s&acirc;u về:</strong></span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Biểu diễn tri thức v&agrave; suy diễn tự động</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Phương ph&aacute;p học m&aacute;y</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Ph&acirc;n t&iacute;ch v&agrave; xử l&yacute; th&ocirc;ng tin h&igrave;nh ảnh</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Nhận dạng mẫu</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- Ph&acirc;n t&iacute;ch v&agrave; xử l&yacute; dữ liệu lớn</span></div>\r\n\r\n<div style=\"margin-left: 18pt;\"><span style=\"color:#000000\">- L&yacute; thuyết tr&ograve; chơi</span></div>\r\n', 22, '                                                                                                                                                                                                                                                                                                <iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3723.6498757055815!2d105.78302651437859!3d21.04669089254305!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ab325960d0dd%3A0x721c9888e25aa2bf!2zVHLGsOG7nW5nIMSQ4bqhaSBI4buNYyDEkGnhu4duIEzhu7Fj!5e0!3m2!1svi!2s!4v1594629020005!5m2!1svi!2s\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>                                                                                                                                                                                                                                                                                                          ', 'dl.png', 4, '2020-06-27 14:28:52', '2020-07-14 21:05:08');

